SET NAMES utf8mb4;
SET FOREIGN_KEY_CHECKS = 0;

-- ----------------------------
-- Table structure for post
-- ----------------------------
DROP TABLE IF EXISTS `post`;
CREATE TABLE `post`  (
  `postID` int(11) NOT NULL AUTO_INCREMENT,
  `postAnhTieuDe` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `postTieuDe` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `postTomTat` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `postNoiDung` varchar(15000) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `postChuyenMucID` int(11) NOT NULL,
  `postTagID` int(11) NOT NULL,
  `postTrangThaiID` varchar(10) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `postLuotView` int(11) NOT NULL,
  `postHangBaiViet` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `postNgayDang` date NULL DEFAULT NULL,
  `postWriterID` int(11) NOT NULL,
  `postLyDoTuChoi` varchar(500) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `postNgayXuatBan` date NULL DEFAULT NULL,
  PRIMARY KEY (`postID`) USING BTREE,
  INDEX `fk_post_category`(`postChuyenMucID`) USING BTREE,
  INDEX `fk_post_tags`(`postTagID`) USING BTREE,
  CONSTRAINT `fk_post_category` FOREIGN KEY (`postChuyenMucID`) REFERENCES `category` (`cateID`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `fk_post_tags` FOREIGN KEY (`postTagID`) REFERENCES `tags` (`tagID`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB AUTO_INCREMENT = 26 CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of post
-- ----------------------------
INSERT INTO `post` VALUES (1, 'buoi.jpg', 'Giá tiêu, điều, gạo, cà phê cùng lao dốc', 'Kết thúc quý 1/2019, hàng loạt nông sản xuất khẩu chủ lực lao dốc làm cho giá trị  xuất khẩu tăng trưởng âm sau nhiều năm', '<p>Ba th&aacute;ng đầu năm, lĩnh vực n&ocirc;ng nghiệp xuất khẩu được 8,8 tỉ USD, giảm gần 3% so với c&ugrave;ng kỳ năm 2018. Nguy&ecirc;n nh&acirc;n l&agrave; do gi&aacute; nhiều mặt h&agrave;ng xuất khẩu sụt giảm mạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&oacute; đến 4 mặt h&agrave;ng n&ocirc;ng sản xuất khẩu m&agrave; tỷ lệ gi&aacute; giảm đến hai con số, so với c&ugrave;ng kỳ năm trước. Dẫn đầu danh s&aacute;ch n&agrave;y l&agrave; gi&aacute; ti&ecirc;u với mức giảm đến 27% n&ecirc;n d&ugrave; lượng xuất khẩu tăng đến 19% nhưng gi&aacute; trị vẫn giảm gần 18% so với c&ugrave;ng kỳ năm ngo&aacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đứng ngay sau ti&ecirc;u l&agrave; gi&aacute; điều với mức giảm đến gần 21% so với c&ugrave;ng kỳ năm 2018, mức gi&aacute; xuất khẩu b&igrave;nh qu&acirc;n hạt điều đạt mức 8.043 USD/tấn. Xuất khẩu hạt điều tăng gần 5% về lượng nhưng giảm đến tr&ecirc;n 17% về gi&aacute; trị.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gạo đứng thứ 3 với mức giảm gần 18%, chỉ c&ograve;n 404 USD/tấn. Đ&acirc;y l&agrave; yếu tố l&agrave;m gi&aacute; trị xuất khẩu trong 3 th&aacute;ng qua giảm tr&ecirc;n 20% so với c&ugrave;ng kỳ năm 2018 d&ugrave; lượng chỉ giảm c&oacute; 3,5%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ugrave;ng giai đoạn tr&ecirc;n, gi&aacute; c&agrave; ph&ecirc; xuất khẩu cũng sụt giảm đến tr&ecirc;n 14%, hiện chỉ c&ograve;n 4.894 USD/tấn. Tổng gi&aacute; trị xuất khẩu c&agrave; ph&ecirc; giảm gần 24%, chỉ thu về c&oacute; 830 triệu USD trong qu&yacute; 1 d&ugrave; lượng giảm chỉ c&oacute; 15,3%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, h&agrave;ng loạt n&ocirc;ng sản kh&aacute;c cũng c&oacute; mức sụt giảm đến gần 10% về gi&aacute; trị so với c&ugrave;ng kỳ, cụ thể như xuất khẩu rau quả đạt 879 triệu USD, giảm 9,3% về gi&aacute; trị; mặt h&agrave;ng sắn v&agrave; c&aacute;c sản phẩm từ sắn, giảm 9,4%; t&ocirc;m c&aacute;c loại ước đạt 676 triệu USD, giảm 9,1%. Cũng cần phải n&oacute;i th&ecirc;m, trong nh&oacute;m n&agrave;y mặt h&agrave;ng rau quả li&ecirc;n tục trong 3 năm gần đ&acirc;y lu&ocirc;n c&oacute; tốc độ tăng trưởng dẫn đầu trong nh&oacute;m ng&agrave;nh n&ocirc;ng l&acirc;m thủy hải sản. Tuy nhi&ecirc;n do qu&aacute; phụ thuộc v&agrave;o thị trường Trung Quốc (75-80%) n&ecirc;n khi gặp kh&oacute; th&igrave; c&aacute;c thị trường c&ograve;n lại kh&ocirc;ng &quot;đỡ&quot; nỗi, tăng trưởng quay đầu đi xuống.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i l&yacute; do kh&aacute;ch quan l&agrave; xu thế chung của thế giới th&igrave; phần lớn những mặt h&agrave;ng n&agrave;y n&ocirc;ng sản n&agrave;y đ&atilde; tăng mạnh diện t&iacute;ch v&agrave; sản lượng trong thời gian qua. Cụ thể những năm gần đ&acirc;y, diện t&iacute;ch trồng c&agrave; ph&ecirc; robusta đang được mở rộng ở T&acirc;y nguy&ecirc;n, Đ&ocirc;ng Nam bộ. C&agrave; ph&ecirc; arabica thậm ch&iacute; c&ograve;n ph&aacute;t triển ở tỉnh Sơn La, Lai Ch&acirc;u, Y&ecirc;n B&aacute;i, H&agrave; Giang, Tuy&ecirc;n Quang.</p>\r\n', 1, 1, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (2, 'tieu 1.jpg', 'Tiêu chết trắng, giá rẻ như rau: Nông dân cầu cứu ngân hàng', 'Tại Gia Lai, tính đến năm 2019, tổng dư nợ mà nông dân vay vốn ngân hàng để trồng tiêu là hơn 4.300 tỷ đồng, trong đó 2.200 tỷ đồng là nợ xấu. Hiện hàng nghìn hộ dân đang phải “cầu cứu” ngành chức năng và ngân hàng có thể gia hạn nợ, cứu họ thoát dần khỏi “thảm cảnh hồ tiêu”…', '<p><strong>N&ocirc;ng d&acirc;n &ldquo;cầu cứu&rdquo;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đỉnh điểm khiến n&ocirc;ng d&acirc;n trồng ti&ecirc;u l&acirc;m cảnh nợ nần l&agrave; l&uacute;c gi&aacute; hồ ti&ecirc;u rớt xuống đ&aacute;y, từ 220.000 đồng/kg chỉ c&ograve;n 41.000 - 43.000 đồng/kg. Th&ecirc;m v&agrave;o đ&oacute;, dịch bệnh v&agrave; nắng n&oacute;ng k&eacute;o d&agrave;i khiến hồ ti&ecirc;u chết như ngả rạ. Vay ng&acirc;n h&agrave;ng, đầu tư h&agrave;ng tỷ đồng trồng ti&ecirc;u nay nhiều hộ n&ocirc;ng d&acirc;n rơi v&agrave;o thảm cảnh, nợ nần chồng chất, c&oacute; bao nhi&ecirc;u t&agrave;i sản cũng đ&atilde; thế chấp hết cho ng&acirc;n h&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://4.bp.blogspot.com/-AEaDZKISJW4/XK7xzQksMcI/AAAAAAACkeg/T1pAxEHFQwgfX0LQ41rl5E9M7NDMR6EWgCLcBGAs/s1600/tieu%2B1.jpg\"><img src=\"https://4.bp.blogspot.com/-AEaDZKISJW4/XK7xzQksMcI/AAAAAAACkeg/T1pAxEHFQwgfX0LQ41rl5E9M7NDMR6EWgCLcBGAs/s400/tieu%2B1.jpg\" style=\"height:295px; width:400px\" /></a></p>\r\n\r\n<p><em>Chỉ v&igrave; hồ ti&ecirc;u, hơn 26.000 hộ d&acirc;n vướng v&agrave;o v&ograve;ng xo&aacute;y nợ nần. Ảnh: T.H</em></p>\r\n\r\n<p><a name=\"more\"></a></p>\r\n\r\n<p>Khu&ocirc;n mặt rầu rĩ, ủ rũ b&ecirc;n vườn ti&ecirc;u chết kh&ocirc;, b&agrave; B&ugrave;i Thị Th&agrave;nh (tr&uacute; tại x&atilde; Ia Băng, huyện Chư Pr&ocirc;ng, Gia Lai) t&acirc;m sự:&nbsp;<em>&ldquo;Gần 2 năm nay, chẳng ng&agrave;y n&agrave;o t&ocirc;i được ngủ ngon v&igrave; khoản nợ lớn dần m&agrave; hồ ti&ecirc;u để lại. Tiền gốc l&agrave; 450 triệu đồng, tiền l&atilde;i mỗi năm gần 50 triệu đồng. Cứ 6 th&aacute;ng trả một lần, nhưng hiện tại tiền ăn c&ograve;n bữa đ&oacute;i bữa no, biết kiếm đ&acirc;u ra 24 triệu đồng trả l&atilde;i. Rồi c&ograve;n tiền gốc nữa, hơn 3.000 trụ ti&ecirc;u đ&atilde; chết hết rồi, ch&uacute;ng t&ocirc;i chưa biết tr&ocirc;ng v&agrave;o đ&acirc;u?&rdquo;.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Hồ Hồng Lam (49 tuổi, chồng b&agrave; Th&agrave;nh) n&oacute;i th&ecirc;m:&nbsp;<em>&ldquo;Cũng muốn b&aacute;n đất trả nợ lắm nhưng khổ nỗi ch&uacute;ng t&ocirc;i đ&atilde; rao b&aacute;n cả năm trời chẳng ai mua n&ecirc;n đ&agrave;nh để vậy. Sắp tới vợ chồng t&ocirc;i định khăn g&oacute;i về TP.HCM l&agrave;m thu&ecirc; để c&ograve;n c&oacute; đồng ra đồng v&agrave;o trả l&atilde;i, chứ như b&acirc;y giờ th&igrave; kh&ocirc;ng biết khi n&agrave;o mới hết nợ. Gần đ&acirc;y, ch&uacute;ng t&ocirc;i bị ng&acirc;n h&agrave;ng gọi giục trả nợ suốt, kh&ocirc;ng trả kịp lại th&agrave;nh nợ xấu th&ocirc;i&rdquo;.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Giữa năm ngo&aacute;i, ch&uacute;ng t&ocirc;i cũng c&oacute; một chuyến l&agrave;m việc tại x&atilde; Ia Blứ, huyện Chư Pưh. Gặp &ocirc;ng Mai Liệu (ở th&ocirc;n Thuỷ Ph&uacute;), &ocirc;ng buồn rầu cho biết &ocirc;ng c&oacute; 9 người con th&igrave; cả 9 đang trong t&igrave;nh trạng nợ xấu ng&acirc;n h&agrave;ng v&igrave; đ&atilde; vay tiền để đầu tư v&agrave;o 15ha ti&ecirc;u.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong đ&oacute;, 2 người đ&atilde; phải b&aacute;n nh&agrave;, b&aacute;n đất trả nợ ng&acirc;n h&agrave;ng nhưng vẫn c&ograve;n nợ nhiều khoản vay kh&aacute;c b&ecirc;n ngo&agrave;i. 7 người c&ograve;n lại, tuy chưa phải b&aacute;n nh&agrave; nhưng con c&aacute;i th&igrave; phải nghỉ học sớm để phụ gi&uacute;p cha mẹ đi l&agrave;m kiếm tiền trả nợ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;Người d&acirc;n trồng ti&ecirc;u x&atilde; n&agrave;y đ&atilde; phải nhổ cả trụ gỗ l&ecirc;n b&aacute;n với gi&aacute; 40.000 đồng/trụ để lấy tiền sinh hoạt, trong khi trước kia mỗi trụ gi&aacute; 250.000 đồng&rdquo;</em>&nbsp;- &ocirc;ng Liệu cho hay.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://4.bp.blogspot.com/--llJMG0p6Kk/XK7xzX8wPjI/AAAAAAACkeo/UfyJ0e7siugag_OCcvKR_-sekckpBXStACLcBGAs/s1600/tieu%2B2.jpg\"><img src=\"https://4.bp.blogspot.com/--llJMG0p6Kk/XK7xzX8wPjI/AAAAAAACkeo/UfyJ0e7siugag_OCcvKR_-sekckpBXStACLcBGAs/s400/tieu%2B2.jpg\" style=\"height:255px; width:400px\" /></a></p>\r\n\r\n<p><em>V&igrave; thua lỗ, nợ nần kh&ocirc;ng c&oacute; khả năng trả nợ, một số hộ d&acirc;n đ&atilde; phải rao b&aacute;n nh&agrave;, b&aacute;n đất bỏ xứ trốn nợ. Ảnh: T.H</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để c&oacute; tiền trả l&atilde;i cho ng&acirc;n h&agrave;ng, nhiều hộ đ&atilde; b&aacute;n b&ograve;, b&aacute;n đất. Giờ đ&acirc;y, ngo&agrave;i việc mong muốn ng&acirc;n h&agrave;ng sẽ gia hạn thời gian trả gốc, trả l&atilde;i, nhiều hộ trồng ti&ecirc;u chẳng biết l&agrave;m g&igrave; ngo&agrave;i cầu mong v&agrave;o&hellip; vận may.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cũng v&igrave; kh&ocirc;ng c&oacute; tiền trả nợ, &ocirc;ng Ph&ugrave;ng Văn Cảnh (tr&uacute; tại x&atilde; Ia Le, huyện Chư Pưh, Gia Lai) đ&atilde; đ&oacute;ng cửa đi khỏi địa phương. Được biết, hiện &ocirc;ng Cảnh vẫn nợ ng&acirc;n h&agrave;ng hơn 200 triệu đồng. D&ugrave; muốn ph&aacute;t triển c&aacute;c c&acirc;y trồng mới gia tăng kinh tế, nhưng phần v&igrave; diện t&iacute;ch đất trồng ti&ecirc;u bị nhiễm bệnh kh&oacute; phục hồi, phần v&igrave; kh&ocirc;ng c&oacute; vốn đầu tư n&ecirc;n &ocirc;ng Cảnh cũng như nhiều hộ kh&aacute;c đ&agrave;nh chấp nhận để đất hoang&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Hy vọng từ ph&iacute;a &ldquo;chủ nợ&rdquo;</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo ước t&iacute;nh, đến nay tổng diện t&iacute;ch trồng c&acirc;y hồ ti&ecirc;u tr&ecirc;n địa b&agrave;n to&agrave;n tỉnh Gia Lai hơn 16.500ha, nhưng c&oacute; đến gần 1/3 l&agrave; số lượng ti&ecirc;u chết (trong đ&oacute; ti&ecirc;u chết do mưa k&eacute;o d&agrave;i g&acirc;y thối rễ tr&ecirc;n 4.500ha; do gi&agrave; cỗi tr&ecirc;n 56ha; do s&acirc;u bệnh gần 1.000ha). Hơn 32.000 hộ c&oacute; ti&ecirc;u chết với khoản nợ hơn 4.300 tỷ đồng, trong đ&oacute; nợ xấu chiếm đến 2.200 tỷ đồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trước thực trạng n&agrave;y, UBND tỉnh Gia Lai đ&atilde; chỉ đạo r&agrave; so&aacute;t, tổng hợp đề nghị Bộ NNPTNT, Bộ T&agrave;i ch&iacute;nh, Ng&acirc;n h&agrave;ng Nh&agrave; nước Việt Nam b&aacute;o c&aacute;o Thủ tướng Ch&iacute;nh phủ xem x&eacute;t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://2.bp.blogspot.com/-FckUrS6I3Ro/XK7xzba0WKI/AAAAAAACkek/6aPS5CFrnJ4wkadjLUrogOxpdq_OBuZYQCLcBGAs/s1600/tieu%2B3.jpg\"><img src=\"https://2.bp.blogspot.com/-FckUrS6I3Ro/XK7xzba0WKI/AAAAAAACkek/6aPS5CFrnJ4wkadjLUrogOxpdq_OBuZYQCLcBGAs/s400/tieu%2B3.jpg\" style=\"height:263px; width:400px\" /></a></p>\r\n\r\n<p><em>&quot;Ti&ecirc;u chết, nợ sống&quot;, h&agrave;ng ngh&igrave;n hộ d&acirc;n chỉ mong được giản nợ từ ph&iacute;a ng&acirc;n h&agrave;ng để kh&ocirc;ng c&oacute; nợ xấu. Ảnh: T.H</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trưởng th&ocirc;n Thủy Ph&uacute; (x&atilde; Ia Blứ) Nguyễn Duy Trung than thở: Chưa bao giờ người d&acirc;n ở đ&acirc;y rơi v&agrave;o cảnh th&ecirc; thảm như thế n&agrave;y, h&agrave;ng ng&agrave;y phải lo từng bữa ăn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở đ&acirc;y hầu hết ai cũng đi vay v&agrave; nhiều gia đ&igrave;nh kh&ocirc;ng c&oacute; khả năng trả nợ. Nếu Nh&agrave; nước, ng&agrave;nh ng&acirc;n h&agrave;ng kh&ocirc;ng can thiệp sớm gi&uacute;p d&acirc;n khoanh nợ th&igrave; c&oacute; hơn 90% hộ d&acirc;n sẽ l&acirc;m cảnh mất nh&agrave; cửa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Li&ecirc;n quan đến vấn đề n&agrave;y, &ocirc;ng Nguyễn Văn Cư - Gi&aacute;m đốc Ng&acirc;n h&agrave;ng Nh&agrave; Nước Chi nh&aacute;nh tại Gia Lai cho biết, để hỗ trợ người d&acirc;n trong giai đoạn kh&oacute; khăn, ng&agrave;nh ng&acirc;n h&agrave;ng sẽ cơ cấu thời hạn nợ, điều chỉnh thời hạn nợ, gia hạn nợ. B&ecirc;n cạnh đ&oacute;, ng&acirc;n h&agrave;ng cũng sẽ c&oacute; giải ph&aacute;p giảm l&atilde;i suất hoặc miễn giảm l&atilde;i tiền vay cho người vay. Đồng thời, ng&acirc;n h&agrave;ng cũng xem x&eacute;t cho người d&acirc;n vay lại để t&aacute;i cơ cấu c&acirc;y trồng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;Về việc người d&acirc;n muốn khoanh nợ, hiện rất kh&oacute; khăn. Bởi khi đ&oacute;, c&aacute;c ng&agrave;nh c&oacute; li&ecirc;n quan sẽ phải v&agrave;o cuộc x&aacute;c minh, sau đ&oacute; mới c&oacute; cơ sở tr&igrave;nh l&ecirc;n chủ tịch UBND tỉnh. Sau đ&oacute;, chủ tịch UBND tỉnh phải xin &yacute; kiến Thủ tướng Ch&iacute;nh phủ xem x&eacute;t việc khoanh nợ. Khi Thủ tướng đồng &yacute; th&igrave; việc khoanh nợ sẽ diễn ra. Theo đ&oacute;, trong thời hạn khoanh nợ (2 năm) người d&acirc;n sẽ kh&ocirc;ng phải trả l&atilde;i&rdquo;</em>&nbsp;&ndash; &ocirc;ng Cư n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, cũng theo &ocirc;ng Cư, quy định hiện nay cấp n&agrave;o đề nghị khoanh nợ, cấp đ&oacute; phải xuất ng&acirc;n s&aacute;ch để chi trả.&nbsp;<em>&ldquo;Với l&atilde;i suất b&igrave;nh qu&acirc;n 10%/năm, th&igrave; 2.200 tỷ đồng nợ xấu sẽ c&oacute; l&atilde;i suất khoảng 220 tỷ đồng. Trong 2 năm, số l&atilde;i n&agrave;y tăng l&ecirc;n khoảng 440 tỷ đồng, đ&acirc;y l&agrave; số tiền kh&ocirc;ng hề nhỏ đối với ng&acirc;n s&aacute;ch địa phương&hellip;&rdquo;</em>&nbsp;- &ocirc;ng Cư cho biết th&ecirc;m.</p>\r\n', 1, 3, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (3, 'ca-phe.jpg', 'Nhập nông sản tăng, xuất khẩu giảm', 'Nông sản xuất giảm nhập tăng, suất siêu giảm 8,7%. Đây là dấu hiệu đáng chú ý của ngành nông nghiệp sau nhiều năm phát triển và tự hào là đất nước có thế mạnh về nông nghiệp.', '<p><a href=\"https://1.bp.blogspot.com/-6i40gc8hxgs/XN9piOF1GaI/AAAAAAAClhY/G04b1AAXpygdsUMCkO3vHRoBqYAsERUeACLcBGAs/s1600/ca-phe.jpg\"><img src=\"https://1.bp.blogspot.com/-6i40gc8hxgs/XN9piOF1GaI/AAAAAAAClhY/G04b1AAXpygdsUMCkO3vHRoBqYAsERUeACLcBGAs/s400/ca-phe.jpg\" style=\"height:300px; width:400px\" /></a></p>\r\n\r\n<p><em>C&agrave; ph&ecirc; l&agrave; một trong những mặt h&agrave;ng bị t&aacute;c động mạnh trong thời gian qua. CTV</em></p>\r\n\r\n<p><a name=\"more\"></a></p>\r\n\r\n<p>B&aacute;o c&aacute;o của Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n cho biết: Trong 4 th&aacute;ng đầu năm 2019, kim ngạch xuất khẩu c&aacute;c mặt h&agrave;ng n&ocirc;ng, l&acirc;m, thủy sản ước đạt 12,4 tỉ USD. Trong đ&oacute;, nh&oacute;m h&agrave;ng n&ocirc;ng sản ch&iacute;nh ước đạt 5,2 tỉ USD, so với c&ugrave;ng kỳ năm 2018 giảm 5,6%. Nhiều mặt h&agrave;ng giảm mạnh như: gạo khối lượng xuất khẩu giảm 8%, gi&aacute; trị giảm đến 19%); c&agrave; ph&ecirc; khối lượng giảm 13%, gi&aacute; trị cũng giảm 19%; sắn v&agrave; sản phẩm từ sắn khối lượng giảm 14%, gi&aacute; trị giảm 3,3%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở chiều ngược lại, tổng gi&aacute; trị nhập khẩu n&ocirc;ng, l&acirc;m, thủy sản v&agrave; vật tư n&ocirc;ng nghiệp 4 th&aacute;ng đạt khoảng 9,7 tỉ USD, tăng 3,2% so với c&ugrave;ng kỳ năm trước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Việc nh&oacute;m ng&agrave;nh n&ocirc;ng l&acirc;m thủy hải sản xuất giảm nhập tăng đ&atilde; l&agrave;m thặng dự thương mại giảm 8,7% so với c&ugrave;ng kỳ 2018, hiện xuất si&ecirc;u chỉ đạt 2,68 tỉ USD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, b&aacute;o c&aacute;o của Tổng cục Hải quan cho biết: Trong 4 th&aacute;ng đầu năm nay, nh&oacute;m h&agrave;ng n&ocirc;ng sản (bao gồm h&agrave;ng rau quả, hạt điều, hạt ti&ecirc;u, ch&egrave;, c&agrave; ph&ecirc;, gạo, sắn v&agrave; sản phẩm sắn, cao su) xuất khẩu đạt 5,56 tỉ USD, giảm 8,9% so với c&ugrave;ng kỳ năm trước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trung Quốc vẫn l&agrave; thị trường lớn nhất nhập khẩu mặt h&agrave;ng n&ocirc;ng sản của Việt Nam với 1,98 tỉ USD, giảm 8,7% so với c&ugrave;ng kỳ năm 2018; tiếp theo l&agrave; thị trường EU đạt 866 triệu USD, giảm 9,7%; sang Mỹ với 522 triệu USD, giảm 19,8%...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&oacute;m h&agrave;ng thủy sản đạt 2,42 tỉ USD, giảm 0,9% so với c&ugrave;ng thời gian năm 2018. Trong 4 th&aacute;ng t&iacute;nh từ đầu năm 2019 chủ yếu được xuất khẩu sang thị trường Nhật Bản đạt 411 triệu USD, tăng 9,7%; Mỹ đạt 386 triệu USD, tăng 1,9%; EU với 393 triệu USD, giảm 9,6%; Trung Quốc đạt 268 triệu USD, giảm 8,6%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gỗ v&agrave; sản phẩm gỗ l&agrave; ng&agrave;nh h&agrave;ng tăng trưởng ấn tượng với tỷ lệ đến gần 18% so với c&ugrave;ng kỳ năm trước, đạt 3,1 tỉ USD. Đ&aacute;ng ch&uacute; &yacute;, xuất khẩu sang Mỹ đạt tr&ecirc;n 1,4 tỉ USD, tăng đến gần 35% v&agrave; Nhật Bản đạt 414 triệu USD, tăng tr&ecirc;n 18%.</p>\r\n', 3, 1, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (4, 'nong san 1.png', '[Infographics] 4 tháng năm 2019: Xuất khẩu nông lâm thủy sản ước đạt 12,4 tỷ USD', '4 tháng đầu năm 2019, tổng kim ngạch xuất khẩu nông lâm thủy sản ước đạt 12,4 tỷ USD, tương đương so với cùng kỳ 2018, trong đó, nhóm hàng nông sản chính ước đạt 5,2 tỷ USD, giảm 5,6% so với cùng kỳ.', '<p><a href=\"https://4.bp.blogspot.com/-YKLqdZuhDgI/XM0QoeBn4lI/AAAAAAAClFk/wRkaidniKi40dyiKHPofxYvfBVIrV-3ygCLcBGAs/s1600/nong%2Bsan%2B1.png\"><img src=\"https://4.bp.blogspot.com/-YKLqdZuhDgI/XM0QoeBn4lI/AAAAAAAClFk/wRkaidniKi40dyiKHPofxYvfBVIrV-3ygCLcBGAs/s1600/nong%2Bsan%2B1.png\" /></a></p>\r\n\r\n<p><a href=\"https://2.bp.blogspot.com/-7kOfn3GCWus/XM0Qoa4N7zI/AAAAAAAClFg/W8lm7R80tGgKH4PXGjF-hgb1QJeuEgjpQCLcBGAs/s1600/nong%2Bsan%2B2.png\"><img src=\"https://2.bp.blogspot.com/-7kOfn3GCWus/XM0Qoa4N7zI/AAAAAAAClFg/W8lm7R80tGgKH4PXGjF-hgb1QJeuEgjpQCLcBGAs/s1600/nong%2Bsan%2B2.png\" /></a></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Lũy kế 4 th&aacute;ng đầu năm 2019, tổng kim ngạch xuất khẩu n&ocirc;ng l&acirc;m thủy sản ước đạt 12,4 tỷ USD, tương đương so với c&ugrave;ng kỳ 2018.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&oacute;m h&agrave;ng n&ocirc;ng sản ch&iacute;nh ước đạt 5,2 tỷ USD, giảm 5,6% so với c&ugrave;ng kỳ năm 2018./.</p>\r\n', 1, 4, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (5, 'tieu.jpg', 'Nông sản ngày càng khó vào EU', 'Liên minh châu Âu (EU) đang tăng cường bảo hộ hàng nông sản thông qua tiêu chuẩn về quản lý chất lượng và an toàn thực phẩm, yêu cầu về truy xuất nguồn gốc, gây áp lực không nhỏ cho nông sản Việt Nam.', '<p><strong>Li&ecirc;n tục r&agrave; so&aacute;t v&agrave; thay đổi</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>EU vừa th&ocirc;ng b&aacute;o sửa đổi phụ lục II, III, IV trong Quy định số 396/2005 của EU li&ecirc;n quan đến quy định dư lượng tối đa c&aacute;c loại h&oacute;a chất như chlorantraniliprole, clomazone, cyclaniliprole, fenazaquin... tr&ecirc;n một số sản phẩm như rau, quả v&agrave; thực phẩm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những thay đổi n&agrave;y, theo &ocirc;ng Vương Trường Giang, Trưởng ph&ograve;ng An to&agrave;n thực phẩm v&agrave; M&ocirc;i trường, Cục Bảo vệ thực vật, Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n (NN&amp;PTNT), sẽ ảnh hưởng tới một số loại n&ocirc;ng sản m&agrave; Việt Nam xuất sang EU. Hiện nay, Việt Nam chủ yếu xuất khẩu c&aacute;c mặt h&agrave;ng như c&agrave; ph&ecirc;, ti&ecirc;u, điều, gạo, thanh long, v&agrave; một số &iacute;t loại rau gia vị kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Số liệu của Cục Chế biến v&agrave; Ph&aacute;t triển thị trường n&ocirc;ng sản, Bộ NN&amp;PTNT cho thấy, một số mặt h&agrave;ng n&ocirc;ng sản chủ lực xuất khẩu sang EU như ti&ecirc;u, điều, c&agrave; ph&ecirc; đều c&oacute; kim ngạch xuất khẩu giảm trong th&aacute;ng 2. V&iacute; dụ, khối lượng xuất khẩu hồ ti&ecirc;u th&aacute;ng 2-2019 ước đạt 12.000 tấn, với gi&aacute; trị đạt 35 triệu đ&ocirc; la Mỹ, giảm tới hơn 20% về gi&aacute; trị so với c&ugrave;ng kỳ năm 2018. Đ&acirc;y l&agrave; một trong những mặt h&agrave;ng c&oacute; kim ngạch xuất khẩu giảm mạnh nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tổng kim ngạch xuất khẩu n&ocirc;ng l&acirc;m thủy sản hai th&aacute;ng đầu năm 2019 đạt 5,5 tỉ đ&ocirc; la Mỹ, giảm 1,6% so với c&ugrave;ng kỳ năm ngo&aacute;i, trong đ&oacute; gi&aacute; trị xuất khẩu c&aacute;c mặt h&agrave;ng n&ocirc;ng sản ch&iacute;nh ước đạt 2,71 tỉ đ&ocirc; la, giảm 10,1%.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>EU l&agrave; một trong những thị trường xuất khẩu n&ocirc;ng sản lớn nhất của Việt Nam, song cũng l&agrave; thị trường c&oacute; y&ecirc;u cầu khắt khe về vệ sinh an to&agrave;n thực phẩm, d&aacute;n nh&atilde;n, m&ocirc;i trường&hellip; D&ugrave; lần thay đổi n&agrave;y t&aacute;c động kh&ocirc;ng qu&aacute; lớn đối với c&aacute;c mặt h&agrave;ng n&ocirc;ng sản chủ lực m&agrave; Việt Nam xuất sang EU, nhưng theo đ&aacute;nh gi&aacute; của &ocirc;ng Giang, c&aacute;c quy định ph&aacute;p l&yacute; li&ecirc;n quan tới h&agrave;ng r&agrave;o kỹ thuật li&ecirc;n tục được EU &ldquo;r&agrave; so&aacute;t&rdquo; v&agrave; &ldquo;điều chỉnh&rdquo;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<blockquote><em>H&agrave;ng h&oacute;a n&ocirc;ng nghiệp của Việt Nam đang được xuất khẩu sang 180 nước v&agrave; v&ugrave;ng l&atilde;nh thổ, đứng thứ 2 khu vực Đ&ocirc;ng Nam &Aacute; v&agrave; đứng thứ 15 tr&ecirc;n thế giới, với 10 mặt h&agrave;ng xuất khẩu tr&ecirc;n 1 tỉ đ&ocirc; la Mỹ, 6 mặt h&agrave;ng đạt tr&ecirc;n 3 tỉ đ&ocirc; la Mỹ gồm t&ocirc;m, gỗ v&agrave; sản phẩm gỗ, h&agrave;ng rau quả, hạt điều, c&agrave; ph&ecirc;, gạo.</em></blockquote>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hiện nay, nhiều doanh nghiệp c&ograve;n chưa mấy quan t&acirc;m tới sự thay đổi kh&aacute; &ldquo;thường xuy&ecirc;n&rdquo; n&agrave;y của EU, đặc biệt trong bối cảnh đa phần c&aacute;c doanh nghiệp xuất khẩu c&oacute; quy m&ocirc; nhỏ v&agrave; si&ecirc;u nhỏ. Theo quan s&aacute;t của &ocirc;ng Giang, ng&agrave;nh n&ocirc;ng sản Việt Nam ng&agrave;y c&agrave;ng chiếm tỷ trọng lớn trong cơ cấu nhập khẩu của EU, khiến cơ quan chức năng của khối n&agrave;y để &yacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;C&aacute;i kh&oacute; của doanh nghiệp n&ocirc;ng sản hiện nay l&agrave; l&agrave;m thế n&agrave;o cập nhật những thay đổi từ ph&iacute;a EU, từ đ&oacute; kiểm so&aacute;t v&agrave; đ&aacute;p ứng được chất lượng n&ocirc;ng sản xuất khẩu sang khu vực n&agrave;y&rdquo;</em>, &ocirc;ng Giang n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Lo ngại quy tắc xuất xứ</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Bộ trưởng Bộ NN&amp;PTNT Nguyễn Xu&acirc;n Cường, tại diễn đ&agrave;n về xuất khẩu n&ocirc;ng sản mới đ&acirc;y, đ&atilde; khẳng định thực tế c&aacute;c quốc gia đang tăng cường bảo hộ h&agrave;ng n&ocirc;ng sản th&ocirc;ng qua ti&ecirc;u chuẩn về quản l&yacute; chất lượng v&agrave; an to&agrave;n thực phẩm, y&ecirc;u cầu về truy xuất nguồn gốc, trong đ&oacute; c&oacute; EU. Từ g&oacute;c độ chuy&ecirc;n ng&agrave;nh, &ocirc;ng Giang khuyến c&aacute;o c&aacute;c doanh nghiệp cần nắm chắc những thay đổi n&agrave;y từ EU th&ocirc;ng qua trang web của Cao ủy thực phẩm của EU.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Giang cho biết th&ecirc;m, trang web của Cục Bảo vệ thực vật cũng thường xuy&ecirc;n cập nhật những thay đổi tại c&aacute;c thị trường m&agrave; Việt Nam xuất khẩu n&ocirc;ng sản, do đ&oacute; c&aacute;c doanh nghiệp l&agrave;m ăn với thị trường EU cần li&ecirc;n tục cập nhật để chủ động kế hoạch kinh doanh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Aacute;p lực đ&aacute;p ứng c&aacute;c ti&ecirc;u chuẩn, chất lượng h&agrave;ng n&ocirc;ng sản sẽ lớn hơn nữa khi Hiệp định Thương mại tự do (FTA) giữa Việt Nam v&agrave; EU (EVFTA) được ph&iacute;a EU th&ocirc;ng qua. D&ugrave; EVFTA sẽ tạo điều kiện thuận lợi để Việt Nam xuất khẩu n&ocirc;ng sản sang EU th&ocirc;ng qua việc dỡ bỏ r&agrave;o cản thuế quan, chuy&ecirc;n gia kinh tế Phạm Thị Dự, Đại học Thương mại, cho rằng việc đ&aacute;p ứng c&aacute;c quy tắc xuất xứ hay vượt qua được c&aacute;c r&agrave;o cản kỹ thuật, kiểm dịch động thực vật l&agrave; c&aacute;c vấn đề đ&aacute;ng quan ngại đối với nhiều doanh nghiệp.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo b&agrave; Dự, nguồn nguy&ecirc;n liệu cho sản xuất, xuất khẩu hiện nay của Việt Nam chủ yếu được nhập khẩu từ Trung Quốc, ASEAN v&agrave; c&aacute;c nước kh&ocirc;ng thuộc khối c&oacute; FTA với Việt Nam. V&iacute; dụ hạt điều, một trong những mặt h&agrave;ng xuất khẩu chủ lực của Việt Nam sang EU, được nhập khẩu nguy&ecirc;n liệu chủ yếu từ Bờ Biển Ng&agrave;, Ghana, Nigeria, chiếm 63% nhu cầu chế biến. Hiện nay, EU c&oacute; quy định tương đối chặt chẽ về nguồn gốc xuất xứ với hạt điều khi xem c&ocirc;ng đoạn gia c&ocirc;ng, b&oacute;c vỏ hạt điều l&agrave; chế biến giản đơn. Do đ&oacute;, muốn xuất khẩu được hưởng lợi về thuế quan, ng&agrave;nh điều nh&acirc;n của Việt Nam phải chế biến từ nguồn điều nguy&ecirc;n liệu sản xuất trong nước.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, theo Cục Chế biến v&agrave; Ph&aacute;t triển thị trường n&ocirc;ng sản, xuất khẩu hạt điều của Việt Nam hai th&aacute;ng đầu năm 2019 đ&atilde; giảm cả về khối lượng v&agrave; gi&aacute; trị khi đạt 44.000 tấn v&agrave; 365 triệu đ&ocirc; la Mỹ, giảm lần lượt 3,1% v&agrave; 22,3% so với c&ugrave;ng kỳ năm 2018. C&aacute;c chuy&ecirc;n gia cho rằng ng&agrave;nh điều Việt Nam đang bị mất c&acirc;n đối nghi&ecirc;m trọng giữa năng lực chế biến v&agrave; sản lượng sản xuất nội địa. Để giữ vững vị thế dẫn đầu thế giới, ng&agrave;nh điều cần tập trung cải thiện năng suất v&agrave; chất lượng, từ đ&oacute; t&igrave;m kiếm cơ hội tăng xuất khẩu sang c&aacute;c thị trường kh&oacute; t&iacute;nh như Mỹ, EU.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để ứng ph&oacute; với t&igrave;nh trạng c&aacute;c quốc gia tăng cường dựng c&aacute;c h&agrave;ng r&agrave;o bảo hộ, một trong những giải ph&aacute;p trọng t&acirc;m m&agrave; Bộ NN&amp;PTNT thực hiện năm 2019 l&agrave; x&acirc;y dựng ti&ecirc;u chu&acirc;̉n, quy chu&acirc;̉n qu&ocirc;́c gia theo hướng hài hòa hóa các quy định của các thị trường nhập kh&acirc;̉u khu vực và tr&ecirc;n th&ecirc;́ giới. Ngo&agrave;i ra, bộ n&agrave;y cũng cho biết sẽ xử lý nghi&ecirc;m các hành vi gian lận, ảnh hưởng đ&ecirc;́n ch&acirc;́t lượng hàng hóa n&ocirc;ng sản, nhằm giữ uy tín cho hàng hóa n&ocirc;ng sản của Việt Nam tr&ecirc;n cả thị trường trong nước và qu&ocirc;́c t&ecirc;́.</p>\r\n', 1, 4, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (6, 'images1520141_TNB_6218.jpg', 'Đánh bắt vụ cá nam: Ngư dân gặp khó', 'Ngư dân đang gặp khó trong vụ cá nam - vụ cá chính, từ đầu tháng 4 đến cuối tháng 9, do thiếu lao động và áp lực tăng giá thành sản xuất.', '<p><strong>Chi ph&iacute; tăng vọt</strong></p>\r\n\r\n<p>Điện v&agrave; xăng dầu tăng gi&aacute; trong thời gian gần đ&acirc;y đ&atilde; &ldquo;k&iacute;ch&rdquo; gi&aacute; d&acirc;y chuyền c&aacute;c mặt h&agrave;ng thiết yếu như lương thực, thực phẩm, đ&aacute; c&acirc;y, nước uống, gas, ngư lưới cụ tăng vọt khiến ngư d&acirc;n &ldquo;ch&oacute;ng mặt&rdquo;. &Ocirc;ng Huỳnh Minh Cảnh ở th&ocirc;n S&acirc;m Linh Đ&ocirc;ng, x&atilde; Tam Quang (N&uacute;i Th&agrave;nh) - chủ đội t&agrave;u đ&aacute;nh bắt hải sản xa bờ 5 chiếc, lo ngại gi&aacute; trị kinh tế sau mỗi chuyến biển trong vụ ch&iacute;nh n&agrave;y sẽ sụt giảm. &ldquo;Mỗi t&agrave;u c&aacute; c&oacute; chi ph&iacute; mỗi chuyến biển d&agrave;i ng&agrave;y hơn 200 triệu đồng, trong đ&oacute;, dầu đ&atilde; tốn gần 170 triệu đồng. Gi&aacute; th&agrave;nh sản xuất tăng l&ecirc;n, trong khi đ&oacute;, đầu ra hải sản vẫn bấp b&ecirc;nh. Lợi nhuận thu được sau mỗi chuyến biển chắc chắn sẽ giảm xuống do sản lượng hải sản thu được kh&ocirc;ng cao. Sản lượng c&aacute; mực đ&aacute;nh bắt tr&ecirc;n c&aacute;c v&ugrave;ng biển xa ng&agrave;y một &iacute;t dần&rdquo; - &ocirc;ng Cảnh n&oacute;i. Theo &ocirc;ng Cảnh, 5 t&agrave;u c&ocirc;ng suất lớn sản xuất theo m&ocirc; h&igrave;nh t&agrave;u mẹ, t&agrave;u con, hỗ trợ lẫn nhau trong qu&aacute; tr&igrave;nh đ&aacute;nh bắt hải sản, sau đ&oacute;, t&agrave;u con sẽ về đất liền b&aacute;n hải sản, mua nhi&ecirc;n liệu v&agrave; nhu yếu phẩm ra biển cung cấp cho c&aacute;c t&agrave;u. Tuy vậy, sản lượng hải sản thu được kh&ocirc;ng cao, chỉ chừng 20 tấn, lợi nhuận dư d&ocirc;i kh&ocirc;ng nhiều d&ugrave; đ&atilde; tiết kiệm chi ph&iacute;.</p>\r\n\r\n<p>T&iacute;ch lũy vốn liếng trong gần 10 năm qua, ngư d&acirc;n Trần Bẹn ở th&ocirc;n S&acirc;m Linh Đ&ocirc;ng, x&atilde; Tam Quang (N&uacute;i Th&agrave;nh) quyết định b&aacute;n t&agrave;u cũ để đ&oacute;ng t&agrave;u vỏ gỗ mới l&agrave; QNa-91594 c&oacute; c&ocirc;ng suất 718CV trị gi&aacute; gần 5 tỷ đồng h&agrave;nh nghề lưới v&acirc;y truyền thống. Ngư d&acirc;n n&agrave;y lo lắng, qu&aacute; tr&igrave;nh b&aacute;m biển d&agrave;i ng&agrave;y trong vụ c&aacute; ch&iacute;nh sẽ gặp rất nhiều nguy nan. &ldquo;Ch&uacute;ng t&ocirc;i ng&agrave;y c&agrave;ng th&ecirc;m kh&oacute; trong mỗi chuyến ra khơi đ&aacute;nh bắt hải sản xa bờ. C&aacute;c mặt h&agrave;ng thiết yếu tăng gi&aacute; cộng dồn lại khiến gi&aacute; th&agrave;nh rất cao. Chuyến biển mới đ&acirc;y, t&ocirc;i v&agrave; 15 bạn biển chỉ khai th&aacute;c được 5 tấn c&aacute; nục, c&aacute; ngừ sau 20 ng&agrave;y sản xuất. B&aacute;n hải sản xong, thu chỉ đủ b&ugrave; chi. C&aacute;c bạn biển bảo nếu kết quả chuyến biển sau cũng vậy, họ sẽ l&agrave;m cho chủ t&agrave;u c&aacute; kh&aacute;c&rdquo; - &ocirc;ng Bẹn n&oacute;i. Ngư d&acirc;n đề xuất, c&aacute;c ng&agrave;nh chức năng ở tỉnh, Trung ương tổ chức khảo s&aacute;t, điều tra, đ&aacute;nh gi&aacute; lại trữ lượng c&aacute;c lo&agrave;i c&aacute; nổi ở c&aacute;c ngư trường truyền thống l&agrave; Ho&agrave;ng Sa, Trường Sa, qua đ&oacute;, th&ocirc;ng tin rộng r&atilde;i để gi&uacute;p ngư d&acirc;n sản xuất tốt hơn. &ldquo;Ch&uacute;ng t&ocirc;i đ&atilde; t&iacute;nh to&aacute;n hết nước, nhưng c&aacute;c mặt h&agrave;ng thiết yếu tăng li&ecirc;n tục n&ecirc;n đ&agrave;nh bất lực. D&ugrave; đ&atilde; linh hoạt, năng động, tổ chức sản xuất tốt nhưng trong điều kiện hải sản qu&aacute; khan hiếm, kết quả thu được kh&ocirc;ng như kỳ vọng&rdquo; - &ocirc;ng Bẹn cho biết th&ecirc;m.</p>\r\n\r\n<p><strong>Thiếu lao động</strong></p>\r\n\r\n<p>V&agrave;o m&ugrave;a cao điểm của nghề c&acirc;u c&aacute; hố nhưng chủ c&aacute;c t&agrave;u đứng ngồi kh&ocirc;ng y&ecirc;n v&igrave; thiếu lao động. Ngư d&acirc;n Nguyễn Một ở khối phố Phước Thịnh, phường Cửa Đại (TP.Hội An) - chủ t&agrave;u QNa-92369 cho biết, trung b&igrave;nh mỗi chuyến biển k&eacute;o d&agrave;i 10 ng&agrave;y với 10 lao động. Do chỉ mời gọi được 5 lao động n&ecirc;n thời gian b&aacute;m biển tăng l&ecirc;n 15 ng&agrave;y, sản xuất gặp kh&oacute; n&ecirc;n sản lượng kh&ocirc;ng cao. C&aacute; hố để l&acirc;u khiến tư thương &eacute;p gi&aacute;, hạ xuống chỉ c&ograve;n 120 ngh&igrave;n đồng/kg, thấp hơn 50 ngh&igrave;n đồng/kg so với trước đ&acirc;y. &ldquo;Kh&oacute; c&oacute; nghề n&agrave;o chiều chuộng lao động như nghề biển. Cứ trước mỗi chuyến biển, t&ocirc;i phải ứng trước cho bạn biển chừng 10 triệu đồng/người, rồi hứa hẹn, động vi&ecirc;n họ đủ kiểu. Vậy nhưng, c&oacute; nhiều bạn biển d&ugrave; đ&atilde; nhận tiền nhưng lại trốn khiến chủ t&agrave;u gặp v&ocirc; v&agrave;n kh&oacute; khăn&rdquo; - &ocirc;ng Một n&oacute;i. Được biết, nhiều thanh ni&ecirc;n ở c&aacute;c v&ugrave;ng biển như Duy Hải, Duy Nghĩa (Duy Xuy&ecirc;n), B&igrave;nh Minh, B&igrave;nh Nam, B&igrave;nh Hải, B&igrave;nh Dương (Thăng B&igrave;nh), Tam Ph&uacute;, Tam Thanh (TP.Tam Kỳ), Tam Quang, Tam Nghĩa, thị trấn N&uacute;i Th&agrave;nh (N&uacute;i Th&agrave;nh) thấy gi&aacute; đất &ldquo;nhảy m&uacute;a&rdquo; n&ecirc;n ở nh&agrave; l&agrave;m &ldquo;c&ograve; đất&rdquo; khiến cho nguồn lao động nghề biển thiếu hụt nghi&ecirc;m trọng. Trong khi đ&oacute;, sản xuất d&agrave;i ng&agrave;y tr&ecirc;n biển rất vất vả, nhiều thanh ni&ecirc;n vốn theo nghề biển cũng đ&atilde; chuyển nghề l&ecirc;n bờ lao động nh&agrave;n rỗi hơn.</p>\r\n\r\n<p>Theo Chi cục Thủy sản Quảng Nam, thiếu lao động nghề c&aacute; l&agrave; vấn đề rất kh&oacute; giải quyết để ph&aacute;t triển bền vững nghề khai th&aacute;c hải sản ở Quảng Nam. Ngư d&acirc;n gi&agrave; yếu cần được thay thế nhưng kh&ocirc;ng c&oacute; người trẻ tiếp nối, buộc phải b&aacute;m trụ với nghề biển. Ngư d&acirc;n trẻ cần được đ&agrave;o tạo, ứng dụng c&ocirc;ng nghệ mới, th&agrave;nh thục c&aacute;c thao t&aacute;c kỹ thuật ti&ecirc;n tiến lại chuyển nghề l&ecirc;n bờ lao động. Nhiều lớp đ&agrave;o tạo thuyền trưởng, m&aacute;y trưởng cho ngư d&acirc;n được mở ra nhưng lại quy tụ được rất &iacute;t học vi&ecirc;n, phần do b&aacute;m biển quanh năm, phần do ngư d&acirc;n kh&ocirc;ng muốn tập trung ở lớp học. &ldquo;Giải quyết vấn đề n&agrave;y cần sự v&agrave;o cuộc đồng bộ của ng&agrave;nh thủy sản, c&aacute;c địa phương ven biển, ng&agrave;nh lao động v&agrave; c&aacute;c cơ quan kh&aacute;c, qua đ&oacute; c&oacute; thể tham mưu UBND tỉnh c&oacute; c&aacute;c giải ph&aacute;p ph&ugrave; hợp, kịp thời để gỡ vướng, khắc phục thực trạng thiếu v&agrave; yếu lao động nghề c&aacute;&rdquo; - b&agrave; Phạm Thị Ho&agrave;ng T&acirc;m - Chi Cục trưởng Chi cục Thủy sản Quảng Nam n&oacute;i.</p>\r\n', 2, 2, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (7, 'images5359855___M_TH_Y_TRI_U.jpg', 'Thu nhập cao từ khai thác thức ăn cho tôm hùm', 'Năm nay, ngoài các loại rau câu, đầm Thủy Triều (huyện Cam Lâm) còn xuất hiện nhiều nguồn lợi khác như: vẹm đất, quy, phi… dùng làm thức ăn cho tôm hùm, ốc hương nuôi, mang lại thu nhập cao cho người khai thác.', '<p>&Ocirc;ng Nguyễn Văn Th&ocirc;ng (x&atilde; Cam H&ograve;a) cho hay: &ldquo;Chưa c&oacute; năm n&agrave;o thượng nguồn đầm Thủy Triều lại xuất hiện nhiều vẹm đất đến vậy. Từ cuối năm trước đến nay, tranh thủ khi chưa nu&ocirc;i t&ocirc;m, vợ chồng t&ocirc;i lại ra đầm để bắt loại thủy sản th&acirc;n mềm, 2 mảnh vỏ n&agrave;y để b&aacute;n cho thương l&aacute;i. Đ&acirc;y kh&ocirc;ng phải l&agrave; nghề ch&iacute;nh, nhưng mang lại thu nhập kh&aacute; cao cho gia đ&igrave;nh, cứ mỗi con nước, nếu si&ecirc;ng năng vợ chồng t&ocirc;i cũng kiếm được hơn 1 triệu đồng&rdquo;.</p>\r\n\r\n<p>Trong khi đ&oacute;, mỗi ng&agrave;y, vợ chồng &ocirc;ng Trần Th&agrave;nh (x&atilde; Cam Hải Đ&ocirc;ng) kiếm được chừng 1 ghe vẹm đất nhờ ra giữa đầm lặn bắt. &ldquo;Trước đ&acirc;y, lo&agrave;i n&agrave;y sinh s&ocirc;i nhưng người d&acirc;n kh&ocirc;ng khai th&aacute;c v&igrave; thịt &iacute;t, gi&aacute; trị thấp. Những năm gần đ&acirc;y, khi nghề nu&ocirc;i t&ocirc;m h&ugrave;m lồng ph&aacute;t triển mạnh, nhiều thương l&aacute;i t&igrave;m mua lo&agrave;i vẹm n&agrave;y với gi&aacute; kh&aacute; cao để b&aacute;n lại cho c&aacute;c hộ nu&ocirc;i t&ocirc;m. Hiện nay, vẹm đất được thu mua với gi&aacute; 6.000 đồng/kg, mỗi con nước, vợ chồng t&ocirc;i khai th&aacute;c được hơn 250kg, kiếm được chừng 1,5 triệu đồng&rdquo;.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo &ocirc;ng Th&agrave;nh, để bắt được chừng ấy vẹm đất rất vất vả. M&ugrave;a n&agrave;y, thủy triều r&uacute;t muộn, từ chập tối vợ chồng &ocirc;ng đ&atilde; chạy ghe ra giữa đầm để lặn bắt. &Ocirc;ng phải dầm m&igrave;nh dưới nước để bắt vẹm, tr&ecirc;n ghe, vợ &ocirc;ng tranh thủ t&aacute;ch, s&agrave;ng rồi đ&oacute;ng th&agrave;nh từng bao. Cứ thế, đến khoảng nửa đ&ecirc;m th&igrave; quay ghe v&agrave;o bờ, chở đến điểm thu mua để b&aacute;n.</p>\r\n\r\n<p>Kh&ocirc;ng chỉ những người lặn bắt vẹm đất, những người lớn tuổi hơn chọn c&aacute;ch đi bắt vẹm đất, đ&agrave;o phi, đ&agrave;o quy&hellip; ở ven đầm cũng c&oacute; thu nhập cao. B&agrave; Nguyễn Thị Hội (x&atilde; Cam H&ograve;a) chia sẻ: &ldquo;T&ocirc;i lội ven đầm để bắt vẹm đất, mỗi con nước kiếm được 50 - 60kg, t&iacute;nh ra cũng được v&agrave;i trăm ngh&igrave;n đồng. Những vợ chồng trẻ hơn th&igrave; đi đ&agrave;o phi, đ&agrave;o quy (cũng d&ugrave;ng l&agrave;m thức ăn cho t&ocirc;m h&ugrave;m), c&oacute; gi&aacute; b&aacute;n cao hơn, l&ecirc;n đến 30.000 đồng/kg n&ecirc;n thu nhập cũng cao hơn. T&iacute;nh ra, khu vực b&atilde;i triều v&agrave; mặt đầm Thủy Triều đoạn c&aacute;c x&atilde; Cam H&ograve;a, Cam Hải T&acirc;y c&oacute; h&agrave;ng trăm người tranh thủ l&uacute;c n&ocirc;ng nh&agrave;n đi đ&agrave;o quy, lặn vẹm về b&aacute;n&rdquo;.</p>\r\n\r\n<p>Hiện nay, ở những khu vực ven biển của x&atilde; Cam H&ograve;a, Cam Hải T&acirc;y c&oacute; nhiều điểm thu mua c&aacute;c loại thủy sản l&agrave;m thức ăn cho t&ocirc;m h&ugrave;m, trung b&igrave;nh mỗi x&atilde; c&oacute; đến 5 - 6 điểm, cao điểm mỗi điểm c&oacute; thể thu đến 3 - 4 tấn c&aacute;c loại thủy sản 2 mảnh vỏ n&agrave;y. &ldquo;Hầu hết vẹm đất, quy, phi ch&uacute;ng t&ocirc;i thu mua đều được đưa v&agrave;o TP. Cam Ranh b&aacute;n cho c&aacute;c hộ nu&ocirc;i lồng b&egrave;. Đối với con quy, do l&agrave;m thức ăn cho t&ocirc;m h&ugrave;m b&ocirc;ng n&ecirc;n c&oacute; gi&aacute; cao, đến 30.000 đồng/kg, c&ograve;n vẹm đất l&agrave;m thức ăn cho t&ocirc;m h&ugrave;m xanh n&ecirc;n gi&aacute; thấp hơn. Hiện nay, nhu cầu thức ăn tươi cho t&ocirc;m h&ugrave;m rất lớn, c&oacute; bao nhi&ecirc;u cũng ti&ecirc;u thụ hết. Kh&ocirc;ng ri&ecirc;ng Cam H&ograve;a, Cam Hải T&acirc;y m&agrave; c&aacute;c x&atilde; kh&aacute;c như: Cam Hải Đ&ocirc;ng, Cam Th&agrave;nh Bắc&hellip; cũng c&oacute; nhiều người đi khai th&aacute;c c&aacute;c loại thủy sản n&agrave;y l&agrave;m thức ăn cho t&ocirc;m h&ugrave;m&rdquo;, b&agrave; Thanh - người thu mua cho hay.</p>\r\n\r\n<p>C&aacute;c nguồn lợi thủy sản tr&ecirc;n đầm Thủy Triều đ&atilde; mang lại thu nhập kh&aacute; cao cho người d&acirc;n sinh sống ven đầm n&ecirc;n thu h&uacute;t nhiều người tranh thủ l&uacute;c n&ocirc;ng nh&agrave;n đi khai th&aacute;c. Tuy nhi&ecirc;n, vấn đề đặt ra l&agrave; người d&acirc;n cần khai th&aacute;c hợp l&yacute;, song song với bảo vệ để nguồn lợi được bền vững. Ngo&agrave;i ra, c&aacute;c địa phương cũng lo lắng việc đ&agrave;o xới đ&aacute;y đầm để khai th&aacute;c c&aacute;c loại thức ăn cho t&ocirc;m h&ugrave;m sẽ khiến nguồn nước phục vụ nu&ocirc;i trồng thủy sản ao đ&igrave;a của c&aacute;c hộ bị ảnh hưởng. Bởi đ&acirc;y ch&iacute;nh l&agrave; vấn đề tạo n&ecirc;n xung đột lợi &iacute;ch giữa người đi khai th&aacute;c v&agrave; c&aacute;c hộ nu&ocirc;i đ&igrave;a ở một số địa phương ở Cam L&acirc;m.</p>\r\n', 2, 5, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (8, 'baba.jpg', 'Nuôi ba ba \'khổng lồ\' trong ao bèo, bán thịt, bán giống thu nửa tỷ', 'Trên vùng đất trũng \'chiêm khê, mùa úng\' xã Yên Dương (huyện Ý Yên, tỉnh Nam Định), anh Lê Hồng Dũng, 42 tuổi ở xóm Tây đã thành công với nghề nuôi ba ba gai. ', '<p>Năm 2004, c&ugrave;ng với nhiều thanh ni&ecirc;n địa phương, anh Dũng t&igrave;m đến v&ugrave;ng kinh tế mới của tỉnh Sơn La để lập nghiệp. Vốn sẵn nghề gia truyền, anh đ&atilde; mở một xưởng khai th&aacute;c v&agrave; chế biến gỗ ở gần s&ocirc;ng M&atilde;.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh sinh sống, l&agrave;m nghề tại đ&acirc;y, anh nhận thấy con ba ba gai được người d&acirc;n địa phương nu&ocirc;i nhỏ lẻ nhưng đem lại hiệu quả kinh tế cao, nghề n&agrave;y c&oacute; thể ph&aacute;t triển tại qu&ecirc; hương. Từ đ&oacute;, vừa l&agrave;m nghề để t&iacute;ch luỹ vốn, anh vừa t&igrave;m hiểu kỹ thuật nu&ocirc;i ba ba gai, nghi&ecirc;n cứu thị trường, chuẩn bị sẵn đầu ra khi về sản xuất tại địa phương.</p>\r\n\r\n<p>Năm 2007, anh &ldquo;hồi hương&rdquo; về qu&ecirc; nh&agrave; Hưng Y&ecirc;n v&agrave; đầu tư cải tạo ao của gia đ&igrave;nh nu&ocirc;i 150 con ba ba gai. Những năm đầu, anh Dũng gặp kh&ocirc;ng &iacute;t kh&oacute; khăn v&igrave; vốn &iacute;t, chưa c&oacute; kinh nghiệm nu&ocirc;i n&ecirc;n đ&agrave;n ba ba chậm lớn v&agrave; thất tho&aacute;t.</p>\r\n\r\n<p>Kh&ocirc;ng nản, anh miệt m&agrave;i t&igrave;m kiếm s&aacute;ch b&aacute;o đọc để học hỏi t&iacute;ch luỹ th&ecirc;m kinh nghiệm nu&ocirc;i ba ba gai &quot;khổng lồ&quot;, tham gia c&aacute;c lớp tập huấn kỹ thuật, đi tham quan c&aacute;c m&ocirc; h&igrave;nh nu&ocirc;i ba ba gai đạt hiệu quả cao ở c&aacute;c nơi. Năm 2010, anh đấu thầu 2.400m2 đất cấy l&uacute;a k&eacute;m hiệu quả tại x&oacute;m T&acirc;y để ph&aacute;t triển quy m&ocirc; nu&ocirc;i ba ba gai.</p>\r\n\r\n<p>Đến nay, số lượng ba ba gai của gia đ&igrave;nh anh Dũng đ&atilde; l&ecirc;n tới gần 1.000 con với 8 ao nu&ocirc;i ba ba gai thương phẩm v&agrave; 2 ao ương giống ba ba.</p>\r\n\r\n<p>Anh Dũng cho biết: &ldquo;So với c&aacute;c đối tượng nu&ocirc;i truyền thống kh&aacute;c, giống ba ba gai n&agrave;y kh&aacute; dễ nu&ocirc;i, &iacute;t khi bị dịch bệnh, lại cho hiệu quả kinh tế cao. Trung b&igrave;nh nu&ocirc;i khoảng 3 năm, ba ba sẽ đạt trọng lượng tr&ecirc;n 6kg, nếu nu&ocirc;i tiếp th&igrave; mỗi năm trọng lượng ba ba gai sẽ tăng th&ecirc;m từ 3-5kg, c&aacute; biệt c&oacute; con nặng đến 25kg. Hiện gia đ&igrave;nh t&ocirc;i c&oacute; hơn 80 cặp ba ba gai bố mẹ, 250 cặp ba ba gai thương phẩm v&agrave; 360 con ba ba giống&rdquo;.</p>\r\n\r\n<p>Chia sẻ kỹ thuật nu&ocirc;i ba ba gai, anh Dũng cho biết, trong những th&aacute;ng đầu sau khi thả con giống, người nu&ocirc;i cần quan t&acirc;m đến việc trị bệnh cho ba ba con, nhất l&agrave; bệnh nấm, n&ecirc;n thường xuy&ecirc;n d&ugrave;ng thuốc ph&ograve;ng ngừa bệnh, cho ăn đều đủ lượng để ba ba mau lớn.</p>\r\n\r\n<p>Ba ba gai l&agrave; lo&agrave;i ăn tạp, ph&agrave;m ăn; thức ăn dễ kiếm l&agrave; c&aacute;c loại phế phẩm gia s&uacute;c, gia cầm, thuỷ sản n&ecirc;n người nu&ocirc;i kh&ocirc;ng phải lo thiếu nguồn v&agrave; tốn chi ph&iacute; thức ăn. Ba ba gai c&oacute; thể nu&ocirc;i trong bể xi măng hoặc trong ao, mật độ thả nu&ocirc;i hợp l&yacute; l&agrave; 4 con/m2, độ s&acirc;u mực nước ao từ 1,5-2m, đ&aacute;y ao cần thiết kế c&oacute; độ nghi&ecirc;ng dần về cống tho&aacute;t nước đảm bảo thuận tiện kh&acirc;u vệ sinh ao.</p>\r\n\r\n<p>Người nu&ocirc;i ba ba gai phải thường xuy&ecirc;n kiểm tra lượng thức ăn trong ao. Ba ba gai ăn nhiều v&agrave; nu&ocirc;i lớn nhanh nhất v&agrave;o thời điểm từ th&aacute;ng 3 đến th&aacute;ng 9 &acirc;m lịch. Từ th&aacute;ng 9 &acirc;m lịch, thời tiết trở lạnh, ba ba ăn &iacute;t, ng&acirc;m m&igrave;nh dưới b&ugrave;n n&ecirc;n người nu&ocirc;i tr&aacute;nh cho ăn nhiều l&atilde;ng ph&iacute; v&agrave; dễ g&acirc;y &ocirc; nhiễm nguồn nước do thức ăn tồn đọng.</p>\r\n\r\n<p>Theo anh Dũng, nu&ocirc;i ba ba gai ngo&agrave;i thu nhập từ b&aacute;n ba ba thịt, người nu&ocirc;i cũng c&oacute; th&ecirc;m thu nhập từ trứng v&agrave; con giống.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Anh Dũng cho hay, ba ba gai 4 năm tuổi đ&atilde; bắt đầu đẻ. Do ba ba kh&ocirc;ng thể tự ấp trứng, người nu&ocirc;i phải chuẩn bị chuồng ấp trứng, nền chuồng được rải c&aacute;t v&agrave; đảm bảo nhiệt độ trung b&igrave;nh từ 28-32 độ C để tỷ lệ ấp nở đạt cao.</p>\r\n\r\n<p>Ba ba mẹ mỗi năm đẻ 3 lứa, mỗi lứa trung b&igrave;nh khoảng 30 quả trứng v&agrave; ấp hơn 60 ng&agrave;y l&agrave; nở. Hiện tại, gi&aacute; ba ba gai giống tr&ecirc;n thị trường l&agrave; 150 ngh&igrave;n đồng/con. Dịp Tết Kỷ Hợi 2019, gia đ&igrave;nh anh xuất b&aacute;n được hơn 500kg ba ba gai thịt thương phẩm với gi&aacute; từ 500-700 ngh&igrave;n đồng/kg. T&iacute;nh cả b&aacute;n con giống, trừ chi ph&iacute;, anh thu về hơn 500 triệu đồng.</p>\r\n\r\n<p>C&oacute; thể thấy, nu&ocirc;i ba ba gai mang lại hiệu quả kinh tế cao, &iacute;t rủi ro, đầu ra tương đối ổn định n&ecirc;n m&ocirc; h&igrave;nh nu&ocirc;i ba ba gai của anh Dũng đ&atilde; được nhiều hộ trong v&agrave; ngo&agrave;i x&atilde; tham quan học tập.</p>\r\n', 2, 8, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (9, '2gd_nthuan.jpg', 'Ninh Thuận: Hội thảo kỹ thuật nuôi tôm và phổ biến lịch thời vụ NTTS', 'Vừa qua, tại thôn Thủy Lợi, xã Hộ Hải, huyện Ninh Hải (tỉnh Ninh Thuận), Trung tâm Khuyến nông Ninh Thuận đã phối hợp với Công ty CP Chăn Nuôi C.P. Việt Nam tổ chức Hội thảo “Kỹ thuật nuôi tôm năm 2019 và lịch thời vụ nuôi trồng thủy sản tại Đầm Nại”.', '<p>Tham dự buổi hội thảo c&oacute; đại diện Trung t&acirc;m Khuyến n&ocirc;ng Ninh Thuận, C&ocirc;ng ty Cổ Phần Chăn nu&ocirc;i C.P. Việt Nam, Trạm Khuyến n&ocirc;ng Ninh Hải, UBND x&atilde; Hộ Hải, T&acirc;n Hải, Phương Hải v&agrave; hơn 100 hộ d&acirc;n tr&ecirc;n địa b&agrave;n c&aacute;c x&atilde; thuộc huyện Ninh Hải.</p>\r\n\r\n<p>Hội thảo tổ chức nhằm gi&uacute;p cho b&agrave; con nắm bắt được lịch sản xuất m&ugrave;a vụ nu&ocirc;i trồng thủy sản năm 2019 của Sở N&ocirc;ng nghiệp v&agrave; PTNT Ninh Thuận, để b&agrave; con c&oacute; kế hoạch sản xuất, nu&ocirc;i trồng thủy sản đảm bảo hiệu quả, giảm thi&ecirc;n tai, dịch bệnh, hạn chế rủi ro. Tại hội thảo, b&agrave; con cũng được hướng dẫn kỹ thuật nu&ocirc;i t&ocirc;m thương phẩm hai giai đoạn, bao gồm:</p>\r\n\r\n<p>Giai đoạn 1- giai đoạn ương t&ocirc;m giống: Cỡ t&ocirc;m ương từ post 10 &ndash; 12, mật độ ương từ 1.000 &ndash; 3.000 con/m<sup>2</sup>; sau thời gian ương từ 25 &ndash; 30 ng&agrave;y, t&ocirc;m đạt k&iacute;ch cỡ từ 1.000 &ndash; 2.000 con/kg th&igrave; tiến h&agrave;nh san t&ocirc;m nu&ocirc;i theo giai đoạn 2.</p>\r\n\r\n<p>Giai đoạn 2- giai đoạn nu&ocirc;i t&ocirc;m thương phẩm: Cỡ t&ocirc;m nu&ocirc;i từ 1.000 &ndash; 2.000 con/kg, mật độ nu&ocirc;i từ 100 &ndash; 300 con/m<sup>2</sup>; sau thời gian nu&ocirc;i từ 60 &ndash; 80 ng&agrave;y, t&ocirc;m đạt k&iacute;ch cỡ từ 40 &ndash; 60 con/kg th&igrave; tiến h&agrave;nh thu hoạch. Năng suất nu&ocirc;i của quy tr&igrave;nh đạt từ 30 &ndash; 40 tấn/ha.</p>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh nu&ocirc;i cần tu&acirc;n thủ đ&uacute;ng quy tr&igrave;nh kỹ thuật: Nước nu&ocirc;i t&ocirc;m cần phải được xử l&yacute; qua ao lắng, cần c&oacute; ao dự trữ nước để cung cấp kịp thời cho ao nu&ocirc;i, c&oacute; mương dẫn nước, c&oacute; khu chứa nước thải, chuẩn bị đủ hệ thống quạt nước, sục kh&iacute; đ&aacute;y, ao được l&oacute;t bạt đ&aacute;y v&agrave; bạt bờ, độ s&acirc;u đ&aacute;y ao từ 1,5 &ndash; 2 m. &nbsp;&nbsp;</p>\r\n\r\n<p>Th&ocirc;ng qua Hội thảo đ&atilde; gi&uacute;p cho đa số b&agrave; con nắm bắt lịch sản xuất m&ugrave;a vụ nu&ocirc;i trồng thủy sản, quy tr&igrave;nh nu&ocirc;i t&ocirc;m thương phẩm 2 giai đoạn v&agrave; hướng cho b&agrave; con c&oacute; c&aacute;i nh&igrave;n mới về nu&ocirc;i t&ocirc;m c&ocirc;ng nghệ cao, g&oacute;p phần tăng thu nhập cho n&ocirc;ng d&acirc;n trong nghề nu&ocirc;i t&ocirc;m thương phẩm.</p>\r\n', 3, 8, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (10, '', 'Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng', 'Trong mấy ngày qua, thời tiết nắng nóng oi bức đã ảnh hưởng không nhỏ đến đời sống sinh hoạt của người dân trên địa bàn tỉnh Khánh Hòa. Trước tình hình trên, nhiều nông dân phải chọn phương pháp mang dù ra đồng.', '<p>Theo người d&acirc;n địa phương, mấy ng&agrave;y nay c&aacute;i nắng r&aacute;t da, r&aacute;t mặt diễn ra tr&ecirc;n khắp địa b&agrave;n tỉnh Kh&aacute;nh H&ograve;a. Hơi n&oacute;ng hầm hập oi bức kh&oacute; chịu, khiến ai cũng bịt k&iacute;n mặt trước khi ra đồng. C&agrave;ng về trưa, nhiệt độ c&agrave;ng tăng cao l&agrave;m cho nhiều n&ocirc;ng d&acirc;n tr&ecirc;n đồng đ&ocirc;ng đ&uacute;c ng&agrave;y n&agrave;o bỗng trở n&ecirc;n vắng b&oacute;ng kh&aacute;c thường.</p>\r\n\r\n<p><img alt=\"khanh hoa: nang rat da, cay chet chay, nong dan mang o ra dong hinh anh 1\" src=\"http://streaming1.danviet.vn/upload/2-2019/images/2019-06-15/Khanh-Hoa-Nang-rat-da-rat-mat-nong-dan-mang-du-ra-dong-lam-viec----nh-4-n---ng-ch---n-1560590268-width640height480.jpg\" style=\"width:100%\" title=\"Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng hình ảnh 1\" /></p>\r\n\r\n<p>Thời tiết nắng n&oacute;ng l&agrave;m cho c&acirc;y cỏ tr&ecirc;n đồng h&eacute;o kh&ocirc;</p>\r\n\r\n<p>Chị Phạm Thị Mỹ Lệ (x&atilde; Cam Phước T&acirc;y, huyện Cam L&acirc;m, Kh&aacute;nh H&ograve;a) ngao ng&aacute;n kh&ocirc;ng giấu được nổi vẻ mệt mỏi cho biết: &ldquo;Mấy ng&agrave;y nay, nắng n&oacute;ng khiếp lắm v&agrave; rất kh&oacute; chịu. Ngay cả ban đ&ecirc;m t&ocirc;i c&ugrave;ng gia đ&igrave;nh nằm ngủ kh&ocirc;ng được. Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, t&ocirc;i c&ugrave;ng lao động ở đ&acirc;y che tấm bạt để che nắng thu hoạch dưa. Do nắng n&oacute;ng, n&ecirc;n t&ocirc;i phải dậy từ 5 giờ 30 s&aacute;ng để ra đồng thu hoạch dưa. Chưa bao giờ t&ocirc;i thấy nắng khủng như vậy&rdquo;.</p>\r\n\r\n<p><img alt=\"khanh hoa: nang rat da, cay chet chay, nong dan mang o ra dong hinh anh 2\" src=\"http://streaming1.danviet.vn/upload/2-2019/images/2019-06-15/Khanh-Hoa-Nang-rat-da-rat-mat-nong-dan-mang-du-ra-dong-lam-viec----nh-7-n---ng-ch---n-1560590325-width640height480.jpg\" style=\"width:100%\" title=\"Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng hình ảnh 2\" /></p>\r\n\r\n<p>C&aacute;c n&ocirc;ng n&ocirc;ng phải che k&iacute;n mặt để l&agrave;m việc</p>\r\n\r\n<p>B&agrave; Phạm Thị Kim Loan (x&atilde; Cam An Nam, Cam L&acirc;m) cho biết th&ecirc;m: &ldquo;Những ng&agrave;y n&agrave;y n&ocirc;ng d&acirc;n như ch&uacute;ng t&ocirc;i rất vả, kh&ocirc;ng d&aacute;m ra đồng v&agrave; một số lao động ra đồng sớm hơn so với thường lệ v&agrave; cũng rời c&aacute;nh đồng rất sớm. Mọi năm, gia đ&igrave;nh t&ocirc;i trồng 1ha diện t&iacute;ch khoai m&igrave; v&agrave; hoa c&uacute;c. Tuy nhi&ecirc;n, năm nay t&ocirc;i chỉ trồng 1,5 s&agrave;o m&igrave;, c&aacute;c c&acirc;y l&ecirc;n cũng kh&ocirc;ng nổi. Ri&ecirc;ng hoa c&uacute;c, chỉ l&agrave;m một &iacute;t để cung cấp cho kh&aacute;ch h&agrave;ng. Mấy ng&agrave;y nay, thời tiết chỉ mưa v&agrave;i giọt, kh&ocirc;ng đ&aacute;ng kể. Để tr&aacute;nh nắng n&oacute;ng trong giai đoạn cao điểm n&agrave;y, t&ocirc;i phải d&ugrave;ng c&aacute;i d&ugrave; để che m&aacute;t&rdquo;.</p>\r\n\r\n<p><img alt=\"khanh hoa: nang rat da, cay chet chay, nong dan mang o ra dong hinh anh 3\" src=\"http://streaming1.danviet.vn/upload/2-2019/images/2019-06-15/Khanh-Hoa-Nang-rat-da-rat-mat-nong-dan-mang-du-ra-dong-lam-viec----nh-1-n---ng-ch---n-1560590382-width720height540.jpg\" style=\"width:100%\" title=\"Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng hình ảnh 3\" /></p>\r\n\r\n<p>B&agrave; Phạm Thị Kim Loan (x&atilde; Cam An Nam, Cam L&acirc;m, Kh&aacute;nh H&ograve;a) d&ugrave;ng d&ugrave; để che nắng</p>\r\n\r\n<p>Ghi nhận của PV D&acirc;n Việt, v&agrave;o khoảng 8h 30 s&aacute;ng, nhiều n&ocirc;ng d&acirc;n đ&atilde; rời c&aacute;nh đồng trở về nh&agrave;. Tr&ecirc;n c&aacute;nh đồng x&atilde; Cam An Bắc, Cam An Nam, Cam Phước T&acirc;y (Cam L&acirc;m) v&agrave; Cam Th&agrave;nh Nam (Cam Ranh) rất thưa thớt người l&agrave;m việc. Những c&acirc;y cỏ tr&ecirc;n đồng bị h&eacute;o &uacute;a, c&acirc;y m&iacute;a, c&acirc;y m&igrave; c&ograve;i cọc, k&eacute;m ph&aacute;t triển. Những người phụ nữ bịt k&iacute;n mặt v&igrave; sợ nắng.</p>\r\n\r\n<p><img alt=\"khanh hoa: nang rat da, cay chet chay, nong dan mang o ra dong hinh anh 4\" src=\"http://streaming1.danviet.vn/upload/2-2019/images/2019-06-15/Khanh-Hoa-Nang-rat-da-rat-mat-nong-dan-mang-du-ra-dong-lam-viec----nh-5-n---ng-ch---n-1560590471-width600height450.jpg\" style=\"width:100%\" title=\"Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng hình ảnh 4\" /></p>\r\n\r\n<p>C&acirc;y m&iacute;a ng&atilde; v&agrave;ng, k&eacute;m ph&aacute;t triển do nắng n&oacute;ng</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"khanh hoa: nang rat da, cay chet chay, nong dan mang o ra dong hinh anh 5\" src=\"http://streaming1.danviet.vn/upload/2-2019/images/2019-06-15/Khanh-Hoa-Nang-rat-da-rat-mat-nong-dan-mang-du-ra-dong-lam-viec----nh-2-n---ng-ch---n-1560590567-width640height480.jpg\" style=\"width:100%\" title=\"Khánh Hòa: Nắng rát da, cây chết cháy, nông dân mang ô ra đồng hình ảnh 5\" /></p>\r\n\r\n<p>Tr&ecirc;n c&aacute;nh đồng vắng b&oacute;ng n&ocirc;ng d&acirc;n l&agrave;m việc, do ảnh hưởng nắng n&oacute;ng</p>\r\n', 3, 10, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (11, 'lua.jpg', 'Trung Quốc tăng nhập nông sản Việt', 'Một trong những mặt hàng đang được các doanh nghiệp nước này quan tâm là gạo. Nhiều doanh nghiệp Trung Quốc gần đây tham gia tìm hiểu thị trường ở vựa lúa ĐBSCL và ký hợp đồng ghi nhớ.', '<p>Theo Thương vụ Đại sứ Qu&aacute;n Việt Nam tại Trung Quốc: Nh&oacute;m h&agrave;ng n&ocirc;ng, l&acirc;m, thủy sản được hưởng ưu đ&atilde;i thuế quan nhập khẩu trong khu&ocirc;n khổ Hiệp định thương mại tự do ASEAN - Trung Quốc (ACFTA) với mức thuế cơ bản l&agrave; 0% n&ecirc;n c&oacute; nhiều thuận lợi v&agrave;o nước n&agrave;y. Trung Quốc cũng đang tăng cường, khuyến kh&iacute;ch nhập khẩu h&agrave;ng h&oacute;a từ nước ngo&agrave;i v&agrave; c&oacute; nhu cầu lớn đối với c&aacute;c sản phẩm xuất khẩu m&agrave; Việt Nam c&oacute; thế mạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; vậy, h&agrave;ng h&oacute;a của Việt Nam sẽ phải cạnh tranh gay gắt với c&aacute;c sản phẩm c&ugrave;ng chủng loại từ c&aacute;c nước trong khu vực ASEAN. Tuy nhi&ecirc;n, để x&acirc;m nhập thị trường n&agrave;y vẫn c&ograve;n nhiều vấn đề m&agrave; doanh nghiệp Việt cần quan t&acirc;m như: ch&iacute;nh s&aacute;ch xuất nhập khẩu, nhu cầu thị trường, hệ thống thương nh&acirc;n để kết nối v&agrave; giao dịch. C&ugrave;ng với đ&oacute;, một số doanh nghiệp chưa c&oacute; những người am hiểu tiếng Trung để phục vụ li&ecirc;n hệ, kết nối với c&aacute;c doanh nghiệp Trung Quốc...</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thực tế, nhu cầu của Trung Quốc với n&ocirc;ng sản Việt l&agrave; rất lớn. Trước nay, nước n&agrave;y vẫn nhập rất nhiều tr&aacute;i c&acirc;y, rau củ, gạo... từ Việt Nam. Gần đ&acirc;y Trung Quốc mở cửa th&ecirc;m cho tr&aacute;i măng cụt của Việt Nam, n&acirc;ng tổng số tr&aacute;i c&acirc;y l&ecirc;n 9 loại v&agrave; đang tiếp tục xem x&eacute;t th&ecirc;m một số loại kh&aacute;c. Ngo&agrave;i ra, đo&agrave;n thương nh&acirc;n ng&agrave;nh gạo của nước n&agrave;y cũng đến ĐBSCL để t&igrave;m hiểu thị trường gạo. Trong cuộc l&agrave;m việc tr&ecirc;n, c&aacute;c doanh nghiệp Việt Nam đ&atilde; k&yacute; bi&ecirc;n bản ghi nhớ với c&aacute;c doanh nghiệp Trung Quốc với tổng lượng gạo l&agrave; 84.000 tấn/năm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c thương nh&acirc;n Trung Quốc khuyến nghị, doanh nghiệp Việt Nam n&ecirc;n xuất khẩu gạo đ&oacute;ng g&oacute;i dạng nhỏ, vừa, h&uacute;t ch&acirc;n kh&ocirc;ng, khoảng 5 - 25 kg; điều n&agrave;y sẽ ph&ugrave; hợp hơn với nhu cầu của người ti&ecirc;u d&ugrave;ng nước sở tại. Trung Quốc cũng tăng cường kiểm so&aacute;t chặt chẽ c&ocirc;ng t&aacute;c đảm bảo vệ sinh an to&agrave;n thực phẩm đối với mặt h&agrave;ng gạo, do vậy, c&aacute;c doanh nghiệp kinh doanh xuất khẩu gạo của Việt Nam cần tu&acirc;n thủ nghi&ecirc;m t&uacute;c c&aacute;c quy tr&igrave;nh, quy định về kiểm dịch, x&ocirc;ng hơi khử tr&ugrave;ng, quy c&aacute;ch bao b&igrave;, đ&oacute;ng g&oacute;i, tr&aacute;nh sai s&oacute;t, rủi ro c&oacute; thể ph&aacute;t sinh khi xuất khẩu gạo sang thị trường n&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Từ đầu năm đến nay, xuất khẩu gạo của Việt Nam gặp nhiều kh&oacute; khăn do ảnh hưởng xu hướng chung của thị trường thế giới. Ch&iacute;nh v&igrave; vậy việc tăng cường giao thương với kh&aacute;ch h&agrave;ng lớn nhất thế giới l&agrave; th&ocirc;ng tin t&iacute;ch cực với ng&agrave;nh l&uacute;a gạo Việt Nam.</p>\r\n', 1, 4, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (12, 'ca-phe (1).jpg', 'Giá cà phê thấp, nông dân Brazil vẫn ồ ạt mở rộng trang trại', 'Giá cà phê trên thị trường thế giới đang rơi về mức thấp nhất trong hơn 10 năm qua nhưng tại Brazil, nước sản xuất cà phê số một thế giới, các nông dân vẫn không lỗ và đang đẩy mạnh mở rộng diện tích trồng cà phê để gia tăng lợi nhuận.', '<p>Năm 2020, Brazil dự kiến sẽ đạt sản lượng c&agrave; ph&ecirc; kỷ lục v&agrave; viễn cảnh n&agrave;y khiến gi&aacute; c&agrave; ph&ecirc; kh&ocirc;ng thể &quot;ng&oacute;c dậy&quot;, đe dọa đời sống của h&agrave;ng triệu n&ocirc;ng d&acirc;n c&agrave; ph&ecirc; ở c&aacute;c nước kh&aacute;c.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trang trại c&agrave; ph&ecirc; của &ocirc;ng Rodrigo de Freitas Silva ở bang Sao Paulo, Brazil ph&aacute;t triển tr&ugrave; ph&uacute; d&ugrave; thị trường c&agrave; ph&ecirc; thế giới đang trải qua một trong những đợt sụt gi&aacute; tồi tệ nhất trong nhiều thập kỷ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong hơn 12 năm qua, n&ocirc;ng d&acirc;n 41 tuổi n&agrave;y đ&atilde; mở rộng diện t&iacute;ch trồng c&agrave; ph&ecirc; từ 12,5 ha l&ecirc;n 220 ha v&agrave; năng suất tr&ecirc;n c&aacute;c diện t&iacute;ch n&agrave;y cũng tăng gấp đ&ocirc;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>To&agrave;n bộ trang trại của &ocirc;ng được cơ giới h&oacute;a v&agrave; 90% trang trại c&oacute; hệ thống tưới ti&ecirc;u. Điều quan trọng nhất l&agrave; ngay cả khi gi&aacute; c&agrave; ph&ecirc; to&agrave;n cầu đang giao dịch ở mức thấp nhất trong 13 năm qua, Silva vẫn c&oacute; l&atilde;i v&agrave; đang c&oacute; kế hoạch mở rộng diện t&iacute;ch trồng v&agrave; n&acirc;ng cao năng suất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;Chỉ dựa v&agrave;o những trang trại m&agrave; t&ocirc;i đang c&oacute;, t&ocirc;i vẫn c&ograve;n đủ khả năng tăng gấp ba diện t&iacute;ch trồng c&agrave; ph&ecirc;&rdquo;,</em>&nbsp;Silva n&oacute;i khi chỉ tay ra một khoảng đất trống m&ecirc;nh m&ocirc;ng, nơi sẽ đặt một ph&ograve;ng th&iacute; nghiệm để ph&acirc;n loại c&agrave; ph&ecirc; ở trang trại của &ocirc;ng ở th&agrave;nh phố Jeriquara, bang Sao Paulo.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cơn b&ugrave;ng nổ c&agrave; ph&ecirc; của Brazil đang đặt ra những th&aacute;ch thức lớn cho những n&ocirc;ng d&acirc;n trồng c&agrave; ph&ecirc; ở mọi quốc gia tr&ecirc;n tr&aacute;i đất. Nhiều n&ocirc;ng d&acirc;n c&agrave; ph&ecirc; từ Nicaragua ở Mỹ Latin cho đến Tanzania ở ch&acirc;u Phi phải sản xuất sản lượng &iacute;t hơn tr&ecirc;n mỗi hecta, tốn nhiều chi ph&iacute; ph&acirc;n b&oacute;n v&agrave; nh&acirc;n c&ocirc;ng hơn, đặc biệt tỉ gi&aacute; của đồng nội tệ ở đất nước họ kh&ocirc;ng c&oacute; lợi thế giống như tỉ gi&aacute; của đồng real (Brazil).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đồng real giảm gi&aacute;, cho ph&eacute;p c&aacute;c nh&agrave; xuất khẩu c&agrave; ph&ecirc; của Brazil kiếm được nhiều hơn v&agrave; đ&oacute; l&agrave; một động lực để c&aacute;c n&ocirc;ng d&acirc;n ở đ&acirc;y mở rộng diện t&iacute;ch trồng c&agrave; ph&ecirc;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tr&aacute;i lại, những n&ocirc;ng d&acirc;n trồng c&agrave; ph&ecirc; ở nước kh&aacute;c đang lo sốt v&oacute; khi gi&aacute; c&agrave; ph&ecirc; tương lai của s&agrave;n giao dịch ICE tại New York (Mỹ) đ&atilde; rớt về mức chưa đủ để b&ugrave; đắp chi ph&iacute; sản xuất của họ. Thua lỗ k&eacute;o d&agrave;i cộng với việc bị hạn chế tiếp cận t&iacute;n dụng đang khiến một số n&ocirc;ng d&acirc;n c&agrave; ph&ecirc; từ Trung Mỹ cho đến ch&acirc;u Phi bỏ b&ecirc; trang trại của họ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Những dấu hiệu căng thẳng như vậy kh&ocirc;ng hề xuất hiện tại c&aacute;c trang trại c&agrave; ph&ecirc; nằm dọc theo một dải đất k&eacute;o d&agrave;i hơn 1.700 km ở v&ugrave;ng Mogiana ở Sao Paulo cho đến c&aacute;c v&ugrave;ng miền Nam v&agrave; v&ugrave;ng Cerrado ở bang Minas Gerais, những nơi đang cung cấp khoảng 70% sản lượng c&agrave; ph&ecirc; arabica của Brazil.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Khắp mọi nơi, dọc theo c&aacute;c con đường dẫn l&ecirc;n c&aacute;c đỉnh n&uacute;i, những trang trại c&agrave; ph&ecirc; trưởng th&agrave;nh hay &ldquo;một biển&rdquo; c&acirc;y c&agrave; ph&ecirc; con bạt ng&agrave;n trước tầm mắt. Đi giữa những trang trại c&agrave; ph&ecirc; n&agrave;y, những &ocirc;ng chủ n&ocirc;ng d&acirc;n kh&ocirc;ng thể giấu được niềm tự h&agrave;o.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;T&ocirc;i đ&atilde; mở rộng diện t&iacute;ch trồng c&agrave; ph&ecirc; v&agrave; trồng lại những diện t&iacute;ch c&agrave; ph&ecirc; gi&agrave; nua để tăng năng suất l&ecirc;n gấp đ&ocirc;i&rdquo;</em>, Silva n&oacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng kh&ocirc;ng c&oacute; &yacute; định dừng đầu tư d&ugrave; gi&aacute; c&agrave; ph&ecirc; tr&ecirc;n thị trường quốc tế đang thấp. &Ocirc;ng khẳng định: &ldquo;<em>T&ocirc;i vẫn kiếm được lợi nhuận với c&agrave; ph&ecirc;&rdquo;.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Silva kỳ vọng sẽ thu được 10.000 bao c&agrave; ph&ecirc; arabica trong năm 2020, tức gấp ba lần sản lượng c&agrave; ph&ecirc; m&agrave; &ocirc;ng thu hoạch trong năm nay, vốn l&agrave; năm sản lượng thấp trong chu kỳ hai năm. Mỗi bao c&agrave; ph&ecirc; ở Brazil nặng 60kg. Silva đ&atilde; t&aacute;i đầu tư lợi nhuận để mở rộng diện t&iacute;ch, cơ giới h&oacute;a, lắp đặt hệ thống tưới ti&ecirc;u v&agrave; tăng cường sử dụng ph&acirc;n b&oacute;n ở trang trại của &ocirc;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Năm 2016, năng suất ở một phần diện t&iacute;ch tại trang trại của &ocirc;ng đạt mức đỉnh 136 bao/hecta trong khi đ&oacute;, năng suất trung b&igrave;nh to&agrave;n trang trại đạt khoảng 50 bao/hecta.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://1.bp.blogspot.com/-bi7ENwcT8n8/XP8Y76fEQ2I/AAAAAAACmCY/7ww2_0IO1hgYh_HfU-ZxevDRyp3D9B7AwCLcBGAs/s1600/ca-phe%2B1.jpg\"><img src=\"https://1.bp.blogspot.com/-bi7ENwcT8n8/XP8Y76fEQ2I/AAAAAAACmCY/7ww2_0IO1hgYh_HfU-ZxevDRyp3D9B7AwCLcBGAs/s400/ca-phe%2B1.jpg\" style=\"height:266px; width:400px\" /></a></p>\r\n\r\n<p><em>Một hồ nước tưới khổng lồ nằm lọt thỏm giữa c&aacute;c c&aacute;nh đồng c&agrave; ph&ecirc; bạt ng&agrave;n tr&ecirc;n một thảo nguy&ecirc;n ở Minas Gerais, Brazil. Ảnh: Bloomberg</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trừ phi gặp phải vấn đề thời tiết khắc nghiệt xảy ra, ước t&iacute;nh v&agrave;o năm 2020, quốc gia Nam Mỹ n&agrave;y c&oacute; thể thu hoạch vụ m&ugrave;a c&agrave; ph&ecirc; kỷ lục với khoảng 70 triệu bao, theo Marcos Figueiredo, một n&ocirc;ng d&acirc;n c&agrave; ph&ecirc; v&agrave; đang quản l&yacute; c&aacute;c nh&agrave; kho ở th&agrave;nh phố Araguari, bang Minas Gerais. &Ocirc;ng cho biết:&nbsp;<em>&ldquo;C&aacute;c trang trại c&agrave; ph&ecirc; chưa bao giờ được chăm s&oacute;c tốt như vậy&rdquo;.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Rodrigo Costa, Gi&aacute;m đốc C&ocirc;ng ty xuất khẩu c&agrave; ph&ecirc; Comexim (Brazil), cho biết nguồn cung dồi d&agrave;o của Brazil giải th&iacute;ch tại sao c&aacute;c hợp đồng c&agrave; ph&ecirc; tương lai tr&ecirc;n thị trường thế giới kh&ocirc;ng thể trụ vững ở những mức gi&aacute; cao. &Ocirc;ng cho biết ngo&agrave;i Brazil th&igrave; Việt Nam v&agrave; Colombia cũng đầu tư để tăng sản lượng c&agrave; ph&ecirc; trong những năm gần đ&acirc;y, c&agrave;ng l&agrave;m dư thừa nguồn cung to&agrave;n cầu.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Hầu hết c&aacute;c hợp đồng c&agrave; ph&ecirc; tương lai tr&ecirc;n thị trường thế giới đang giao dịch gần c&aacute;c mức thấp nhất kể từ năm 2005. Điều n&agrave;y dường như kh&ocirc;ng l&agrave;m nản ch&iacute; Nairo Teixeira Dias, một n&ocirc;ng d&acirc;n trồng c&agrave; ph&ecirc; 66 tuổi ở Machado, bang Minas Gerais. Hầu hết diện t&iacute;ch trồng c&agrave; ph&ecirc; của &ocirc;ng nằm tr&ecirc;n c&aacute;c sườn n&uacute;i, nơi việc h&aacute;i c&agrave; ph&ecirc; kh&ocirc;ng thể thực hiện được bằng m&aacute;y m&oacute;c v&agrave; chi ph&iacute; thu hoạch cao gấp ba lần so với c&aacute;c trang trại c&agrave; ph&ecirc; được cơ giới h&oacute;a. Tuy vậy, trang trại c&agrave; ph&ecirc; 150 hecta của Dias vẫn kh&ocirc;ng thua lỗ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;Dĩ nhi&ecirc;n, bi&ecirc;n lợi nhuận bị thu hẹp nhưng c&acirc;y c&agrave; ph&ecirc; vẫn cho thu nhập tốt hơn c&aacute;c c&acirc;y trồng kh&aacute;c&rdquo;</em>, &ocirc;ng n&oacute;i. Tr&ecirc;n v&ugrave;ng thảo nguy&ecirc;n của bang Minas Gerais, anh Noe Francisco Bartolomeu Rodrigues, 37 tuổi, vẫn mở rộng diện t&iacute;ch trồng c&agrave; ph&ecirc; để cắt giảm chi ph&iacute;, cải thiện sản lượng v&agrave; n&acirc;ng cao chất lượng tr&ecirc;n c&aacute;c c&aacute;nh đồng c&agrave; ph&ecirc; rộng 2.070 hecta được cơ giới h&oacute;a ho&agrave;n to&agrave;n v&agrave; được tưới ti&ecirc;u bởi hợp t&aacute;c x&atilde; sản xuất n&ocirc;ng nghiệp Farroupilha m&agrave; &nbsp;Rodrigues l&agrave; một th&agrave;nh vi&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vụ thu hoạch c&agrave; ph&ecirc; của Farroupilha v&agrave;o năm 2020 dự kiến sẽ tăng mạnh, một phần nhờ c&aacute;c khu vực mới trồng bắt đầu cho quả. Hợp t&aacute;c x&atilde; n&agrave;y l&ecirc;n kế hoạch trồng lại 700 hecta c&agrave; ph&ecirc; trong bốn năm tới.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>&ldquo;Một số diện t&iacute;ch trồng cũ kh&ocirc;ng được tưới ti&ecirc;u chỉ đạt năng suất 25 bao/hecta, đ&atilde; được thay thế bằng những diện t&iacute;ch mới với năng suất gấp đ&ocirc;i&rdquo;</em>, Rodrigues cho biết.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Givago Miranda, một n&ocirc;ng d&acirc;n sở hữu trang trại c&agrave; ph&ecirc; 340 hecta ở Tres Pontas, bang Minas Gerais, cho biết sang năm 2020, &ocirc;ng sẽ thay thế c&aacute;c diện t&iacute;ch c&agrave; ph&ecirc; cũ bằng những c&acirc;y c&agrave; ph&ecirc; mới để duy tr&igrave; mức năng suất cao. &Ocirc;ng cho biết:&nbsp;<em>&ldquo;Ch&uacute;ng t&ocirc;i sẽ t&igrave;m c&aacute;ch sản xuất c&agrave; ph&ecirc; với chi ph&iacute; ng&agrave;y c&agrave;ng thấp&rdquo;.</em></p>\r\n', 4, 1, 'CDD', 0, 'normal', '2019-06-16', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (13, 'xkg.jpg', 'Xuất khẩu nông sản giảm trong 2 tháng đầu năm', 'Mặt hàng gạo lại gặp khó khi giá tại khu vực Đồng bằng sông Cửu Long giảm trong bối cảnh thu hoạch vụ Đông Xuân đang vào vụ rộ...', '<p><strong>Trong 2 th&aacute;ng đầu năm 2019, xuất khẩu thủy sản v&agrave; l&acirc;m sản vẫn tăng trưởng kh&aacute;. Thế nhưng, nh&oacute;m c&aacute;c mặt h&agrave;ng n&ocirc;ng sản (l&uacute;a gạo, c&agrave; ph&ecirc;, cao su, hồ ti&ecirc;u...) lại giảm 10,1% về gi&aacute; trị kim ngạch. Điều n&agrave;y đ&atilde; k&eacute;o tụt kết quả xuất khẩu của to&agrave;n ng&agrave;nh n&ocirc;ng l&acirc;m thủy sản, tổng kim ngạch 2 th&aacute;ng đầu năm giảm 1,6% so với c&ugrave;ng kỳ năm 2018, chỉ đạt 5,5 tỷ USD.</strong></p>\r\n\r\n<p>Ng&agrave;y 1/3/2019, Cục Chế biến v&agrave; ph&aacute;t triển thị trường n&ocirc;ng sản (Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n) phối hợp c&ugrave;ng Viện Ch&iacute;nh s&aacute;ch v&agrave; chiến lược ph&aacute;t triển n&ocirc;ng nghiệp, n&ocirc;ng th&ocirc;n (Ipsard) tổ chức họp b&aacute;o về kết quả xuất khẩu n&ocirc;ng l&acirc;m thủy sản th&aacute;ng 2, dự b&aacute;o thị trường th&aacute;ng 3/2019.</p>\r\n\r\n<p><strong>Thu hoạch rộ, gi&aacute; l&uacute;a giảm</strong></p>\r\n\r\n<p>&Ocirc;ng Nguyễn Quốc Toản, quyền Cục trưởng Cục Chế biến v&agrave; ph&aacute;t triển thị trường n&ocirc;ng sản cho biết, kim ngạch xuất khẩu n&ocirc;ng l&acirc;m thủy sản th&aacute;ng 2/2019 ước đạt 1,93 tỷ USD, đưa tổng gi&aacute; trị xuất khẩu to&agrave;n ng&agrave;nh 2 th&aacute;ng đầu năm 2019 đạt 5,5 tỷ USD, giảm 1,6% so với c&ugrave;ng kỳ năm 2018.&nbsp;</p>\r\n\r\n<p>Trong đ&oacute;, gi&aacute; trị xuất khẩu c&aacute;c mặt h&agrave;ng n&ocirc;ng sản ch&iacute;nh ước đạt 2,71 tỷ USD, giảm 10,1%; gi&aacute; trị xuất khẩu thủy sản ước đạt 1,11 tỷ USD, tăng 4,4%; gi&aacute; trị xuất khẩu chăn nu&ocirc;i ước đạt 77 triệu USD, giảm 7%; gi&aacute; trị xuất khẩu c&aacute;c mặt h&agrave;ng l&acirc;m sản ch&iacute;nh ước đạt 1,5 tỷ USD, tăng 12,8%.&nbsp;</p>\r\n\r\n<p>Ở chiều ngược lại, gi&aacute; trị nhập khẩu n&ocirc;ng, l&acirc;m, thủy sản th&aacute;ng 2/2019 ước đạt 1,8 tỷ USD, đưa tổng gi&aacute; trị nhập khẩu 2 th&aacute;ng đầu năm đạt 4,59 tỷ USD, tăng 3% so với c&ugrave;ng kỳ năm 2018.</p>\r\n\r\n<p>Đối với mặt h&agrave;ng gạo, th&aacute;ng 2/2019 xuất khẩu ước đạt 399.000 tấn với gi&aacute; trị 169 triệu USD. Lũy kế 2 th&aacute;ng đầu năm 2019, xuất khẩu gạo đạt 837.000 tấn v&agrave; 364 triệu USD, tăng 1,1% về khối lượng nhưng giảm tới 10,4% về gi&aacute; trị so với c&ugrave;ng kỳ năm 2018.&nbsp;</p>\r\n\r\n<p>Về thị trường xuất khẩu, Philippines đứng vị tr&iacute; thứ nhất với 46,7% thị phần, tăng 53,8% về khối lượng v&agrave; tăng 41,6% về gi&aacute; trị so với c&ugrave;ng kỳ năm 2018. C&aacute;c thị trường c&oacute; gi&aacute; trị xuất khẩu gạo tăng mạnh l&agrave; Hồng K&ocirc;ng (gấp 3,3 lần), &Uacute;c (gấp 2,4 lần), Tiểu vương quốc Ả Rập Thống nhất (tăng 85,5%), Gana (tăng 47,9%) v&agrave; Philippines (tăng 41,6%). Về chủng loại gạo, gi&aacute; trị xuất khẩu gạo trắng chiếm 64% tổng kim ngạch; gạo Jasmine v&agrave; gạo thơm chiếm 23%; gạo nếp chiếm 9% v&agrave; gạo Japonica, gạo giống Nhật chiếm 4% trong tổng gi&aacute; trị kim ngạch.&nbsp;</p>\r\n\r\n<p>C&aacute;c thị trường xuất khẩu gạo trắng lớn nhất của Việt Nam l&agrave; Philippines (chiếm 68%), Cuba (chiếm 20%). Với gạo Jasmine v&agrave; gạo thơm, thị trường xuất khẩu lớn nhất gồm Bờ Biển Ng&agrave; (chiếm 21%), Ghana (chiếm 14%) v&agrave; Malaysia (chiếm 11%).</p>\r\n\r\n<p>Điều đ&aacute;ng lo ngại l&agrave;, tại thị trường trong nước, gi&aacute; l&uacute;a gạo tại khu vực Đồng bằng s&ocirc;ng Cửu Long giảm trong bối cảnh thu hoạch vụ Đ&ocirc;ng Xu&acirc;n đang v&agrave;o vụ rộ.&nbsp;</p>\r\n\r\n<p>Tại Ki&ecirc;n Giang, l&uacute;a IR50404 giảm từ 6.000 đồng/kg xuống c&ograve;n 5.400 đồng/kg; l&uacute;a OM 4218 giảm 300 đồng/kg; l&uacute;a OM 6976 giảm 300 đồng/kg; l&uacute;a Jasmine giảm 400 đồng/kg.&nbsp;</p>\r\n\r\n<p>Những diễn biến tr&ecirc;n tr&aacute;i ngược với dự b&aacute;o của Cục Chế biến v&agrave; Ph&aacute;t triển thị trường n&ocirc;ng sản đưa ra v&agrave;o th&aacute;ng đầu năm, rằng: Xuất khẩu gạo sẽ khởi sắc trong năm 2019.&nbsp;</p>\r\n\r\n<p>Khi ph&oacute;ng vi&ecirc;n thắc mắc điều n&agrave;y, &ocirc;ng Nguyễn Quốc Toản cho hay: &quot;Về dự b&aacute;o gi&aacute; gạo, th&ocirc;ng tin dự b&aacute;o đưa ra l&agrave; tại thời điểm đ&oacute; khi ch&uacute;ng ta tận dụng c&aacute;c lợi thế của ng&agrave;nh l&uacute;a gạo. Nhưng thị trường diễn biến kh&ocirc;n lường, n&ecirc;n việc dự b&aacute;o chưa tr&uacute;ng v&agrave; đ&uacute;ng&quot;.</p>\r\n\r\n<p><strong>Ch&iacute;nh phủ v&agrave;o cuộc giải cứu gi&aacute;</strong></p>\r\n\r\n<p>Theo &ocirc;ng Toản, Nhờ sự chỉ đạo quyết liệt của Thủ tướng Ch&iacute;nh phủ, sự v&agrave;o cuộc t&iacute;ch cực của c&aacute;c bộ, gi&aacute; l&uacute;a gạo ở Đồng bằng s&ocirc;ng Cửu Long đ&atilde; ổn định trong 3 ng&agrave;y cuối th&aacute;ng 2, kh&ocirc;ng giảm xuống nữa.&nbsp;</p>\r\n\r\n<p>C&ugrave;ng với đ&oacute;, Ng&acirc;n h&agrave;ng Nh&agrave; nước cũng đ&atilde; chỉ đạo c&aacute;c ng&acirc;n h&agrave;ng phải l&agrave;m việc trực tiếp, nắm bắt c&aacute;c kh&oacute; khăn, vướng mắc của doanh nghiệp để phối hợp, c&oacute; giải ph&aacute;p th&aacute;o gỡ cụ thể như: xem x&eacute;t tăng hạn mức vay vốn, đẩy nhanh tiến độ giải ng&acirc;n, tạo điều kiện để thu mua kịp thời l&uacute;a gạo tại khu vực Đồng bằng s&ocirc;ng Cửu Long trong thời gian tới.&nbsp;</p>\r\n\r\n<p>Trước diễn biến của thị trường xuất khẩu l&uacute;a gạo, Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển N&ocirc;ng th&ocirc;n tiếp tục đề xuất giảm diện t&iacute;ch trồng l&uacute;a kh&ocirc;ng hiệu quả, đặc biệt l&agrave; ở những khu vực nhiễm mặn, v&ugrave;ng thiếu nước sản xuất đồng thời tiếp tục phối hợp với Bộ C&ocirc;ng Thương thực hiện c&aacute;c giải ph&aacute;p nhằm t&igrave;m kiếm thị trường ti&ecirc;u thụ l&uacute;a gạo.</p>\r\n\r\n<p>&Ocirc;ng Nguyễn Trung Ki&ecirc;n, Trưởng bộ m&ocirc;n Nghi&ecirc;n cứu thị trường v&agrave; ng&agrave;nh h&agrave;ng của IPSARD cho hay, trong bức tranh xuất khẩu của n&ocirc;ng sản Việt Nam c&oacute; nhiều mảng m&agrave;u s&aacute;ng tối đan xen. Trong c&aacute;c mặt h&agrave;ng c&acirc;y c&ocirc;ng nghiệp, trừ ch&egrave; l&agrave; c&oacute; sự gia tăng về sản lượng v&agrave; gi&aacute; trị xuất khẩu, c&ograve;n lại hầu hết c&aacute;c mặt h&agrave;ng đều giảm cả lượng v&agrave; gi&aacute; trị như: c&agrave; ph&ecirc;, ti&ecirc;u, điều; c&acirc;y cao su mặc d&ugrave; sản lượng tăng nhưng gi&aacute; tiếp tục giảm.</p>\r\n\r\n<p>Đề cập nguy&ecirc;n nh&acirc;n giảm gi&aacute; c&aacute;c sản phẩm c&acirc;y c&ocirc;ng nghiệp hiện nay, &ocirc;ng Nguyễn Trung Ki&ecirc;n cho rằng, c&oacute; hiện tượng dư cung tr&ecirc;n thị trường quốc tế. Cụ thể, đối với c&acirc;y c&agrave; ph&ecirc;, sau thời điểm gi&aacute; cao trong giai đoạn 2010-2011, đ&atilde; đẩy c&aacute;c nước thực hiện c&aacute;c chương tr&igrave;nh t&aacute;i canh c&acirc;y c&agrave; ph&ecirc;.&nbsp;</p>\r\n\r\n<p>Tại Brazil hay Colombia việc thực hiện chương tr&igrave;nh t&aacute;i canh, tăng năng suất khiến sản lượng của Colombia tăng gấp đ&ocirc;i so với thời điểm năm 2010-2011, tương tự như vậy, c&agrave; ph&ecirc; của Brazil cũng tăng gấp rưỡi so với thời điểm 2010-2011. Ng&agrave;nh h&agrave;ng n&agrave;y được dự b&aacute;o đến giữa năm 2019 gi&aacute; vẫn tiếp tục suy giảm.</p>\r\n\r\n<p>Tương tự như vậy, đối với mặt h&agrave;ng ti&ecirc;u, thời điểm gi&aacute; ti&ecirc;u tăng dẫn đến t&igrave;nh trạng c&aacute;c nước mở rộng diện t&iacute;ch trồng ti&ecirc;u, tăng cung một c&aacute;ch qu&aacute; mạnh. Dẫn đến gi&aacute; ti&ecirc;u hiện nay giảm, dự b&aacute;o con số sẽ giảm xuống 40.000 đồng/kg, gần bằng với gi&aacute; th&agrave;nh.&nbsp;</p>\r\n\r\n<p>Theo dự b&aacute;o của nhiều chuy&ecirc;n gia quốc tế, chu kỳ giảm gi&aacute; c&aacute;c sản phẩm c&acirc;y c&ocirc;ng nghiệp phải hết năm 2019 mới kết th&uacute;c v&agrave; mới c&oacute; thể phục hồi. Do đ&oacute;, giải ph&aacute;p đưa ra trong thời gian tới đ&oacute; l&agrave; vẫn phải duy tr&igrave; bằng sản lượng, chất lượng, bảo vệ năng lực sản xuất trong nước.</p>\r\n', 1, 4, 'CDD', 0, 'premium', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (14, 'gia-heo.jpg', 'Chạy dịch, nhiều hộ dân bán tháo lợn số lượng lớn', 'Giá lợn hơi trong nước giảm do dịch tả lợn châu Phi diễn biến phức tạp và chưa có dấu hiệu dừng lại...', '<p><strong>Do t&acirc;m l&yacute; lo sợ dịch bệnh, nhiều hộ n&ocirc;ng d&acirc;n b&aacute;n lợn chạy dịch số lượng lớn khiến nguồn cung lợn hơi tăng đột ngột v&agrave; gi&aacute; lợn hơi giảm mạnh.</strong></p>\r\n\r\n<p>Trong th&aacute;ng 5/2019, gi&aacute; lợn hơi trong nước giảm do dịch tả lợn ch&acirc;u Phi diễn biến phức tạp. Mặc d&ugrave; nhiều tỉnh mặc d&ugrave; đ&atilde; c&ocirc;ng bố hết dịch nhưng lại bị t&aacute;i b&ugrave;ng ph&aacute;t dịch tả lợn ch&acirc;u Phi trở lại.</p>\r\n\r\n<p>Đặc biệt, do t&acirc;m l&yacute; lo sợ dịch bệnh, nhiều hộ n&ocirc;ng d&acirc;n đ&atilde; b&aacute;n lợn chạy dịch với số lượng lớn khiến nguồn cung lợn hơi tăng đột ngột v&agrave; gi&aacute; lợn hơi giảm mạnh.&nbsp;</p>\r\n\r\n<p>Cụ thể, theo thống k&ecirc; của Bộ N&ocirc;ng nghiệp, tại miền Bắc, gi&aacute; lợn hơi tại Ph&uacute; Thọ, Tuy&ecirc;n Quang, Th&aacute;i B&igrave;nh, Y&ecirc;n B&aacute;i, L&agrave;o Cai đồng loạt giảm 6.000 - 7.000 đ/kg so với th&aacute;ng trước, ở mức 29.000 - 32.000 đồng/kg.&nbsp;</p>\r\n\r\n<p>C&aacute;c địa phương c&ograve;n lại như Hưng Y&ecirc;n, H&agrave; Nội, Th&aacute;i Nguy&ecirc;n, H&agrave; Nam, Vĩnh Ph&uacute;c, Nam Định dao động trong khoảng 28.000 - 31.000 đồng/kg, giảm 5.000 đồng/kg</p>\r\n\r\n<p>Tương tự, tại miền Trung, T&acirc;y Nguy&ecirc;n gi&aacute; lợn hơi cũng giảm. Chẳng hạn, gi&aacute; lợn hơi tại Quảng B&igrave;nh, Thừa Thi&ecirc;n Huế giảm 11.000 đ/kg xuống 29.000 đ/kg. Đắk Lắk cũng giảm 10.000 đ/kg xuống 32.000 đ/kg.&nbsp;</p>\r\n\r\n<p>Tại Thanh H&oacute;a, Nghệ An, H&agrave; Tĩnh, Quảng Trị, B&igrave;nh Định, lợn hơi được thu mua trong khoảng 28.000 - 31.000 đ/kg, giảm 9.000 &ndash; 10.000 đ/kg.&nbsp;</p>\r\n\r\n<p>Tại c&aacute;c tỉnh miền Nam như S&oacute;c Trăng, C&agrave; Mau, gi&aacute; lợn giảm 9.000 &ndash; 11.000 đ/kg xuống 35.000 đ/kg.&nbsp;</p>\r\n\r\n<p>Theo đ&aacute;nh gi&aacute; của cơ quan chức năng, hiện nay dịch tả lợn ch&acirc;u Phi vẫn đang c&oacute; những diễn biến phức tạp v&agrave; chưa c&oacute; dấu hiệu dừng lại.&nbsp;</p>\r\n\r\n<p>Một số tỉnh d&ugrave; đ&atilde; c&ocirc;ng bố hết dịch nhưng sau đ&oacute; dịch bệnh vẫn quay trở lại b&ugrave;ng ph&aacute;t do c&ocirc;ng t&aacute;c kiểm so&aacute;t dịch bệnh vẫn chưa được chặt chẽ, chủ yếu ph&aacute;t sinh tại c&aacute;c hộ chăn nu&ocirc;i nhỏ lẻ do thiếu &yacute; thức ph&ograve;ng chống dịch, kh&ocirc;ng khử tr&ugrave;ng chuồng trại,d&ugrave;ng thức ăn thừa cho lợn...&nbsp;</p>\r\n\r\n<p>Tại nhi&ecirc;̀u địa phương, h&acirc;̀u h&ecirc;́t c&aacute;c hộ chăn nu&ocirc;i nhỏ lẻ đ&ecirc;̀u kh&ocirc;ng thực hiện t&aacute;i đ&agrave;n do lo sợ dịch tả lợn ch&acirc;u Phi. Do đ&oacute; theo dự b&aacute;o, ngu&ocirc;̀n cung thịt lợn v&agrave;o cu&ocirc;́i năm 2019 c&oacute; th&ecirc;̉ giảm mạnh v&agrave; gi&aacute; thịt lợn sẽ tăng l&ecirc;n trong thời gian tới.&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, tại thị trường thế giới, gi&aacute; lợn hơi giao th&aacute;ng 6/2019 tại S&agrave;n Giao dịch h&agrave;ng h&oacute;a Chicago, Mỹ bi&ecirc;́n động giảm 3,35 UScent/lb xu&ocirc;́ng c&ograve;n 89,425 UScent/lb.&nbsp;</p>\r\n\r\n<p>Gi&aacute; lợn hơi giảm do ngu&ocirc;̀n cung d&ocirc;̀i d&agrave;o tr&ecirc;n thị trường th&ecirc;́ giới. B&ecirc;n cạnh đ&oacute;, căng thẳng thương mại Mỹ-Trung cũng g&acirc;y ảnh hưởng lớn tới kinh t&ecirc;́ to&agrave;n c&acirc;̀u n&oacute;i chung v&agrave; tới c&aacute;c ng&agrave;nh kinh t&ecirc;́ mũi nhọn của hai nước n&oacute;i ri&ecirc;ng.&nbsp;</p>\r\n\r\n<p>Đỉnh đi&ecirc;̉m l&agrave; v&agrave;o giữa th&aacute;ng 5, sau khi t&ocirc;̉ng th&ocirc;́ng Trump th&ocirc;ng qua quy&ecirc;́t định n&acirc;ng thu&ecirc;́ nhập kh&acirc;̉u với 200 tỷ USD h&agrave;ng Trung Qu&ocirc;́c, đ&ocirc;̀ng thời đe dọa đ&aacute;nh thu&ecirc;́ to&agrave;n bộ h&agrave;ng h&oacute;a nước n&agrave;y, Trung Qu&ocirc;́c đ&aacute;p trả bằng c&aacute;ch hủy c&aacute;c đơn h&agrave;ng mua 3.247 t&acirc;́n thịt lợn của Mỹ.&nbsp;</p>\r\n\r\n<p>Sự kiện n&agrave;y đang g&acirc;y &aacute;p lực lớn l&ecirc;n ng&agrave;nh chăn nu&ocirc;i trị gi&aacute; 6,5 tỷ USD của Mỹ bởi Trung Qu&ocirc;́c v&agrave; Hong Kong cộng lại, l&agrave; thị trường xu&acirc;́t kh&acirc;̉u thịt lợn lớn thứ hai của Mỹ.&nbsp;</p>\r\n', 1, 3, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (15, '170940.jpg', 'Nông dân phải vay chui lãi \'cắt cổ\', ngân hàng ở đâu?', 'Tại Hội thảo về chính sách tín dụng cho nông nghiệp và nông thôn ngày 29/9, nhiều ý kiến đã nêu ra những bất cập khiến nông dân nhiều nơi phải vay để sản xuất với mức lãi “cắt cổ”.', '<p>Tại Hội thảo về ch&iacute;nh s&aacute;ch t&iacute;n dụng cho n&ocirc;ng nghiệp v&agrave; n&ocirc;ng th&ocirc;n ng&agrave;y 29/9, nhiều &yacute; kiến đ&atilde; n&ecirc;u ra những bất cập khiến n&ocirc;ng d&acirc;n nhiều nơi phải vay để sản xuất với mức l&atilde;i &ldquo;cắt cổ&rdquo;.</p>\r\n\r\n<p>TS. Ho&agrave;ng Cầm, Viện Nghi&ecirc;n cứu văn h&oacute;a cho biết vừa ho&agrave;n th&agrave;nh nghi&ecirc;n cứu về t&igrave;nh trạng t&iacute;n dụng của n&ocirc;ng d&acirc;n l&agrave; người d&acirc;n tộc thiểu số. Kết quả cho thấy ở T&acirc;y Nguy&ecirc;n, c&oacute; 86% c&aacute;c hộ g&aacute;nh nợ nần, trong đ&oacute; 77% l&agrave; đi vay tư nh&acirc;n (vay n&oacute;ng), với trung b&igrave;nh từ 43-44 triệu đồng/hộ, c&aacute; biệt c&oacute; những hộ nợ tới hơn 240 triệu đồng. Nợ xấu ở T&acirc;y Nguy&ecirc;n trung b&igrave;nh theo con số tuyệt đối l&agrave; 20 triệu đồng/hộ.</p>\r\n\r\n<p><strong>Sổ đỏ cả x&atilde;&nbsp;nằm trong tay một&nbsp;cửa h&agrave;ng t&iacute;n dụng</strong></p>\r\n\r\n<p>&Ocirc;ng Cầm cho biết, tại những khu vực khảo s&aacute;t, người d&acirc;n trồng c&agrave; ph&ecirc; cần tiền mua ph&acirc;n b&oacute;n, thuốc trừ s&acirc;u, thuốc trừ cỏ... đều đi vay. Đ&aacute;ng n&oacute;i, t&igrave;nh trạng vay n&oacute;ng với l&atilde;i suất 3-5%/th&aacute;ng (tương đương 36-60%/năm), thậm ch&iacute; c&ograve;n cao hơn kh&aacute; phổ biến.</p>\r\n\r\n<p>T&igrave;nh trạng &ldquo;vay nh&acirc;n, vay non&rdquo; (vay tiền đầu vụ của c&aacute;c hộ kinh doanh vật tư n&ocirc;ng nghiệp, cuối vụ trả bằng c&agrave; ph&ecirc;, ng&ocirc;) với tỷ lệ 50-60% kh&ocirc;ng hiếm. &ldquo;Ở đ&acirc;y c&oacute; những người lập cửa h&agrave;ng tạp h&oacute;a chuy&ecirc;n cho vay tiền, cứ mỗi triệu th&igrave; trả l&atilde;i 30.000-50.000 đồng/ng&agrave;y. Họ c&oacute; hai cuốn sổ, một cuốn nh&aacute;p, một cuốn đầy đủ cả chữ k&yacute;. Đến cuối vụ, họ cho người đi xe m&aacute;y đến tận ch&acirc;n cầu thang tịch thu ng&ocirc;, c&agrave; ph&ecirc; của từng hộ vay&rdquo;, &ocirc;ng Cầm kể.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nong dan phai vay chui lai \'cat co\', ngan hang o dau? hinh anh 1 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/Ngtmtz/2016_10_01/170940.jpg\" style=\"height:438px; width:600px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Kh&oacute; vay ng&acirc;n h&agrave;ng, nhiều hộ n&ocirc;ng d&acirc;n phải vay &ldquo;n&oacute;ng&rdquo;với l&atilde;i suất tới 60%/năm.&nbsp;\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo &ocirc;ng Cầm, người d&acirc;n địa phương thường gọi những cửa h&agrave;ng tr&ecirc;n với c&aacute;i t&ecirc;n kh&aacute; đặc biệt: &ldquo;Cửa h&agrave;ng t&iacute;n dụng&rdquo;.</p>\r\n\r\n<p>&ldquo;C&oacute; &ocirc;ng chủ tịch x&atilde; tiết lộ rằng sổ hộ khẩu của cả x&atilde; nằm trong tay của b&agrave; chủ &ldquo;cửa h&agrave;ng t&iacute;n dụng&rdquo;. T&ocirc;i hỏi sao kh&ocirc;ng vay t&iacute;n dụng ch&iacute;nh thức th&igrave; &ocirc;ng ấy cho biết từ 3 năm nay, ng&acirc;n h&agrave;ng đưa ra quy định: Tất cả th&agrave;nh vi&ecirc;n trong sổ hộ khẩu phải đến ng&acirc;n h&agrave;ng tr&igrave;nh diện mới được vay. Nếu cứ đi đi lại lại lo thủ tục m&agrave; tr&igrave;nh diện như thế th&igrave; rất tốn k&eacute;m&rdquo;, &ocirc;ng Cầm kể lại.</p>\r\n\r\n<p>Cũng theo kết quả điều tra của nh&oacute;m t&aacute;c giả, tỷ lệ vay nợ để sản xuất của b&agrave; con d&acirc;n tộc thiểu số tại c&aacute;c tỉnh Đắk Lắk l&agrave; 68,3%, Lầm Đồng 67%, Th&aacute;i Nguy&ecirc;n v&agrave; Sơn La cũng trong t&igrave;nh trạng tương tự.</p>\r\n\r\n<p><strong>T&agrave;i ch&iacute;nh vi m&ocirc;:&nbsp;Bao nhi&ecirc;u năm vẫn l&agrave;&nbsp;&yacute; tưởng</strong></p>\r\n\r\n<p>&Ocirc;ng L&ecirc; B&aacute; Lịch, Chủ tịch Hiệp hội thức ăn chăn nu&ocirc;i Việt Nam, cho biết c&aacute;c th&agrave;nh vi&ecirc;n của hiệp hội đều đang phải vay ng&acirc;n h&agrave;ng với l&atilde;i suất 11%/năm. Kinh nghiệm của Trung Quốc, Th&aacute;i Lan, Mỹ cho thấy để hỗ trợ n&ocirc;ng nghiệp th&igrave; phải hạ l&atilde;i vay, mức cho vay phải theo v&ograve;ng đời gia s&uacute;c, c&acirc;y trồng.</p>\r\n\r\n<p>B&agrave; Nguyễn Ngọc Mai (Viện H&agrave;n l&acirc;m Khoa học Việt Nam) cho biết, b&agrave; đ&atilde; tiến h&agrave;nh khảo s&aacute;t về t&iacute;n dụng đối với 600 người ngh&egrave;o ở H&agrave; Nội th&igrave; thấy tỷ lệ tiếp cận vốn của họ rất thấp, dưới 5%.</p>\r\n\r\n<p>&ldquo;Trong đ&oacute;, 50% vay chủ yếu để chữa bệnh. Số lượng c&ograve;n lại đứng danh nghĩa người ngh&egrave;o vay để kinh doanh. C&oacute; những người đ&atilde; &#39;xui&#39; người ngh&egrave;o đứng ra vay vốn để vay lại số vốn n&agrave;y, sau đ&oacute; trả cho họ một khoản tiền&rdquo;.</p>\r\n\r\n<p>&ldquo;Trồng c&agrave; ph&ecirc;, nu&ocirc;i lợn chỉ cho vay 6 th&aacute;ng th&igrave; l&agrave;m được g&igrave;, lấy g&igrave; trả gốc? Nếu kh&ocirc;ng nhanh ch&oacute;ng giải quyết vấn đề n&agrave;y th&igrave; t&ocirc;i nghĩ khi v&agrave;o TPP (Hiệp định đối t&aacute;c xuy&ecirc;n Th&aacute;i B&igrave;nh Dương) th&igrave; năm, bảy năm nữa ch&uacute;ng ta ngồi đ&oacute; m&agrave; ăn thịt lợn Mỹ!&rdquo;, &ocirc;ng Lịch n&ecirc;u vấn đề.</p>\r\n\r\n<p>Đồng quan điểm, b&agrave; L&ecirc; Thị L&acirc;n, Gi&aacute;m đốc Trung t&acirc;m ph&aacute;t triển nguồn lực cộng đồng, nhận định ng&acirc;n h&agrave;ng chưa tin người d&acirc;n c&oacute; thể trả được nợ, n&ecirc;n ngay từ kh&acirc;u x&acirc;y dựng ch&iacute;nh s&aacute;ch đ&atilde; kh&ocirc;ng ch&uacute; trọng.</p>\r\n\r\n<p>B&agrave; L&acirc;n v&iacute; dụ, Ng&acirc;n h&agrave;ng Nh&agrave; nước đưa ra ch&iacute;nh s&aacute;ch hỗ trợ l&atilde;i suất lĩnh vực n&ocirc;ng nghiệp, n&ocirc;ng th&ocirc;n, nhưng Agribank hay c&aacute;c ng&acirc;n h&agrave;ng thương mại kh&aacute;c kh&ocirc;ng thể cho vay 7% được khi huy động đ&atilde; 7-8% rồi. &ldquo;Ch&iacute;nh v&igrave; thế, c&aacute;c ng&acirc;n h&agrave;ng lu&ocirc;n y&ecirc;u cầu hồ sơ vay vốn của n&ocirc;ng d&acirc;n phải chứng minh t&iacute;nh khả thi, đầu ra, đầu v&agrave;o... Ch&iacute;nh v&igrave; thế, v&ugrave;ng ch&uacute;ng t&ocirc;i v&agrave;o khảo s&aacute;t kh&ocirc;ng ai c&oacute; thể vay được&rdquo;, b&agrave; L&acirc;n n&oacute;i.</p>\r\n\r\n<p>Nhiều &yacute; kiến cho rằng n&ecirc;n ph&aacute;t triển t&agrave;i ch&iacute;nh vi m&ocirc; để b&ugrave; đắp thiếu hụt t&iacute;n dụng cho c&aacute;c đối tượng tr&ecirc;n. Nhưng theo &ocirc;ng Ph&iacute; Trọng Hiển, đại diện Ng&acirc;n h&agrave;ng Nh&agrave; nước, hiện nay cơ quan điều h&agrave;nh đang r&agrave; so&aacute;t c&aacute;c văn bản ph&aacute;p luật, đặc biệt ch&iacute;nh s&aacute;ch với đối tượng chưa được điều chỉnh (như tổ chức phi Ch&iacute;nh phủ) để x&acirc;y dựng văn bản ph&aacute;p l&yacute;.</p>\r\n\r\n<p>C&ograve;n về thu xếp nguồn vốn cho tổ chức t&agrave;i ch&iacute;nh vi m&ocirc;, &ocirc;ng Hiển cho biết đ&atilde; được đặt ra từ l&acirc;u v&agrave; kh&ocirc;ng th&aacute;o gỡ một, hai tổ chức m&agrave; l&agrave; cả hệ thống. &ldquo;Cần x&acirc;y dựng đầu mối để k&ecirc;u gọi vốn cả trong v&agrave; ngo&agrave;i nước, nhưng giờ mới l&agrave; &yacute; tưởng&rdquo;, &ocirc;ng Hiển n&oacute;i.</p>\r\n', 3, 1, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (16, 'thucanchannuoi28.jpg', 'Nước mắt của người trồng ngô trên đồi tại Sơn La', 'Từng đồi ngô vàng óng, miên man, tít tắp tận chân trời ẩn giấu sau đó là máu, là nước mắt, là nỗi buồn, là bi kịch của nghìn vạn kiếp người trồng ngô ở Sơn La.', '<p>Gi&aacute; ng&ocirc; giống ở ngo&agrave;i 70.000 đồng/kg nhưng chủ đầu tư cho nợ cuối vụ, t&iacute;nh l&ecirc;n 130.000 đồng/kg, kh&ocirc;ng trả nổi năm sau lại vọt l&ecirc;n.</p>\r\n\r\n<h3>Cay hơn ớt</h3>\r\n\r\n<p>Cơn b&atilde;o số ba khiến cho hơn 10 km đường đất từ trung t&acirc;m x&atilde; Phi&ecirc;ng Pằn (huyện Mai Sơn, Sơn La) về bản người Xinh Mun ở N&agrave; Nhụng trở th&agrave;nh sự đ&agrave;y ải. C&acirc;y đổ, đ&aacute; ch&egrave;n, đất lấp. Nhiều chỗ l&ograve;ng đường tựa l&ograve;ng suối khiến t&ocirc;i ho&agrave;i nghi dừng lại để hỏi, nhiều chỗ đường chỉ đặt vừa vặn một hai b&agrave;n ch&acirc;n người, sơ sẩy t&iacute; l&agrave; rơi xuống vực.</p>\r\n\r\n<p>Đi mải miết kh&ocirc;ng d&aacute;m nh&igrave;n l&ecirc;n cũng chẳng d&aacute;m nh&igrave;n xuống, mặt trời xế b&oacute;ng t&ocirc;i mới t&igrave;m được v&agrave;o nh&agrave; Trưởng bản L&ograve; Văn Thu.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Nuoc mat cua nguoi trong ngo tren doi tai Son La hinh anh 1 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/pgi_xvauqbnau/2016_09_14/155251_dsc_5501.jpg\" style=\"height:440px; width:665px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Những đồi ng&ocirc; b&aacute;t ng&aacute;t nhưng nay kh&ocirc;ng thuộc về &ocirc;ng Phương nữa.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đ&atilde; qu&aacute; bữa cơm chiều, h&agrave;ng qu&aacute;n lại kh&ocirc;ng c&oacute;, anh &aacute;i ngại nh&igrave;n kẻ bộ h&agrave;nh lấm lem b&ugrave;n đất v&agrave; hỏi: &ldquo;Ăn được cơm nếp kh&ocirc;ng?&rdquo;. T&ocirc;i gật đầu. Vợ Thu dỡ &iacute;t x&ocirc;i đ&atilde; đồ sẵn từ buổi s&aacute;ng để trong giỏ, xăm xới ra vườn h&aacute;i vội một quả bầu về nấu canh với cục xương tr&acirc;u g&aacute;c tr&ecirc;n bếp.</p>\r\n\r\n<p>Con tr&acirc;u vốn l&agrave; t&agrave;i sản hồi m&ocirc;n của chị khi về nh&agrave; chồng, đang thả tr&ecirc;n rừng th&igrave; bị xảy ch&acirc;n, ng&atilde; n&uacute;i, đất v&ugrave;i. L&uacute;c người ta đ&agrave;o được th&igrave; n&oacute; đ&atilde; th&agrave;nh một c&aacute;i x&aacute;c mềm nhũn, lạnh ngắt, xương sườn, xương ống n&aacute;t vụn n&ecirc;n đ&agrave;nh k&eacute;o về mổ. Phần b&aacute;n chịu cho b&agrave; con với gi&aacute; rẻ, phần g&aacute;c bếp ăn dần. Tự dưng mất đứt hơn 20 triệu đồng khiến cho cả hai vợ chồng khi kể với t&ocirc;i cứ thay nhau thở d&agrave;i sườn sượt.Theo tổng điều tra d&acirc;n số năm 2009, Xinh Mun l&agrave; d&acirc;n tộc c&oacute; 23.278 người, n&oacute;i tiếng Puộc, chủ yếu sinh sống ở Sơn La 21.288 người, Điện Bi&ecirc;n 1.926 người, Đồng Nai 10 người, Nam Định 10 người v&agrave; H&agrave; Nội 10 người. Họ sinh sống bằng nghề gieo l&uacute;a nương v&agrave; săn bắn th&uacute;, c&oacute; tập qu&aacute;n nhuộm răng đen, ăn trầu, uống rượu cần, th&iacute;ch gia vị cay.</p>\r\n\r\n<p>Kể từ hồi trồng ng&ocirc; lai cũng l&agrave; l&uacute;c m&agrave; d&acirc;n tộc n&agrave;y biết đến một thứ gia vị c&ograve;n cay hơn cả ớt, đắng hơn cả rượu, đ&oacute; l&agrave; nợ.</p>\r\n\r\n<p>Bản 146 hộ nhưng c&oacute; đến 106 hộ ngh&egrave;o. Một số kh&ocirc;ng thuộc diện hộ ngh&egrave;o kh&ocirc;ng phải do đ&atilde; gi&agrave;u c&oacute; g&igrave;, m&agrave; bởi v&igrave; khi trưởng bản bắc &ldquo;loa mồm&rdquo; th&ocirc;ng b&aacute;o đến từng nh&agrave; phải đi họp x&eacute;t hộ ngh&egrave;o họ lại kh&ocirc;ng để &yacute;. Thế l&agrave; nghiễm nhi&ecirc;n thuộc v&agrave;o danh s&aacute;ch hộ kh&aacute; giả. Ấy vậy m&agrave; chẳng thấy ai th&egrave;m thắc mắc về chuyện n&agrave;y. &nbsp;</p>\r\n\r\n<h3>X&oacute;t hơn muối</h3>\r\n\r\n<p>Trước đ&acirc;y d&acirc;n Xinh Mun trồng l&uacute;a nương, cuộc sống tự sản tự ti&ecirc;u tuy khổ cực nhưng kh&ocirc;ng hề mắc nợ. Hơn mười năm trước c&oacute; c&acirc;y ng&ocirc; lai về bản l&agrave; c&oacute; chủ đầu tư về theo. Trước ti&ecirc;n c&oacute; &ocirc;ng H&agrave; ở C&ograve; N&ograve;i, &ocirc;ng Tuấn Đen ở H&aacute;t L&oacute;t đến nay th&ecirc;m &ocirc;ng Bi&ecirc;n, &ocirc;ng Bộ, &ocirc;ng H&ugrave;ng Thụ, &ocirc;ng Đ&ocirc;ng Nhung, &ocirc;ng X&iacute; B&agrave;i...</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Nuoc mat cua nguoi trong ngo tren doi tai Son La hinh anh 2 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/pgi_xvauqbnau/2016_09_14/155251_dsc_5607.jpg\" style=\"height:1004px; width:665px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&acirc;y ng&ocirc; tr&ecirc;n đất Sơn La.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>C&aacute;c chủ đầu tư thuyết phục d&acirc;n bản kh&ocirc;ng trồng l&uacute;a nữa m&agrave; chuyển sang trồng ng&ocirc;. Họ sẵn s&agrave;ng cấp ph&aacute;t từ giống, ph&acirc;n b&oacute;n, gạo, muối, m&igrave; ch&iacute;nh đến c&aacute;i kim, sợi chỉ, b&aacute;nh x&agrave; ph&ograve;ng. Tất cả đều sẵn c&oacute;, chỉ việc k&yacute; v&agrave;o sổ nợ l&agrave; xong. Nhiều hộ kh&ocirc;ng biết chữ cũng chẳng hay trong giấy viết những g&igrave;, cứ k&yacute; hoặc điểm chỉ đại.</p>\r\n\r\n<p>Ứng vật tư ư? Gi&aacute; ng&ocirc; giống ở ngo&agrave;i 70.000đ/kg nhưng chủ đầu tư cho nợ cuối vụ t&iacute;nh l&ecirc;n 130.000 đồng/kg, kh&ocirc;ng trả nổi năm sau vọt l&ecirc;n 180.000 200.000 đồng/kg. Ngược lại, gi&aacute; mua ng&ocirc; thương phẩm ở ngo&agrave;i 4.000 đồng/kg nhưng b&aacute;n cho đại l&yacute; chỉ được 2.500-3.000 đồng/kg.</p>\r\n\r\n<p>Kh&ocirc;ng b&aacute;n cũng kh&ocirc;ng xong v&igrave; đ&atilde; tr&oacute;t nhận đầu tư rồi. Gi&aacute; ph&acirc;n đạm b&ecirc;n ngo&agrave;i chỉ 9.000 đồng/kg nhưng mua chịu cuối vụ phải trả th&agrave;nh 16.000 đồng/kg.</p>\r\n\r\n<p>Từ th&aacute;ng 3 đến th&aacute;ng 6 người d&acirc;n phải ăn gạo của chủ đầu tư, th&aacute;ng 7 trở đi mới c&oacute; l&uacute;a xu&acirc;n gặt về. Gạo đầu tư cứng qu&egrave;o, đồ l&ecirc;n để 1-2 tiếng l&agrave; kh&ocirc;ng nhai được, chỉ d&ugrave;ng v&agrave;o mỗi việc nấu rượu. Gạo ấy ngo&agrave;i chợ b&aacute;n 10-11.000 đồng/kg nhưng v&igrave; mua chịu n&ecirc;n phải trả th&agrave;nh 20.000 đồng/kg.</p>\r\n\r\n<p>Vay tiền ư? Đại l&yacute; sẵn s&agrave;ng cung cấp với mức l&atilde;i phổ biến 3%/th&aacute;ng tương đương 36%/năm. Ốm đau, cưới hỏi, ma chay cần vay n&oacute;ng ư? Lại c&agrave;ng dễ d&agrave;ng nhưng h&atilde;y chấp nhận l&atilde;i 50% bất kể l&agrave; v&agrave;i h&ocirc;m sau trả hay v&agrave;i th&aacute;ng sau mới trả (kh&ocirc;ng được qu&aacute; 10 th&aacute;ng).</p>\r\n\r\n<p>Đến ngay nh&agrave; trưởng bản Thu vẫn c&ograve;n một m&oacute;n nợ 20 triệu vay từ h&ocirc;m 17/3 để mua ph&acirc;n, mua giống. Nếu b&aacute;n ng&ocirc; cho người kh&aacute;c lấy tiền trả, chủ đầu tư t&iacute;nh l&atilde;i 2,5%/th&aacute;ng, nếu b&aacute;n ng&ocirc; cho ch&iacute;nh chủ đầu tư th&igrave; sẽ được trừ tiền. Một vụ ng&ocirc; k&eacute;o d&agrave;i 5 th&aacute;ng, m&oacute;n nợ 20 triệu Thu vay sẽ nở ra th&agrave;nh 25 triệu.</p>\r\n\r\n<p>Gần như 100% d&acirc;n bản &ocirc;m nợ, 30-40% phải g&aacute;n đất trừ dần. Trừ m&atilde;i m&agrave; chẳng thấy ai tho&aacute;t. 700 ha ng&ocirc; của N&agrave; Nhụng theo năm th&aacute;ng cứ thế dần teo t&oacute;p như miếng mỡ ở trong chảo bị vắt đến tận kiệt c&ugrave;ng.</p>\r\n\r\n<p>Anh V&igrave; Văn Xồn vốn c&oacute; khoảng 3 ha đất nay kh&ocirc;ng c&ograve;n t&iacute; n&agrave;o v&igrave; g&aacute;n nợ cho chủ đầu tư X&iacute; B&agrave;i trong thời hạn 10 năm, v&agrave; c&ograve;n &ocirc;m th&ecirc;m khoản 40 triệu chưa trả được. 17 năm cặm cụi trồng ng&ocirc;, c&agrave;ng l&agrave;m lại c&agrave;ng nợ, đến cả c&aacute;i xe m&aacute;y anh cũng kh&ocirc;ng c&oacute;.</p>\r\n\r\n<p>Từ địa vị &ocirc;ng chủ nay Xồn trở th&agrave;nh người l&agrave;m thu&ecirc; tr&ecirc;n ch&iacute;nh đất của m&igrave;nh. Với gi&aacute; ng&ocirc; ng&agrave;y c&agrave;ng xuống dốc như hiện nay kh&ocirc;ng biết bao giờ anh mới trả được hết nợ?</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Nuoc mat cua nguoi trong ngo tren doi tai Son La hinh anh 3 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/pgi_xvauqbnau/2016_09_14/155251_dsc_5493.jpg\" style=\"height:1004px; width:665px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Anh Xồn thất thần b&ecirc;n nương ng&ocirc;.&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Nh&agrave; &ocirc;ng L&ograve; Văn Phương bố vợ trưởng bản Thu nằm lọt thỏm tr&ecirc;n đỉnh một quả đồi xung quanh to&agrave;n ng&ocirc;. Quả đồi m&ecirc;nh m&ocirc;ng đ&oacute; b&igrave;nh thường &ocirc;ng phải gieo 60-70 kg ng&ocirc; giống mới phủ k&iacute;n (khoảng 5 ha) nay đ&atilde; thuộc về người kh&aacute;c.</p>\r\n\r\n<p>&Ocirc;ng Phương kh&ocirc;ng biết chữ lại c&agrave;ng chẳng biết đến c&aacute;c con số ghi trong sổ chốt nợ l&agrave; đ&uacute;ng hay sai. Khi chuyển giao, chủ đầu tư đ&atilde; mang sẵn một hộp phẩm đỏ để &ocirc;ng chỉ việc ấn ng&oacute;n tay to đ&ugrave;ng, th&ocirc; kệch xuống tờ giấy đ&atilde; bi&ecirc;n sẵn, điểm chỉ. Hiện, tổng số nợ của &ocirc;ng l&ecirc;n tới 165 triệu.</p>\r\n\r\n<p>Theo người con rể của &ocirc;ng, đồng thời cũng l&agrave; trưởng bản, buổi l&agrave;m thủ tục g&aacute;n đất 39 hộ cho d&acirc;n N&agrave; Nhụng như sau: Chủ đầu tư ph&ocirc; t&ocirc; sẵn giấy bi&ecirc;n nhận nợ, x&atilde; xuống r&agrave; so&aacute;t rồi gọi trưởng th&ocirc;n c&ugrave;ng đại diện 5 đo&agrave;n thể đến chứng kiến cảnh k&yacute; giấy g&aacute;n nợ.</p>\r\n\r\n<p>Từ đầu đến cuối chủ đầu tư kh&ocirc;ng xuất hiện m&agrave; chỉ c&oacute; con nợ. Đ&agrave;n &ocirc;ng c&ograve;n đỡ, đ&agrave;n b&agrave; nhiều người vừa ra khỏi cửa mắt đ&atilde; đỏ l&ecirc;n rồi. Đ&oacute; l&agrave; những nh&agrave; bị g&aacute;n nợ dưới sự ch&iacute;nh kiến của c&aacute;n bộ ch&iacute;nh quyền, c&ograve;n nhiều nh&agrave; bị g&aacute;n nợ chui.</p>\r\n\r\n<p>Gia đ&igrave;nh chị L&ograve; Thị Pản bắt đầu trồng ng&ocirc; với chủ đầu tư Đinh Duy Sĩ từ năm 2002, được cung cấp ph&acirc;n, giống, gạo&hellip; L&uacute;c đầu số nợ chỉ nhỏ như hạt ng&ocirc; giống, sau mỗi m&ugrave;a n&oacute; lại ph&igrave;nh to l&ecirc;n để hiện nay lớn bằng cả đồi ng&ocirc;, trị gi&aacute; 145 triệu.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n			<p><img alt=\"Nuoc mat cua nguoi trong ngo tren doi tai Son La hinh anh 4 \" src=\"https://znews-photo.zadn.vn/w660/Uploaded/pgi_xvauqbnau/2016_09_14/155251_dsc_5513.jpg\" style=\"height:1004px; width:665px\" /></p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Những tờ giấy g&aacute;n nợ.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>V&eacute;t sạch c&aacute;c thứ trong gia đ&igrave;nh đem b&aacute;n, chị Pản chỉ trả được 21 triệu, vẫn c&ograve;n nợ 128 triệu. Người chồng bỏ nh&agrave; ra đi để lại n&uacute;i nợ cho đứa con trai l&agrave; L&ograve; Văn Thi&ecirc;n năm ấy cũng vừa mới lập gia đ&igrave;nh, g&aacute;nh v&aacute;c.</p>\r\n\r\n<p>Hai vợ chồng Thi&ecirc;n suốt ng&agrave;y cắm mặt, c&ograve;ng lưng tr&ecirc;n đồi từ mờ s&aacute;ng đến tối mịt người đi sau kh&ocirc;ng nh&igrave;n r&otilde; lưng người đi trước. Trời kh&ocirc;ng phụ c&ocirc;ng người, vụ m&ugrave;a năm ấy bắp ng&ocirc; to như bắp chuối rừng. Vợ chồng Thi&ecirc;n b&aacute;n cả nương ng&ocirc; được đ&uacute;ng 52 triệu nhưng kh&ocirc;ng hề được đếm, m&agrave; tất cả đều chảy v&agrave;o t&uacute;i &ocirc;ng chủ.</p>\r\n\r\n<p>Chủ đầu tư bảo với Thi&ecirc;n rằng, bố m&agrave;y đ&atilde; g&aacute;n đất cho tao, giờ mẹ con m&agrave;y phải viết giấy giao lại đất từ năm 2012-2022, nếu kh&ocirc;ng tao sẽ lấy người dưới trung ương l&ecirc;n để giải quyết.</p>\r\n\r\n<p>Lời dọa ấy khiến cho mẹ con Thi&ecirc;n khiếp v&iacute;a đ&agrave;nh phải nghe theo, k&yacute; v&agrave;o tờ giấy b&agrave;n giao mảnh đất gieo được 60 kg ng&ocirc; giống (khoảng hơn 3 ha). Nh&agrave; anh h&ocirc;m đ&oacute; c&ograve;n bị biến th&agrave;nh một b&atilde;i tập trung cho khoảng 20-30 con nợ đến k&yacute; t&ecirc;n hay điểm chỉ.</p>\r\n\r\n<p>C&aacute;i k&eacute;t sắt trong nh&agrave; thay v&igrave; chứa tiền như thiết kế nay được ho&aacute;n đổi c&ocirc;ng năng d&ugrave;ng để chứa đống giấy ghi nợ.</p>\r\n', 3, 1, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (17, 'gao.jpg', 'Những loại \'đinh\' làm khốn khổ doanh nghiệp', 'TS Lê Hồng Sơn – Nguyên Cục trưởng Cục kiểm tra văn bản quy phạm pháp luật (Bộ Tư Pháp) chỉ rõ các loại đinh dưới thảm “đón” doanh nghiệp, đặc biệt các doanh nghiệp vừa và nhỏ.', '<p>Ph&aacute;t biểu tại Hội thảo Ch&iacute;nh s&aacute;ch ph&aacute;t triển doanh nghiệp (DN) vừa v&agrave; nhỏ: Tầm nh&igrave;n v&agrave; h&agrave;nh động s&aacute;ng 3/6, TS L&ecirc; Hồng Sơn &ndash; Nguy&ecirc;n Cục trưởng Cục Kiểm tra văn bản (Bộ Tư ph&aacute;p) cho hay, l&acirc;u nay vẫn tồn tại thực trạng bất b&igrave;nh đẳng về điều kiện kinh doanh với DN vừa v&agrave; nhỏ trong văn bản quy phạm ph&aacute;p luật.</p>\r\n\r\n<p>Dẫn chứng một số loại &ldquo;đinh&rdquo; g&acirc;y kh&oacute; cho DN nhỏ v&agrave; vừa, &ocirc;ng Sơn cho biết trong c&aacute;c thể chế, c&aacute;c văn bản quy phạm ph&aacute;p luật, &ldquo;đinh&rdquo; chủ yếu ở c&aacute;c th&ocirc;ng tư &ndash; văn bản quy phạm ph&aacute;p luật cấp Bộ, c&aacute; biệt c&oacute; trong một số quy định của Ch&iacute;nh phủ hoặc trong c&aacute;c văn bản của địa phương.</p>\r\n\r\n<p>Đơn cử, cuối năm 2015, Quảng Ninh ban h&agrave;nh 2 quyết định (4088/2015/QĐ- UBND v&agrave; 3625/QĐ-UBND) đặt ra một loạt c&aacute;c điều kiện, ti&ecirc;u ch&iacute; đối với c&aacute;c t&agrave;u thuyền kinh doanh du lịch tr&ecirc;n Vịnh Hạ Long &ndash; B&aacute;i Tử Long, như: R&uacute;t ngắn thời hạn (ni&ecirc;n hạn) sử dụng c&aacute;c phương tiện thủy 5 năm &ndash; 10 năm; quy định về điều kiện ph&ograve;ng ch&aacute;y, chữa ch&aacute;y đối với t&agrave;u lưu tr&uacute;; phải c&oacute; thiết bị tự động b&aacute;o ch&aacute;y ở tất cả c&aacute;c buồng của t&agrave;u....</p>\r\n\r\n<p>&ldquo;Như vậy phải c&oacute; th&ecirc;m bể chứa nước tr&ecirc;n t&agrave;u, hệ thống dẫn nước v&agrave; điều n&agrave;y kh&ocirc;ng thể thực hiện được với c&aacute;c t&agrave;u đang hoạt động&rdquo;, &ocirc;ng Sơn cho biết.</p>\r\n\r\n<p>Theo &ocirc;ng Sơn, 2 quyết định tr&ecirc;n bức tử c&aacute;c DN đang kinh doanh hoạt động du lịch, dịch vụ tr&ecirc;n địa b&agrave;n, đe dọa hơn 1.000 lao động c&oacute; nguy cơ mất việc l&agrave;m, vi phạm một loạt c&aacute;c luật: Luật DN, Luật Đầu tư, Luật Ph&ograve;ng ch&aacute;y chữa ch&aacute;y, Luật Du lịch, Luật Giao th&ocirc;ng đường thủy nội địa, Bộ luật d&acirc;n sự về thẩm quyền điều kiện kinh doanh, ph&ograve;ng ch&aacute;y, chữa ch&aacute;y, ni&ecirc;n hạn sử dụng phương tiện tủy, thậm ch&iacute; vi phạm điều 14, điều 33 Hiến ph&aacute;p 2013.</p>\r\n\r\n<p>&Ocirc;ng lấy th&ecirc;m một dẫn chứng kh&aacute;c cho thấy l&atilde;nh đạo một số tỉnh tạo s&acirc;n chơi bất b&igrave;nh đẳng, g&acirc;y kh&oacute; cho DN ngoại tỉnh. Quảng Ninh từng ra c&ocirc;ng văn số 5290 (ng&agrave;y 28/7/2014) y&ecirc;u cầu d&acirc;n d&ugrave;ng bia tỉnh ta để &ldquo;chung tay g&oacute;p sức ti&ecirc;u thụ bia tr&ecirc;n địa b&agrave;n tỉnh&rdquo;. Kh&ocirc;ng chỉ thế, &ldquo;xi măng cũng tỉnh ta&rdquo; khi họ ra c&ocirc;ng văn số 1747 (ng&agrave;y 14/5/2014) y&ecirc;u cầu người d&acirc;n, DN hỗ trợ ti&ecirc;u thụ xi măng sản xuất tr&ecirc;n địa b&agrave;n tỉnh l&agrave; vi phạm Luật Cạnh tranh.</p>\r\n\r\n<p>&Ocirc;ng Sơn cho biết c&oacute; nơi c&ograve;n &eacute;p DN, kh&ocirc;ng cho điều chỉnh mức lương tối thiểu v&ugrave;ng trong dự to&aacute;n c&ocirc;ng tr&igrave;nh, hay ban h&agrave;nh quyết định h&agrave;nh ch&iacute;nh &ldquo;bẻ&rdquo; quy định đ&atilde; c&oacute; hiệu lực.</p>\r\n\r\n<p>Nghị định 109/2010/NĐ-CP của Ch&iacute;nh phủ quy định về điều kiện kinh doanh xuất khẩu gạo: DN kinh doanh xuất khẩu gạo phải c&oacute; kho chuy&ecirc;n dụng 5.000 tấn th&oacute;c, c&oacute; &iacute;t nhất 1 cơ sở xay x&aacute;t gạo c&oacute; c&ocirc;ng suất 10 tấn/giờ l&agrave; kh&ocirc;ng hợp l&yacute;, hạn chế quyền kinh doanh, tạo ti&ecirc;u cực (mượn giấy ph&eacute;p) tăng chi ph&iacute; của DN.</p>\r\n\r\n<p>Đại diện doanh nghiệp Cỏ may Đồng Th&aacute;p &ldquo;tố&rdquo; từng khốn đốn v&igrave; Nghị định tr&ecirc;n.</p>\r\n\r\n<p>&ldquo;Năm 2014, Gạo Cỏ May t&igrave;m được thị trường Singapore. Nhưng khi k&yacute; hợp đồng, đối t&aacute;c Singapore từ chối v&igrave; nghĩ rằng Cỏ May chỉ l&agrave; một anh c&ograve;, do ch&uacute;ng t&ocirc;i phải xuất ủy th&aacute;c qua một DN kh&aacute;c v&igrave; Nghị định tr&ecirc;n&rdquo;, đại diện Cỏ May cho biết.</p>\r\n\r\n<p>L&atilde;nh đạo DN n&agrave;y cho hay để c&oacute; thể xuất khẩu gạo, họ phải sang Singapore lập một c&ocirc;ng ty lấy t&ecirc;n Cỏ May Singapore v&agrave; nhập gạo của ch&iacute;nh m&igrave;nh qua một đối t&aacute;c trong nước kh&ocirc;ng c&oacute; thị trường, nhưng c&oacute; quyền xuất khẩu trực tiếp. Mỗi lần như thế chi ph&iacute; tăng th&ecirc;m&nbsp;2 USD/tấn.</p>\r\n\r\n<p>Hơn thế, Cỏ May Singapore c&ograve;n phải đ&oacute;ng thuế thu nhập DN 17% cho ch&iacute;nh phủ nước sở tại, trong khi Việt Nam kh&ocirc;ng thu được đồng thuế n&agrave;o của DN n&agrave;y, v&igrave; &ldquo;hai Cỏ May&rdquo; b&aacute;n huề vốn khi xuất khẩu.</p>\r\n', 3, 1, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (18, 'ngo-reuters.jpg', 'Giá nông sản Mỹ tăng mạnh trong tuần qua', 'Trên Sàn giao dịch hàng hóa Chicago (CBOT, Mỹ), giá nông sản giao dịch kỳ hạn tăng mạnh trong tuần giao dịch kết thúc ngày 14/6, trong bối cảnh nguồn cung sụt giảm và thời tiết ẩm ướt kéo dài.', '<p>Kh&eacute;p lại tuần giao dịch vừa qua, gi&aacute; ng&ocirc; giao th&aacute;ng 7/2019 tăng 37,25 xu Mỹ (8,96%) l&ecirc;n 4,53 USD/bushel. Gi&aacute; l&uacute;a mỳ giao th&aacute;ng 7/2019 tăng 34 xu Mỹ (6,74%) l&ecirc;n 5,385 USD/bushel. Gi&aacute; đậu tương giao th&aacute;ng 7/2019 tăng 40,5 xu Mỹ (4,73%) l&ecirc;n 8,9675 USD/bushel. (1 bushel l&uacute;a mỳ/đậu tương = 27,2 kg, 1 bushel ng&ocirc; = 25,4 kg).&nbsp;<br />\r\n<br />\r\nT&igrave;nh h&igrave;nh thời tiết ẩm ướt l&agrave; yếu tố ch&iacute;nh đẩy gi&aacute; c&aacute;c mặt h&agrave;ng n&ocirc;ng sản giao dịch tr&ecirc;n s&agrave;n CBOT, nhất l&agrave; ng&ocirc; v&agrave; đậu tương.&nbsp;<br />\r\n<br />\r\nTheo số liệu mới cập nhật do Bộ N&ocirc;ng nghiệp Mỹ (USDA) c&ocirc;ng bố, t&iacute;nh đến ng&agrave;y 9/6, chỉ c&oacute; 83% diện t&iacute;ch ng&ocirc; được gieo trồng, so với mức trung b&igrave;nh 99% trong giai đoạn 2014-2018. Trong khi đ&oacute;, diện t&iacute;ch gieo trồng đậu tương chỉ đạt 60%, thấp hơn mức trung b&igrave;nh của 5 năm l&agrave; 88%.&nbsp;<br />\r\n<br />\r\nDự b&aacute;o thời tiết cho thấy sẽ c&oacute; th&ecirc;m nhiều trận mưa b&atilde;o ảnh hưởng đến khu vực trồng trọt Midwest (Mỹ), qua đ&oacute; c&oacute; thể tiếp tục l&agrave;m gi&aacute;n đoạn tiến độ gieo trồng.&nbsp;<br />\r\n<br />\r\nNguồn cung sụt giảm cũng đ&atilde; gi&uacute;p hỗ trợ gi&aacute; ng&ocirc;. Ng&agrave;y 11/6, USDA c&ocirc;ng bố b&aacute;o c&aacute;o h&agrave;ng th&aacute;ng về nguồn cung v&agrave; nhu cầu n&ocirc;ng sản cho thấy sản lượng ng&ocirc; Mỹ trong ni&ecirc;n vụ 2019/20 dự b&aacute;o giảm 1,4 tỷ bushel xuống 13,7 tỷ bushel, được coi l&agrave; mức thấp nhất kể từ ni&ecirc;n vụ 2015/16.&nbsp;<br />\r\n<br />\r\nTrong c&ugrave;ng b&aacute;o c&aacute;o n&agrave;y, USDA cũng hạ dự trữ l&uacute;a mỳ khoảng 69 triệu bushel xuống 1.071 triệu bushel, qua đ&oacute; g&oacute;p phần hỗ trợ gi&aacute; l&uacute;a mỳ tr&ecirc;n s&agrave;n CBOT.&nbsp;<br />\r\n<br />\r\nC&aacute;c nh&agrave; quan s&aacute;t thị trường cho hay mưa b&atilde;o ở khu vực Midwest v&agrave; khu vực trồng trọt ph&iacute;a Nam cũng c&oacute; thể ảnh hưởng đến chất lượng l&uacute;a mỳ.&nbsp;<br />\r\n<br />\r\nSức &eacute;p từ xung đột thương mại k&eacute;o d&agrave;i giữa Mỹ v&agrave; nước mua đậu tương h&agrave;ng đầu thế giới l&agrave; Trung Quốc vẫn gi&uacute;p duy tr&igrave; đ&agrave; tăng của loại hạt c&oacute; dầu n&agrave;y. Tuy nhi&ecirc;n, đợt mưa lũ gần đ&acirc;y v&agrave; những đợt thuế trả đũa qua lại đ&atilde; đặt n&ocirc;ng d&acirc;n Mỹ v&agrave;o t&igrave;nh thế kh&oacute; khăn, nhất l&agrave; khi họ chuyển một số diện t&iacute;ch trồng ng&ocirc; sang đậu tương do thời tiết ẩm ướt l&agrave;m chậm trễ việc trồng ng&ocirc;.&nbsp;</p>\r\n', 4, 11, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (19, '26662.jpg', 'Thông tin về đàm phán thương mại Mỹ-Trung chi phối thị trường nông sản', 'Giá đậu tương giảm hơn 1% do thiếu các thông tin mới về cuộc đàm phán thương mại giữa Mỹ và Trung Quốc.', '<p>Tr&ecirc;n S&agrave;n giao dịch h&agrave;ng h&oacute;a Chicago (CBOT, Mỹ), gi&aacute;&nbsp;<a href=\"https://m.bnews.vn/tag/nong-san/101996/1.html\" target=\"_blank\">n&ocirc;ng sản&nbsp;</a>b&aacute;n theo c&aacute;c hợp đồng giao kỳ hạn đều sụt giảm trong tuần giao dịch kết th&uacute;c v&agrave;o ng&agrave;y 18/4, trong đ&oacute; gi&aacute; đậu tương giảm hơn 1% do thiếu c&aacute;c th&ocirc;ng tin mới về cuộc đ&agrave;m ph&aacute;n thương mại giữa Mỹ v&agrave; Trung Quốc.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Cụ thể, gi&aacute; đậu tương giao th&aacute;ng 5/2019 đ&atilde; hạ 14,75 xu Mỹ (1,65%) xuống 8,805 USD/bushel; gi&aacute; l&uacute;a mỳ giao c&ugrave;ng kỳ hạn giảm 20,25 xu Mỹ (4,36%) xuống 4,4425 USD/bushel. C&ograve;n gi&aacute; ng&ocirc; giao th&aacute;ng 7/2019 cũng giảm 2,25 xu Mỹ (0,61%) xuống 3,6725 USD/bushel. (1 bushel l&uacute;a mỳ/đậu tương = 27,2 kg, 1 bushel ng&ocirc; = 25,4 kg).&nbsp;<br />\r\n<br />\r\nC&aacute;c th&ocirc;ng tin thị trường &ldquo;eo hẹp&rdquo;, xuất khẩu chậm v&agrave; c&aacute;c nh&agrave; giao dịch của c&aacute;c quỹ giảm đầu tư đều đ&atilde; g&acirc;y sức &eacute;p l&ecirc;n gi&aacute; đậu tương trong tuần qua. Gi&aacute; đậu tương giao ngay đ&atilde; giảm xuống mức thấp của 5 th&aacute;ng trước l&uacute;c nghỉ lễ cuối tuần.&nbsp;<br />\r\n<br />\r\nC&aacute;c nh&agrave; giao dịch đang chờ đợi những dấu hiệu r&otilde; r&agrave;ng hơn về việc cuộc đ&agrave;m ph&aacute;n thương mại giữa Mỹ v&agrave; Trung Quốc đang c&oacute; tiến triển. Hai b&ecirc;n đ&atilde; nh&oacute;m họp một v&agrave;i lần trong v&agrave;i th&aacute;ng qua để cố gắng đi đến một thỏa thuận thương mại giữa hai nền kinh tế lớn nhất thế giới.&nbsp;<br />\r\n<br />\r\nBộ N&ocirc;ng nghiệp Mỹ (USDA) ng&agrave;y 18/4 cho biết xuất khẩu đậu tương r&ograve;ng trong tuần kết th&uacute;c ng&agrave;y 11/4 ở mức 382.100 tấn. USDA dự kiến sẽ c&ocirc;ng bố số liệu về tiến độ gieo trồng đậu tương Mỹ trong năm 2019 trong b&aacute;o c&aacute;o m&ugrave;a m&agrave;ng v&agrave;o tuần n&agrave;y.&nbsp;<br />\r\n<br />\r\nGi&aacute; ng&ocirc; kỳ hạn tr&ecirc;n s&agrave;n CBOT đ&atilde; kh&eacute;p tuần n&agrave;y ở mức thấp do t&igrave;nh h&igrave;nh m&ugrave;a m&agrave;ng ở Nam Mỹ thuận lợi v&agrave; th&ecirc;m một tuần thiếu những dấu hiệu cụ thể về nhu cầu của Trung Quốc trong ngắn hạn.&nbsp;<br />\r\n<br />\r\nGi&aacute; ng&ocirc; cũng chịu ảnh hưởng từ nguồn cung ngũ cốc dồi d&agrave;o tr&ecirc;n to&agrave;n cầu v&agrave; ở Mỹ, trong khi khả năng tr&igrave; ho&atilde;n gieo trồng ở khu vực Midwest (Mỹ).&nbsp;<br />\r\n<br />\r\nGi&aacute; l&uacute;a mỳ kỳ hạn giao dịch gần mức thấp của một th&aacute;ng trong đầu tuần nay do triển vọng thu hoạch thuận lợi tại khu vực Bắc b&aacute;n cầu đ&atilde; t&aacute;c động l&ecirc;n gi&aacute;.</p>\r\n', 4, 11, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (20, 'che 1.jpg', 'Thị trường chè thế giới và triển vọng', 'Thị trường chè vốn đã lớn nhưng vẫn không ngừng tăng đều đặn, chủ yếu bởi Trung Quốc, nơi chiếm gần 40% tổng tiêu thụ chè toàn cầu và đang sử dụng lượng chè xanh cao nhất trong lịch sử.', '<p>Ch&egrave; đ&atilde; trở th&agrave;nh đồ uống được nhiều người sử dụng nhất tr&ecirc;n to&agrave;n cầu. Số liệu đưa ra tại Diễn đ&agrave;n Ch&egrave; thế giới năm 2018 cho thấy trong tổng số 1,6 triệu l&iacute;t đồ uống kh&ocirc;ng cồn sử dụng tr&ecirc;n to&agrave;n cầu th&igrave; ch&egrave; chiếm 266 tỷ l&iacute;t. T&iacute;nh trung b&igrave;nh tr&ecirc;n to&agrave;n cầu, mức ti&ecirc;u thụ ch&egrave; l&agrave; 35,1 l&iacute;t/người, cao hơn so với đồ uống c&oacute; gas (30,6 l&iacute;t) v&agrave; c&agrave; ph&ecirc; (21,1 l&iacute;t).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thị trường ch&egrave; vốn đ&atilde; lớn nhưng vẫn kh&ocirc;ng ngừng tăng đều đặn, chủ yếu bởi Trung Quốc, nơi chiếm gần 40% tổng ti&ecirc;u thụ ch&egrave; to&agrave;n cầu v&agrave; đang sử dụng lượng ch&egrave; xanh cao nhất trong lịch sử. Trong khi đ&oacute;, nhu cầu ở những thị trường kh&aacute;c cũng kh&ocirc;ng ngừng tăng l&ecirc;n, trong đ&oacute; c&oacute; Ấn Độ, dư sức b&ugrave; lại cho mức tăng trưởng yếu ở Ch&acirc;u &Acirc;u (nơi doanh số b&aacute;n ch&egrave; giảm s&uacute;t bởi sự cạnh tranh từ nước đ&oacute;ng chai).</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nh&igrave;n chung, thị trường Ch&acirc;u &Acirc;u phần lớn đ&atilde; b&atilde;o h&ograve;a, ti&ecirc;u thụ b&igrave;nh qu&acirc;n đầu người giảm trong một thập kỷ qua; hiện ti&ecirc;u thụ ch&egrave; đang suy giảm tại hầu hết c&aacute;c nước nhập khẩu truyền thống ở Ch&acirc;u &Acirc;u, ngoại trừ Đức.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Năm 2018, gi&aacute; ch&egrave; thế giới diễn biến thất thường ở c&aacute;c nước sản xuất v&agrave; xuất khẩu chủ chốt. Nếu so gi&aacute; trung b&igrave;nh của năm 2018 so với trung b&igrave;nh năm 2017, gi&aacute; ch&egrave; thế giới năm vừa qua tại c&aacute;c thị trường nh&igrave;n chung vững đến giảm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại Ấn Độ, gi&aacute; ch&egrave; giảm trong 6 th&aacute;ng đầu năm 2018, từ mức 97,15 rupee th&aacute;ng 1/2018 xuống 77,82 rupee v&agrave;o th&aacute;ng 6/2018 (thấp nhất trong năm 2018). Tuy nhi&ecirc;n, bắt đầu từ th&aacute;ng7/2018, gi&aacute; đảo chiều tăng, bước v&agrave;o đầu năm 2019 ở mức trung b&igrave;nh 100 rupee/kg, cao hơn 28 rupee so với c&ugrave;ng kỳ năm trước (gần 40%), v&agrave; cũng l&agrave; mức cao nhất kể từ 21/4/2017. Nguy&ecirc;n nh&acirc;n bởi nhu cầu tăng trong khi sản lượng tr&igrave; trệ. Ngo&agrave;i ra, đồng rupee mạnh l&ecirc;n so với USD v&agrave; chi ph&iacute; sản xuất tăng cũng đẩy gi&aacute; ch&egrave; tăng l&ecirc;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Kh&aacute;c với thị trường Ấn Độ, gi&aacute; ch&egrave; Bangladesh tăng ngay đầu năm 2018, từ mức 238,25 taka/kg l&ecirc;n 280 taka/kg v&agrave;o th&aacute;ng 8/2018 v&agrave; duy tr&igrave; ở mức cao cho đến cuối năm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại Sri Lanka, gi&aacute; ch&egrave; trung b&igrave;nh giảm trong năm 2018 từ mức cao kỷ lục của năm trước do đồng rupee Sri Lanka giảm mạnh so với USD, nhất l&agrave; trong 4 th&aacute;ng cuối năm. Trung b&igrave;nh trong năm 2018, gi&aacute; ch&egrave; Sri Lanka ở mức 581,91 rupee/kg, giảm 36,23 rupee so với 618,14 rupee của năm 2017 (khi gi&aacute; cao kỷ lục lịch sử). Nếu t&iacute;nh theo USD, gi&aacute; ch&egrave; trung b&igrave;nh năm 2018 l&agrave; 3,59 USD/kg, giảm 52 US cent so với 4,11 USD trung b&igrave;nh của năm 2017.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute; ch&egrave; Kenya li&ecirc;n tiếp giảm trong năm 2018 v&agrave; k&eacute;o d&agrave;i tới đầu năm 2019. Cuối năm 2018, gi&aacute; xuống mức thấp nhất kể từ 2014, l&agrave; 219 shilling/kg, so với mức 278 shilling một năm trước đ&oacute;, nguy&ecirc;n nh&acirc;n bởi nguồn cung tăng mạnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại Việt Nam, bước sang năm 2018, gi&aacute; ch&egrave; c&agrave;nh chất lượng cao tại Th&aacute;i Nguy&ecirc;n ở mức 195.000 đồng/kg, ch&egrave; xanh b&uacute;p kh&ocirc; tại Th&aacute;i Nguy&ecirc;n 105.000 đồng/kg, ch&egrave; b&uacute;p tươi loại 1 (nguy&ecirc;n liệu ch&egrave;) tại Bảo Lộc (L&acirc;m Đồng) 9.000 đồng/kg, trong khi ch&egrave; b&uacute;p tươi loại l&agrave;m nguy&ecirc;n liệu sản xuất ch&egrave; đen ở Bảo Lộc 6.000 đồng/kg.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Gi&aacute; ch&egrave; c&agrave;nh chất lượng cao nh&iacute;ch nhẹ trong th&aacute;ng 2/2018 khi nhu cầu tăng trong dịp Tết cổ truyền, l&ecirc;n 200.000 đồng/kg. C&aacute;c loại ch&egrave; kh&aacute;c giữ ổn định. Kể từ đ&oacute;, gi&aacute; ch&egrave; ổn định cho tới cuối năm, trong bối cảnh thời tiết diễn biến thuận lợi n&ecirc;n c&acirc;y ch&egrave; ph&aacute;t triển tốt.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sản lượng ch&egrave; đen to&agrave;n cầu tăng 3,14% trong năm 2018 so với năm 2017, chủ yếu do sản lượng của Kenya tăng mạnh. Cụ thể, sản lượng ch&egrave; đen thế giới trong năm vừa qua đạt 2.102,79 triệu kg, so với 2.038,78 triệu kg năm 2017.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại Ấn Độ, sản lượng năm 2018 giảm 0,8% so với năm trước đ&oacute;, chỉ đạt 1.311,63 triệu kg, khiến cho xuất khẩu của nước sản xuất ch&egrave; đen lớn thứ 2 thế giới n&agrave;y cũng giảm 1,1%. Xuất khẩu loại orthodox bị chậm chủ yếu do sự sụt giảm xuất khẩu sang Iran bởi lệnh cấm vận của Mỹ đối với Iran khiến cho việc thanh to&aacute;n tiền giữa 2 b&ecirc;n trở n&ecirc;n kh&oacute; khăn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại thị trường Mỹ, ch&egrave; Ấn Độ đang mất dần thị phần do những quy định khắt khe hơn về dư lượng thuốc trừ s&acirc;u. Trong 10 th&aacute;ng đầu năm 2018, xuất khẩu ch&egrave; Ấn Độ sang Mỹ giảm 33% xuống 7,84 triệu kg (so với c&ugrave;ng kỳ năm trước). Ấn Độ h&agrave;ng năm xuất khẩu ch&egrave; orthodox sangIran, Saudi Arabia, Nga, Đức, Mỹ, Nhật Bản&hellip;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tại Việt Nam, theo Tổng cục Thống k&ecirc;, sản lượng ch&egrave; b&uacute;p năm 2018 đạt 987,3 ngh&igrave;n tấn, tăng 1,6% so với năm 2017. Về xuất khẩu, theo số liệu của Tổng cục Hải quan, năm 2018 cả nước xuất khẩu 127.338 tấn ch&egrave;, thu về 217,83 triệu USD, giảm 8,9% về lượng v&agrave; giảm 4,4%về kim ngạch so với năm 2017. Gi&aacute; ch&egrave; xuất khẩu b&igrave;nh qu&acirc;n trong năm 2018 đạt 1.710,7 USD/tấn, tăng 4,9% so với năm 2017.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Pakistan tiếp tục l&agrave; thị trường nhập khẩu nhiều ch&egrave; Việt Nam nhất trong năm qua, với 38.213 tấn, tương đương 81,63 triệu USD, chiếm 30% trong tổng khối lượng ch&egrave; xuất khẩu của Việt Nam v&agrave; chiếm 37,5% trong tổng kim ngạch, tăng 19,4% về lượng v&agrave; tăng 18,8% về kim ngạch so với năm 2017. Gi&aacute; ch&egrave; xuất khẩu sang Pakistan giảm nhẹ 0,5%, đạt 2.136,3 USD/tấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&agrave;i Loan l&agrave; thị trường lớn thứ 2 ti&ecirc;u thụ ch&egrave; của Việt Nam chiếm gần 14,6% trong tổng khối lượng v&agrave; chiếm 13,2% trong tổng kim ngạch, đạt 18.573 tấn, tương đương 28,75 triệu USD, tăng 6% về lượng v&agrave; tăng 5,4% về kim ngạch; gi&aacute; xuất khẩu sang thị trường n&agrave;y sụt giảm 0,6%, chỉ đạt 1.548 USD/tấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Xuất khẩu sang thị trường Nga &ndash; thị trường lớn thứ 3 sụt giảm mạnh 20% về lượng v&agrave; giảm 114,6%về kim ngạch, đạt 13.897 tấn, tương đương tr&ecirc;n 21,21 triệu USD, chiếm 10,9% trong tổng khối lượng v&agrave; chiếm 9,7% trong tổng kim ngạch. Gi&aacute; xuất khẩu tăng 6,7%, đạt 1.526,2 USD/tấn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ri&ecirc;ng ch&egrave; xuất khẩu sang thị trường Trung Quốc lại tăng gi&aacute; rất mạnh so với năm 2017, tăng 47,3%, đạt trung b&igrave;nh 1.943,3 USD/tấn, v&igrave; vậy lượng ch&egrave; xuất khẩu tuy giảm 8,8%, đạt 10.121 tấn nhưng kim ngạch lại tăng 34,2%, đạt 19,67 triệu USD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c thị trường nổi bật về mức tăng mạnh kim ngạch trong năm 2018 gồm c&oacute;: Đức tăng 39%, đạt 1,96 triệu USD; Philippines tăng 24%, đạt 1,6 triệu USD, Saudi Arabia tăng 33,1%, đạt 5,72 triệu USD; Pakistan tăng18,8%, đạt 81,63 triệu USD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><a href=\"https://4.bp.blogspot.com/-bT8cNCTnukk/XIcWdlwXV9I/AAAAAAACjVY/YcUK3My30SQg1UJuwhx5PR5sMvmMh3JtQCLcBGAs/s1600/che%2B2.jpg\"><img src=\"https://4.bp.blogspot.com/-bT8cNCTnukk/XIcWdlwXV9I/AAAAAAACjVY/YcUK3My30SQg1UJuwhx5PR5sMvmMh3JtQCLcBGAs/s400/che%2B2.jpg\" style=\"height:263px; width:400px\" /></a></p>\r\n\r\n<p><em>Chế biến ch&egrave; sạch</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c thị trường sụt giảm mạnh về kim ngạch gồm c&oacute;: Ấn Độ giảm 56,6%, đạt 0,91 triệu USD; U.A.E giảm 59,1%, đạt 4,21 triệu USD; Thổ Nhĩ Kỳ giảm 48%, đạt 0,78 triệu USD.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Dự b&aacute;o sản lượng ở cả Kenya v&agrave; Trung Quốc đều hồi phục mạnh trong năm 2018 sau khi bị sụt giảm trong năm 2017. Do đ&oacute; sản lượng to&agrave;n cầu ni&ecirc;n vụ 2018/2019 ước t&iacute;nh tăng nhẹ, khoảng 4,5%, nhưng sẽ chỉ tăng 2,8% trong năm 2019/2020 (thấp hơn mức tăng trung b&igrave;nh 4,5% giai đoạn 2006 - 2016), theo nhận định của EIU.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong khi đ&oacute;, c&oacute; một số yếu tố c&oacute; thể sẽ cản trở sản lượng tăng trong khoảng thời gian dự b&aacute;o, đ&oacute; l&agrave; lạm ph&aacute;t khiến lợi nhuận từ trồng ch&egrave; giảm so với thập kỷ trước, trong khi chi ph&iacute; đầu v&agrave;o tăng c&oacute; thể khiến đầu tư giảm đi.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&aacute;c nước sản xuất ch&egrave; ng&agrave;y c&agrave;ng quy định chặt chẽ hơn về việc sử dụng h&oacute;a chất sẽ khiến sản lượng ch&egrave; thế giới kh&oacute; tăng mạnh, chẳng hạn như ở Sri Lanka. Ngo&agrave;i ra, năng suất thấp do nhiều diện t&iacute;ch ch&egrave; gi&agrave; cỗi cũng cản trở việc tăng sản lượng, như ở Ấn Độ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về nhu cầu, triển vọng nhu cầu ti&ecirc;u thụ ch&egrave; tại c&aacute;c nước sản xuất ng&agrave;y c&agrave;ng tăng. Do đ&oacute;, mặc d&ugrave; sản lượng của nhiều nước cũngtăng, song lượng dư thừa d&agrave;nh cho xuất khẩu kh&ocirc;ng c&oacute; sự đột biến, thậm ch&iacute; ở một số nơi sụt giảm. Trung Quốc v&agrave; Ấn Độ l&agrave; hai thị trường điển h&igrave;nh c&oacute; mức ti&ecirc;u thụ tăng nhanh, c&oacute; thể khiến cầu vượt cung ng&agrave;y c&agrave;ng xa, dẫn tới gi&aacute; tr&ecirc;n thị trường nội địa tăng. Tại Mỹ cũng tương tự, ph&acirc;n kh&uacute;c thị trường tr&agrave; đ&aacute; ng&agrave;y c&agrave;ng ph&aacute;t triển, l&agrave; một trong những l&yacute; do khiến cho xuất khẩu ch&egrave; của Sri Lanka sang Mỹ năm 2018 tăng kh&aacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về thị hiếu, mấy năm qua thị trường chứng kiến sự thay đổi mạnh mẽ về th&oacute;i quen thưởng tr&agrave; của người ti&ecirc;u d&ugrave;ng. Trước đ&acirc;y người ti&ecirc;u d&ugrave;ng th&iacute;ch uống tr&agrave; truyền thống trong bữa s&aacute;ng, l&agrave; ch&egrave; đen hoặc tr&agrave; xanh ướp hương.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n hiện c&oacute; 2 ph&acirc;n kh&uacute;c đang ph&aacute;t triển rất mạnh, đ&oacute; l&agrave; tr&agrave; thảo mộc v&agrave; tr&agrave; chế biến thủ c&ocirc;ng (theo c&aacute;ch truyền thống, từ b&agrave;n tay của những nghệ nh&acirc;n). Sở dĩ tr&agrave; thảo mộc l&ecirc;n ng&ocirc;i l&agrave; bởi đ&oacute; được đ&aacute;nh gi&aacute; l&agrave; đồ uống c&oacute; lợi cho sức khỏe, với những t&aacute;c dụng như giảm stress, chống vi&ecirc;m, thải độc, tốt cho ti&ecirc;u h&oacute;a&hellip;</p>\r\n', 4, 11, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (21, '37588.jpg', 'Alibaba bắt đầu đưa trí tuệ nhân tạo vào nông nghiệp', 'Không chỉ giúp tiêu thụ nông sản, Alibaba còn đang triển khai dự án trí tuệ nhân tạo giúp nông dân trồng trọt, chăn nuôi...', '<p><strong>Tại Trung Quốc, g&atilde; c&ocirc;ng nghệ khổng lồ Alibaba</strong><strong>&nbsp;đang ứng dụng tr&iacute; tuệ nh&acirc;n tạo để gi&uacute;p n&ocirc;ng d&acirc;n cắt giảm chi ph&iacute; v&agrave; tăng sản lượng. Từ điện thoại th&ocirc;ng minh, n&ocirc;ng d&acirc;n giờ đ&acirc;y c&oacute; thể gi&aacute;m s&aacute;t liệu c&ocirc;n tr&ugrave;ng c&oacute; đang tiếp cận c&aacute;nh đồng t&aacute;o hay kh&ocirc;ng, hoặc xem liệu nho đ&atilde; ch&iacute;n để h&aacute;i chưa.&nbsp;</strong></p>\r\n\r\n<p>Alibaba Cloud, nh&aacute;nh điện to&aacute;n đ&aacute;m m&acirc;y của Alibaba, mới cho ra mắt dự &aacute;n ET Agricultural Brain, sử dụng c&ocirc;ng nghệ nhận diện trực quan v&agrave; theo d&otilde;i m&ocirc;i trường theo thời gian thực để theo s&aacute;t điều kiện ph&aacute;t triển của c&acirc;y trồng v&agrave; vật nu&ocirc;i. Tất cả dữ liệu được thu thập sẽ được chuyển th&agrave;nh th&ocirc;ng tin cho n&ocirc;ng d&acirc;n qua c&aacute;c thuật to&aacute;n m&aacute;y học (machine learning).&nbsp;</p>\r\n\r\n<p>Alibaba Cloud sẽ kh&ocirc;ng l&agrave;m việc trực tiếp với n&ocirc;ng d&acirc;n m&agrave; cung cấp phần mềm cho c&aacute;c c&ocirc;ng ty v&agrave; hợp t&aacute;c x&atilde; n&ocirc;ng nghiệp. C&aacute;c đơn vị n&agrave;y sau đ&oacute; sẽ đ&agrave;o tạo n&ocirc;ng d&acirc;n sử dụng những c&ocirc;ng nghệ đ&oacute;.&nbsp;</p>\r\n\r\n<p>Hiện tại, chương tr&igrave;nh n&agrave;y của Alibaba Cloud đ&atilde; được ứng dụng v&agrave; mang lại những hiệu quả nhất định. Tequ Group, một doanh nghiệp chăn nu&ocirc;i lợn ở Tứ Xuy&ecirc;n, đ&atilde; sử dụng ET Agricultural Brain để l&agrave;m cho mỗi lợn n&aacute;i đẻ th&ecirc;m 3 lứa mỗi năm. Tr&ecirc;n c&aacute;nh đồng, c&ocirc;ng nghệ n&agrave;y đ&atilde; gi&uacute;p c&ocirc;ng ty rau củ Haisheng Group tiết kiệm khoảng 3 triệu USD cho mỗi c&aacute;nh đồng t&aacute;o một năm.&nbsp;</p>\r\n\r\n<p>ET Agricultural Brain l&agrave; một phần trong tham vọng mang t&ecirc;n ET Brain của Alibaba Cloud - chương tr&igrave;nh to&agrave;n diện nhằm ứng dụng tr&iacute; tuệ nh&acirc;n tạo v&agrave;o giải quyết c&aacute;c vấn đề của c&aacute;c ng&agrave;nh c&ocirc;ng nghiệp truyền thống như quy hoạch đ&ocirc; thị, sản xuất c&ocirc;ng nghiệp, chăm s&oacute;c sức khỏe v&agrave; bảo vệ m&ocirc;i trường.&nbsp;</p>\r\n\r\n<p>Alibaba kh&ocirc;ng phải l&agrave; h&atilde;ng thương mại điện tử đầu ti&ecirc;n lấn s&acirc;n sang lĩnh vực n&ocirc;ng nghiệp. Đối thủ của c&ocirc;ng ty l&agrave; JD.com cũng triển khai một dự &aacute;n thử nghiệm để gi&uacute;p c&aacute;c hộ ngh&egrave;o nu&ocirc;i g&agrave; kiếm tiền. NetEase, c&ocirc;ng ty game lớn thứ 2 tại Trung Quốc đang tấn c&ocirc;ng thị trường thương mại điện tử v&agrave;i năm gần đ&acirc;y, đ&atilde; x&acirc;y dựng một trang trại rộng 2,2 triệu m2 để nu&ocirc;i lợn. Kh&ocirc;ng giống c&aacute;c đối thủ, Alibaba x&acirc;y dựng một m&ocirc; h&igrave;nh &quot;nền tảng&quot; v&agrave; kh&ocirc;ng tham gia v&agrave;o việc sản xuất.&nbsp;</p>\r\n\r\n<p>&quot;Ch&uacute;ng t&ocirc;i sẽ kh&ocirc;ng tự nu&ocirc;i lợn bởi ch&uacute;ng t&ocirc;i kh&ocirc;ng giỏi việc đ&oacute;. Lợi thế của ch&uacute;ng t&ocirc;i l&agrave; c&ocirc;ng nghệ, v&igrave; vậy, ch&uacute;ng t&ocirc;i sẽ mang những hiểu biết đ&oacute; tới cho n&ocirc;ng d&acirc;n để gi&uacute;p họ nu&ocirc;i g&agrave;, lợn tốt hơn&quot;,&nbsp;Simon Hu, gi&aacute;m đốc của Alibaba Cloud n&oacute;i. &quot;Hợp t&aacute;c với c&aacute;c tổ chức sẽ gi&uacute;p ch&uacute;ng t&ocirc;i tiếp cận được n&ocirc;ng d&acirc;n &#39;tr&ecirc;n diện lớn hơn v&agrave; hiệu quả hơn&#39;&quot;.</p>\r\n\r\n<p>Alibaba Cloud tin rằng nền tảng của m&igrave;nh c&oacute; thể gi&uacute;p kh&aacute;ch h&agrave;ng cải thiện to&agrave;n bộ ng&agrave;nh n&ocirc;ng nghiệp. Tuy nhi&ecirc;n, Hu thừa nhận rằng cần nhiều thời gian để trở th&agrave;nh một chuy&ecirc;n gia trong lĩnh vực n&agrave;y.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i việc cung cấp c&ocirc;ng nghệ, Alibaba Cloud cũng đang l&agrave;m việc với trang b&aacute;n lẻ trực tuyến Tmall.com (cũng thuộc Alibaba) để gi&uacute;p ti&ecirc;u thụ n&ocirc;ng sản. Với s&agrave;n thương mại điện tử n&agrave;y, n&ocirc;ng d&acirc;n kh&ocirc;ng cần th&ocirc;ng qua trung gian, gi&uacute;p giảm chi ph&iacute;, từ đ&oacute; tăng lợi nhuận từ vụ m&ugrave;a, Hu cho biết.&nbsp;</p>\r\n', 4, 1, 'CDD', 0, 'premium', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (22, '151349.jpg', 'Thu nhập của ngành nông nghiệp Mỹ có thể sụt giảm', 'Theo báo cáo mới đây của Bộ Nông nghiệp Mỹ (USDA), thu nhập ròng trong năm 2017 của ngành nông Mỹ có khả năng sẽ giảm năm thứ liên tiếp, do giá của nhiều loại nông sản giảm sâu.', '<p>USDA cũng cho biết, b&aacute;o c&aacute;o mới nhất về n&ocirc;ng nghiệp Mỹ trong năm 2017 tr&ecirc;n&nbsp;kh&ocirc;ng gồm to&agrave;n tin xấu, khi c&aacute;c điều kiện của thị trường n&ocirc;ng nghiệp được cho l&agrave; tốt hơn so với dự b&aacute;o đưa ra c&aacute;ch đ&acirc;y một năm.&nbsp;</p>\r\n\r\n<p>Cụ thể, USDA ước t&iacute;nh thu nhập r&ograve;ng của ng&agrave;nh n&ocirc;ng nghiệp Mỹ trong năm 2017 sẽ giảm 8,7% so với năm 2016 v&agrave; chỉ đạt 62,3 tỷ USD. Trong năm 2016, thu hoạch ng&ocirc; v&agrave; đậu tương của Mỹ đạt mức cao kỷ lục, với sản lượng lần lượt l&agrave; 15,2 tỷ bushel v&agrave; 4,3 tỷ bushel.&nbsp;</p>\r\n\r\n<p>B&aacute;o c&aacute;o n&ecirc;u r&otilde; triển vọng tăng khối lượng b&aacute;n ng&ocirc; trong năm 2017 kh&ocirc;ng đủ để b&ugrave; đắp cho xu hướng giảm gi&aacute; của mặt h&agrave;ng n&agrave;y, khiến doanh thu b&aacute;n ng&ocirc; trong năm nay sẽ giảm gần 0,4 tỷ USD (0,8%) so với năm 2016. Trong l&uacute;c đ&oacute;, l&uacute;a m&igrave;&nbsp;&ldquo;thất thế&rdquo; tr&ecirc;n cả hai phương diện khối lượng b&aacute;n ra v&agrave; gi&aacute; trị thu về - dự kiến giảm 1,5 tỷ USD (16,6%) so với năm 2016.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, thu nhập từ đậu tương trong năm 2017 c&oacute; thể tăng 0,4 tỷ USD (1,1%) nhờ triển vọng cải thiện của gi&aacute; mặt h&agrave;ng n&agrave;y, bất chấp khối lượng b&aacute;n giảm. Kể từ năm 2013, thời điểm thu nhập r&ograve;ng của ng&agrave;nh n&ocirc;ng nghiệp Mỹ đạt mức cao kỷ lục l&agrave; 123 tỷ USD, tới nay con số n&agrave;y đ&atilde; giảm khoảng 50%.&nbsp;</p>\r\n\r\n<p>Kh&ocirc;ng chỉ vậy, c&aacute;c chương tr&igrave;nh chi ti&ecirc;u trực tiếp của Ch&iacute;nh phủ Mỹ cho ng&agrave;nh n&ocirc;ng nghiệp cũng được dự b&aacute;o giảm 4% trong năm 2017, xuống 12,5 tỷ USD. B&aacute;o c&aacute;o cho hay sau khi tăng ổn định trong giai đoạn 2010-14, thu nhập trung b&igrave;nh của c&aacute;c hộ n&ocirc;ng d&acirc;n giảm từ mức 81.637 USD/hộ năm 2014 xuống 76.735 USD/hộ năm 2015, v&agrave; được dự b&aacute;o sẽ phục hồi trong hai năm tới, trong đ&oacute; ước đạt 79.733 USD/hộ trong năm 2017.&nbsp;</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o, gi&aacute; trị đất n&ocirc;ng nghiệp của Mỹ trong năm nay sẽ v&agrave;o khoảng 2.400 tỷ USD, giảm nhẹ so với năm 2016 v&agrave; vẫn chiếm tới 84% tổng gi&aacute; trị t&agrave;i sản n&ocirc;ng nghiệp. Gi&aacute; trị đất n&ocirc;ng nghiệp được dự b&aacute;o sẽ c&oacute; xu hướng giảm trong thời gian tới, do l&atilde;i suất tăng cao v&agrave; c&aacute;c điều kiện cho vay bị thắt chặt hơn.&nbsp;</p>\r\n\r\n<p>Đặc biệt, tổng mức nợ của ng&agrave;nh n&ocirc;ng nghiệp Mỹ trong năm 2017 được dự b&aacute;o sẽ tăng 5% so với năm trước đ&oacute;. Kể từ năm 2013 tới nay, nợ n&ocirc;ng nghiệp của Mỹ li&ecirc;n tục gia tăng (với tốc độ 25%).&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n, theo USDA, c&aacute;c điều kiện hiện tại của ng&agrave;nh n&ocirc;ng nghiệp Mỹ kh&ocirc;ng đ&aacute;ng lo ngại như viễn cảnh ảm đạm được vẽ&nbsp;ra trong b&aacute;o c&aacute;o hồi th&aacute;ng 2/2016 của Bộ n&agrave;y. Th&ecirc;m v&agrave;o đ&oacute;, chỉ số l&ograve;ng tin của c&aacute;c nh&agrave; sản xuất n&ocirc;ng nghiệp của Mỹ (Ag Economy Barometer) trong th&aacute;ng 1/2017 dựa tr&ecirc;n kết quả khảo s&aacute;t 400 nh&agrave; sản xuất n&ocirc;ng nghiệp nước n&agrave;y, đạt 153 điểm, tăng 21 điểm so với th&aacute;ng 12/2016 v&agrave; cao hơn 61 điểm so với th&aacute;ng 10/2016. Đ&acirc;y kh&ocirc;ng những l&agrave; mức cao nhất của chỉ số n&agrave;y kể từ khi &quot;ra đời&quot; hồi th&aacute;ng 10/2015, m&agrave; c&ograve;n l&agrave; mức tăng theo th&aacute;ng mạnh nhất. Yếu tố ch&iacute;nh tạo n&ecirc;n kết quả t&iacute;ch cực n&agrave;y l&agrave; xu hướng cải thiện về gi&aacute; của một số n&ocirc;ng phẩm.</p>\r\n', 4, 10, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (23, 'anh1-14.jpg', 'Quảng Bình tiêu hủy hơn 600 tấn hải sản sau thảm họa môi trường', 'GiadinhNet - Hơn 600 tấn hải sản tồn đọng được Sở Y tế tỉnh Quảng Bình phát hiện có hàm lượng Cadimin vượt ngưỡng cho phép nên các ngành chức năng đã tiến hành tiêu hủy để đảm bảo an toàn thực phẩm.', '<p>Ng&agrave;y 10/12, c&aacute;c ng&agrave;nh chức năng của tỉnh Quảng Bình đã tiến h&agrave;nh ki&ecirc;̉m k&ecirc; hải sản đ&ocirc;ng lạnh tồn kho. Qua kiểm tra, Sở Y tế Quảng B&igrave;nh ph&aacute;t hiện hơn 606 tấn hải sản c&oacute; chứa h&agrave;m lượng Cadimin vượt ngưỡng cho ph&eacute;p n&ecirc;n đ&atilde; tiến h&agrave;nh ti&ecirc;u hủy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Phan Xu&acirc;n H&agrave;o, Chi Cục trưởng Chi cục bảo vệ m&ocirc;i trường tỉnh Quảng B&igrave;nh cho biết: &ldquo;Hiện ch&uacute;ng t&ocirc;i đ&atilde; triển khai 8 đội, trong đ&oacute; c&oacute; c&aacute;n bộ gi&aacute;m s&aacute;t v&agrave; c&aacute;c phương tiện vận chuyển. L&uacute;c n&agrave;o c&aacute;c đo&agrave;n li&ecirc;n ng&agrave;nh của sở N&ocirc;ng nghiệp v&agrave; ph&aacute;t triển n&ocirc;ng th&ocirc;n kiểm k&ecirc; xong th&igrave; ch&uacute;ng t&ocirc;i đưa l&ecirc;n xe, cử c&aacute;n bộ gi&aacute;m s&aacute;t trực tiếp đến nơi ti&ecirc;u hủy để tiến h&agrave;nh ch&ocirc;n lấp&rdquo;.</p>\r\n\r\n<p>Sau sự cố m&ocirc;i trường biển, tỉnh Quảng B&igrave;nh bị tồn đọng hơn 2.000 tấn hải sản.</p>\r\n\r\n<p><img alt=\"Dự kiến, việc tiêu hủy sẽ được tiến hành đến hết ngày 14/12. Ảnh: M.K\" id=\"img_73502800-bee5-11e6-b63e-879eb9f5f4fc\" src=\"http://giadinh.mediacdn.vn/2016/anh2-1481380334445.jpg\" /></p>\r\n\r\n<p>Dự ki&ecirc;́n, vi&ecirc;̣c ti&ecirc;u hủy sẽ được ti&ecirc;́n hành đến hết ngày 14/12. Ảnh: M.K</p>\r\n\r\n<p>Việc kiểm tra v&agrave; ti&ecirc;u hủy số hải sản kh&ocirc;ng đảm bảo an to&agrave;n thực phẩm gi&uacute;p người d&acirc;n v&agrave; c&aacute;c chủ cơ sở thu mua hải sản thấy y&ecirc;n t&acirc;m hơn.</p>\r\n\r\n<p>Dự ki&ecirc;́n, vi&ecirc;̣c ti&ecirc;u hủy hơn 600 t&acirc;́n hải sản kh&ocirc;ng an toàn tr&ecirc;n được ti&ecirc;́n hành từ ngày 10 đ&ecirc;́n hết ngày 14/12.</p>\r\n', 2, 2, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (24, 'unnamed-14.jpg', 'Quảng Trị tiêu hủy 60 tấn hải sản thu mua trong thời điểm cá chết vì Formosa', 'GiadinhNet – Toàn bộ số cá nục nhiễm phenol và số hải sản được các cơ sở đông lạnh tại Quảng Trị thu mua vào thời điểm cá chết hàng loạt do sự cố môi trường biển, hiện không có khả năng tiêu thụ sẽ được cơ quan chức năng tổ chức tiêu hủy.', '<p>Theo đ&oacute;, chiều 25/8, tin từ UBND tỉnh Quảng Trị cho biết vừa c&oacute; ph&ecirc; duyệt phương &aacute;n ti&ecirc;u hủy đối với 60 tấn hải sản đ&ocirc;ng lạnh được thu mua trong thời điểm c&aacute; chết.</p>\r\n\r\n<p>Trong số 60 tấn hải sản, c&oacute; 20 tấn c&aacute; nhiễm phenol của cơ sở đ&ocirc;ng lạnh Dũng Thuộc (thị trấn Cửa T&ugrave;ng, huyện Vĩnh Linh). 40 tấn hải sản c&ograve;n lại bị ti&ecirc;u hủy bao gồm nước mắm, sứa, c&aacute; nục, c&aacute; hố,&hellip; được c&aacute;c cơ sở thu mua v&agrave;o thời điểm xảy ra sự cố m&ocirc;i trường biển v&agrave; hiện kh&ocirc;ng c&oacute; khả năng ti&ecirc;u thụ.</p>\r\n\r\n<p><img alt=\"Lấy mẫu kiểm nghiệm 20 tấn cá nhiễm phenol. Ảnh: Lê Chung\" id=\"img_f74989d0-6ab7-11e6-9ff0-97390eda2b52\" src=\"http://giadinh.mediacdn.vn/k:thumb_w/640/2016/anh-nguyendo-anhbuoilaymaucanucnhiemphennol-h7-1472124897889/quangtritieuhuy60tanhaisanthumuatrongthoidiemcachetviformosa.jpg\" /></p>\r\n\r\n<p>Lấy mẫu kiểm nghiệm 20 tấn c&aacute; nhiễm phenol. Ảnh: L&ecirc; Chung</p>\r\n\r\n<p>Trước đ&oacute;, v&agrave;o khoảng th&aacute;ng 6, Sở Y tế tỉnh Quảng Trị kiểm tra c&aacute;c cơ sở đ&ocirc;ng lạnh tr&ecirc;n địa b&agrave;n tỉnh. Qua qu&aacute; tr&igrave;nh kiểm tra th&igrave; ph&aacute;t hiện hơn 20 tấn c&aacute; nục của cơ sở đ&ocirc;ng lạnh Dũng Thuộc nhiễm phenol với h&agrave;m lượng 0,037mg/kg. Đ&acirc;y l&agrave; chất cực độc kh&ocirc;ng được tồn tại trong thực phẩm.</p>\r\n\r\n<p>Sau khi ph&aacute;t hiện, 20 tấn c&aacute; n&oacute;i tr&ecirc;n bị ni&ecirc;m phong cho đến nay. Theo b&agrave; L&ecirc; Thị Thuộc th&igrave; việc chậm trễ xử l&yacute; khiến cơ sở của b&agrave; phải chi trả 25 triệu đồng/th&aacute;ng chi ph&iacute; bảo quản.</p>\r\n\r\n<p>Được biết Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường tỉnh Quảng Trị đang lập kế hoạch ti&ecirc;u hủy bằng c&aacute;ch đ&agrave;o hố ch&ocirc;n lấp, c&oacute; sử dụng bạt l&oacute;t đ&aacute;y, rắc v&ocirc;i bột v&agrave; h&oacute;a chất CloruaminB. Kinh ph&iacute; ti&ecirc;u hủy số hải sản l&agrave; 100 triệu đồng. Tuy nhi&ecirc;n hiện đơn vị n&agrave;y vẫn chưa chọn được địa điểm để tiến h&agrave;nh ch&ocirc;n lấp, ti&ecirc;u hủy.</p>\r\n', 2, 3, 'CDD', 0, 'normal', '2019-06-18', 1, NULL, NULL,NULL);
INSERT INTO `post` VALUES (25, 'hqdefault.jpg', 'Thừa Thiên – Huế kết luận cá chết là do nước biển nhiễm chất cực độc', 'GiadinhNet - Sở Tài nguyên và Môi trường Thừa Thiên - Huế xác định, nguyên nhân cá biển, cá nuôi chết không phải do dịch bệnh mà do một tác nhân cực mạnh - chất độc trong môi trường nước biển.', '<p>Chiều 26/4, Sở T&agrave;i nguy&ecirc;n v&agrave; m&ocirc;i trường tỉnh Thừa Thi&ecirc;n - Huế cho biết đ&atilde; c&oacute; kết quả ph&acirc;n t&iacute;ch mẫu nước tại c&aacute;c v&ugrave;ng ph&aacute;t hiện c&aacute; biển chết h&agrave;ng loạt tr&ocirc;i dạt v&agrave;o bờ trong thời gian qua.</p>\r\n\r\n<p>Trước đ&oacute; để đảm bảo t&iacute;nh kh&aacute;ch quan, ch&iacute;nh x&aacute;c, Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường đ&atilde; tiến h&agrave;nh khảo s&aacute;t v&agrave; lấy 09 mẫu nước mặt v&agrave; 07 mẫu trầm t&iacute;ch tại khu vực đầm Lập An (gần cửa biển Lăng C&ocirc;), huyện Ph&uacute; Lộc, v&ugrave;ng ven bờ x&atilde; Quảng C&ocirc;ng, huyện Quảng Điền v&agrave; c&aacute;c x&atilde; Điền Hương, Điền Hải, huyện Phong Điền để ph&acirc;n t&iacute;ch, đ&aacute;nh gi&aacute; c&aacute;c th&ocirc;ng số về m&ocirc;i trường.</p>\r\n\r\n<p><img alt=\"Cá nuôi lồng chết, trôi dạt trên đầm Lập An ngày 15/4. Ảnh: Sở Tài nguyên và Môi trường\" id=\"img_343934\" src=\"http://giadinh.mediacdn.vn/k:2016/a1-1461667257904/thua-thien--hue-ket-luan-ca-chet-la-do-nuoc-bien-nhiem-chat-cuc-doc.jpg\" /></p>\r\n\r\n<p>C&aacute; nu&ocirc;i lồng chết, tr&ocirc;i dạt tr&ecirc;n đầm Lập An ng&agrave;y 15/4. Ảnh: Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường</p>\r\n\r\n<p>Theo b&aacute;o c&aacute;o, c&aacute;c th&ocirc;ng số: Tổng h&agrave;m lượng nitơ t&iacute;nh theo amoni (NH<sub>4</sub><sup>+</sup>-N), h&agrave;m lượng kim loại nặng cr&ocirc;m (Cr) vượt giới hạn cho ph&eacute;p theo quy chuẩn kỹ thuật quốc gia về chất lượng nước biển cũng như quy chuẩn kỹ thuật quốc gia về chất lượng nước mặt.</p>\r\n\r\n<p>B&aacute;o c&aacute;o cũng chỉ ra rằng nguy&ecirc;n nh&acirc;n c&aacute; biển, c&aacute; nu&ocirc;i chết kh&ocirc;ng phải do dịch bệnh m&agrave; do một t&aacute;c nh&acirc;n cực mạnh - chất độc trong m&ocirc;i trường nước dẫn đến sự cố c&aacute; chết h&agrave;ng loạt tại tỉnh Thừa Thi&ecirc;n Huế.</p>\r\n\r\n<p><img alt=\"Khảo sát, lấy mẫu khu vực biển xã Quảng Công ngày 21/4. Ảnh: Sở Tài nguyên và Môi trường\" id=\"img_343935\" src=\"http://giadinh.mediacdn.vn/k:2016/a2-1461667257912/thua-thien--hue-ket-luan-ca-chet-la-do-nuoc-bien-nhiem-chat-cuc-doc.jpg\" /></p>\r\n\r\n<p>Khảo s&aacute;t, lấy mẫu khu vực biển x&atilde; Quảng C&ocirc;ng ng&agrave;y 21/4. Ảnh: Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường</p>\r\n\r\n<p>Trước thực trạng n&agrave;y, Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường đ&atilde; c&oacute; c&ocirc;ng văn y&ecirc;u cầu c&aacute;c địa phương bị sự cố c&aacute; chết khẩn trương thu gom v&agrave; xử l&yacute; theo quy định, kh&ocirc;ng được để c&aacute; chết g&acirc;y &ocirc; nhiễm m&ocirc;i trường; Thống k&ecirc; thiệt hại trong hoạt động nu&ocirc;i trồng v&agrave; đ&aacute;nh bắt cũng như c&aacute;c hoạt động kinh tế li&ecirc;n quan của địa phương.</p>\r\n\r\n<p>Sở T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường cũng đề nghị Tổng cục M&ocirc;i trường, Bộ T&agrave;i nguy&ecirc;n v&agrave; M&ocirc;i trường phối hợp với Bộ N&ocirc;ng nghiệp v&agrave; Ph&aacute;t triển n&ocirc;ng th&ocirc;n, Bộ Y tế khẩn trương nghi&ecirc;n cứu v&agrave; c&oacute; hướng dẫn kịp thời gi&uacute;p nh&acirc;n d&acirc;n sử dụng thủy sản khoa học, hợp l&yacute; nhằm ổn định thị trường v&agrave; cuộc sống.</p>\r\n', 1, 1, 'CDD', 0, 'premium', '2019-06-18', 1, NULL, NULL,NULL);

SET FOREIGN_KEY_CHECKS = 1;
